  {
     color:#ccc
}
  .newsteaser__time, .teaser-buzz__metadata, .teaser-dossier__metadata, .teaser-large__metadata, .teaser-small__metadata, .teaser-small-minor__metadata, .teaser-column-exclusive__metadata, .teaser-large-column__metadata, .teaser-small-column__metadata, .teaser-small-column-minor__metadata{
  color: #eee
  }
.paragraph a, .list a {
    border-bottom: 1px solid darksalmon;
    color: darksalmon;
}
.paragraph a:focus, .list a:focus, .paragraph a:hover, .list a:hover, .paragraph a:active, .list a:active {
    border-bottom-color: chocolate;
    color: chocolate;
}
.comment--author{
background-color: chocolate;
}
.summary {
background-color: #33333c;
color: #eee;
    padding: 20px;
}
.comment-overlay{
     background-image:none;
}
  .article-heading__title--underlined-skip{
  text-shadow:none;
    color:white;
  }
.js.no-touchevents .podcast-bar__list-arrow--right {
    background-image: -webkit-linear-gradient(left, rgba(255,255,255,0) 0%, #41464d 60%, #41464d 100%);
    background-image: -o-linear-gradient(left, rgba(255,255,255,0) 0%, #41464d 60%, #41464d 100%);
    background-image: linear-gradient(90deg, rgba(255,255,255,0) 0%, #41464d 60%, #41464d 100%);
}
.js.no-touchevents .podcast-bar__list-arrow--left {
    background-image: -webkit-linear-gradient(left, #41464d 0%, #41464d 40%, rgba(255,255,255,0) 100%);
    background-image: -o-linear-gradient(left, #41464d 0%, #41464d 40%, rgba(255,255,255,0) 100%);
    background-image: linear-gradient(90deg, #41464d 0%, #41464d 40%, rgba(255,255,255,0) 100%);
}
.liveblog-text-wrapper--wrapped:after{
    background-image: linear-gradient(0deg, #41464d 1.5em, rgba(255,255,255,0) 100%);
}
.teaser-shop, .zon-section-heading, .teaser-large-column, .teaser-small-column, .teaser-small-column-minor, .lb-post {
    background-color: #33333c;
}
  .markup--solo, .markup--standard, .cp-area--ranking, .cp-area--paginated{
    box-shadow:none;
    background-color: #33333c;
  }
.cp-area--ranking {
    padding-bottom: 1.875rem;
}
.zon-dossier__kicker{
color: black
}
