css:
@-moz-document url-prefix("https://somtoday"), domain("somtoday.nl") {
#content {
	display: table;
	background: url(https://steamuserimages-a.akamaihd.net/ugc/781851765942785816/2539D144D4A51B20C85FFB94B64FA4C08BA9132C/);
	border: 1px solid #2b2b2b00;
	border-top: 0;
	box-shadow: 100px 2px 5px #2b2b2bde;
}
body {
	line-height: 1;
	color: #000000b0;
	font-size: 12px;
	font-family: Open Sans, Arial, Helvetica, sans-serif;
	overflow-y: scroll;
	overflow-x: hidden;
	background: url(https://vignette.wikia.nocookie.net/fandomium/images/0/0c/Obomba.gif/revision/latest/scale-to-width-down/220?cb=20191122172620);
}
#header-wrapper {
	background-color: #2b2b2b40;
}
a {
	outline: 0;
	color: #2b2b2b;
	cursor: pointer;
}
h1 {
	font-weight: 700;
	font-size: 16pt;
	color: #2b2b2b;
}
.roster-table-content .uurNummer {
	color: #2b2b2ba6!important;
	border-radius: 5px!important;
	border: 2px solid #2b2b2bb3!important;
}
.roster-table-content .day {
	border-top: solid 1px #2b2b2b;
}
.afspraak {
	position: absolute;
	left: 5px;
	right: 5px;
	background-color: #2b2b2bad;
	margin: 2px;
	text-align: left;
}
.day {
	display: table-cell;
	position: relative;
	border-left: dotted 1px #2b2b2b;
	overflow: hidden;
}
.dayTitle {
	border-bottom: solid 3px #2b2b2b;
	background: #00000085;
}
.roster-table-header .geen-items {
	font-size: 13px;
	font-style: italic;
	padding-top: 3px;
	color: #2b2b2b;
}
.roster-table-content .uurNummer {
	color: #2b2b2ba6!important;
	border-radius: 5px!important;
	border: 2px solid #2b2b2b!important;
}
.roster-table-content .uurNummer {
	color: #2b2b2ba6!important;
	border-radius: 5px!important;
	border: 2px solid #2b2b2bb3!important;
}
.uurNummer {
	width: 40px;
	font-size: 16px;
	color: #2b2b2b8a;
	background-color: #15ff00;
	font-weight: 700;
	top: 50%;
	text-align: center;
	vertical-align: middle;
	margin-left: 10px;
}
#master-panel {
	display: table-cell;
	background-color: #00000066;
	position: relative;
	vertical-align: top;
	-moz-transition: width .3s;
	-webkit-transition: width .3s;
	-o-transition: width .3s;
	-ms-transition: width .3s;
	transition: width .3s;
}
.roster-table-header .day {
	text-align: center;
	font-size: 15px;
	background: #0000005e;
	height: 100%;
}
.roster-table-header .day .huiswerk-items {
	background-color: #0e0e0e;
	min-height: 100px;
	height: 100%;
	padding-left: 3px;
}
.afspraak .toekenning {
	background-color: #2b2b2ba1;
	margin-left: 3px;
	height: 100%;
}
.afspraakVakNaam {
	height: 14px;
	color: #484848;
	font-size: 13px;
}
#main-menu a.active,
#main-menu a.active:hover {
	text-decoration: none;
	cursor: pointer;
	color: #000000;
	background: #2b2b2b94;
}
.roster-table-header {
	border-top: 2px solid #2b2b2b8f!important;
	border-bottom: 0!important;
}
.yellow.ribbon a {
	line-height: 22px;
	color: #2b2b2b;
}
.afspraakLocatie {
	color: #2b2b2b;
	font-size: 12px;
	line-height: 20px;
	float: left;
}
.yellow.ribbon span {
	font-style: italic;
	color: #2b2b2b;
	position: relative;
	margin-right: 5px;
}
.d-next,
.d-prev {
	border-style: solid;
	border-color: #2b2b2b;
	border-width: 5px 5px 0 0;
	content: '';
	display: inline-block;
	height: 10px;
	width: 10px;
	position: relative;
	vertical-align: top;
	background: 0 0;
}
#main-menu a {
	display: block;
	float: left;
	height: 30px;
	margin-right: 4px;
	font-size: 13.5px;
	border-radius: 5px 5px 0 0;
	padding: 12px 16px 4px;
	color: #000000;
	background: #2b2b2b30;
}
#master-panel .date div,
.pauze,
.roster-table-header .day .huiswerk-items,
.uurNummer {
	background: #2b2b2b59!important;
}
.icon-geen-van-beide,
.icon-huiswerk,
.icon-leermiddelen,
.icon-studiewijzer,
.icon-toets {
	color: #2b2b2b;
}
.icon-geen-van-beide,
.icon-huiswerk,
.icon-leermiddelen,
.icon-maatregel,
.icon-studiewijzer,
.icon-toets {
	height: 22px;
	width: 22px;
	text-align: center;
	line-height: 22px;
	border-radius: 50%;
	background: #666;
	display: inline-block;
}
.roster-table-content .hours {
	display: table-cell;
	border-top: 1px solid #2b2b2b;
}
.weekitems .weekitemLabel {
	color: #2b2b2b;
	font-size: 15px;
	margin-left: 10px;
}
.dayTitle,
.weekTitle {
	padding: 10px;
}
.day {
	display: table-cell;
	position: relative;
	border-left: dotted 0px #2b2b2b;
	overflow: hidden;
}
.roster-table-content .hours .hour {
	border-top: 1px solid #2b2b2b!important;
	border-bottom: 1px solid #2b2b2b!important;
}
.toekenning a:hover {
	color: #615f5f;
}
#main-menu-bottom {
	height: 5px;
	background-color: #2b2b2b;
}
#header .left {
	padding-top: 2px;
	line-height: 20px;
	margin-bottom: 10px;
	font-size: 11px;
	color: #000000;
	overflow: hidden;
}
.pauze {
	width: 960px;
	height: 18px!important;
	color: #808080;
	background-color: #2b2b2b;
	line-height: 26px;
	vertical-align: middle;
}
.roster-table-header .day,
.roster-table-header .today {
	color: #2b2b2b!important;
}
.stpanel section div#logo figure.figurelogo {
	display: block;
	background-image: url(https://samensom.nl/wp-content/uploads/2019/06/somtoday-_logo-cmyk-600x129.png);
	background-position: center;
	background-size: contain;
	margin-left: 2%;
	height: 7.7rem;
}
.afspraak.nlg {
	background-color: #2b2b2bc2;
	border-radius: 5px;
	border-left: 3px solid #4b4f50;
	border-top: 1px solid #2b2b2b;
	border-right: 1px solid #2b2b2b;
	border-bottom: 1px solid #2b2b2b;
	box-shadow: 0 2px 5px #000000;
}
.ribbon .chk.checked, .ribbon .chk:hover, .ribbon.chk.checked {
    color: #ffffff;
    border-bottom: 3px solid #272727;
}
#user span.header {
    margin-right: 0;
    color: #2b2b2b;
    line-height: 50px;
    max-width: 180px;
    max-height: 50px;
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
}
.logo {
    display: flex;
    background-image: url(https://piskel-imgstore-b.appspot.com/img/4555d89c-20e3-11ea-8ca7-570d362a6519.gif);
    background-size: unset;
    background-position: center;
    background-repeat: no-repeat;
    width: 40px;
    height: 40px;
    margin: 1px 20px 10px 0;
}
.afspraakLocatie {
    color: #ccc;
    font-size: 12px;
    line-height: 20px;
    float: left;
}
#detail-panel-wrapper {
    display: table-cell;
    padding: 0 15px;
    background-color: #00000040;
    vertical-align: top;
    min-width: 250px;
}
.m-element, div.m-wrapperTable table tr td {
    margin-top: -1px;
    border: 1px solid #666;
    overflow: hidden;
}
.stpanel--error--message, .stpanel--status {
    background: url(https://files.gamebanana.com/img/ico/sprays/error1.gif);
    padding: 100rem;
    margin: 1rem auto;
    margin-bottom: auto;
}
#master-panel h2 {
    font-size: 15px;
    font-weight: 400;
    line-height: 20px;
    color: #efefef;
    margin-bottom: 4px;
}
#master-panel .sub {
    font-size: 10pt;
    color: #bbbbbb;
    padding: 4px 0 0 16px;
}
#master-panel .date div p.date-day, #master-panel .date div p.date-month, #master-panel .date span {
    color: #000000!important;
    font-weight: 600!important;
}
#master-panel .date div {
    border: 2px solid #000000!important;
    border-radius: 5px 0 0 5px!important;
    position: absolute;
    width: 28px;
    height: 42px;
    padding: 4px!important;
    margin: -1px 0 0 -40px!important;
    background-color: #2b2b2b;
    font-size: 21px;
    font-weight: 700;
}
.type {
