/* Navbar and footer */
.navbar, .footer {
	background-image: url('https://i.imgur.com/hgRiRbm.gif')!important;
	background: #171a21;
	border-radius: 0 !important;
	border-bottom: 0 !important;
    border-top: 0;
}

.navbar-inverse .navbar-nav > li > a {
	color: #c7c7c7!important;
