	background-image: url('https://i.imgur.com/NUe8maw.png'); 
	width: 105px;
	height: 21px;
	padding-right: 105px!important;
	margin: 3px 0 0 30px;
}

.navbar-logo {
    margin-top: -5px !important;
}


/* Main rules for font and positions */

body {
    font-family: -apple-system,BlinkMacSystemFont,Segoe UI,Helvetica,Arial,sans-serif,Apple Color Emoji,Segoe UI Emoji !important;
    padding-top: 80px;
}

#app {
    margin-bottom: 150px;
}

.main-container .container-content section, .chat-form  {
    background: #21242b;
}

/* Right section removing list ">" */
section.aside ul li:before {
    content: '';
}

/* Chat */
.chat-message {
    font-size: 12px;
}

.chat-box {
    height: unset;
    max-height: 380px;
}

body {
	background-color: #171a21;
}

.table > thead, .bbcodes {
    background: #171a21;
    border: 1px solid #171a21;
}

.table-striped > tbody > tr:nth-of-type(2n+1),  input.form-control, input.form-control[disabled], input.form-control[readonly], fieldset[disabled] input.form-control, select.form-control, textarea.form-control {
    background-color: #282b32;
}

input.form-control, input.form-control[disabled], input.form-control[readonly], fieldset[disabled] input.form-control, select.form-control, textarea.form-control {
    border: 1px solid #282b32;
}

textarea.form-control {
    resize: vertical;
    min-height: 150px;
}

.form-control:focus {
    border-color: #32353c;
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 8px #32353c88;
}

#app > div > a, .pull-right > section:nth-child(2), .pull-right > section.aside > img, .pull-right > section.aside > a, .bloc-title, .message-infos, .breadcrumb, .profile-secondary-title, .profile-infos tbody tr:first-child, .visible-xs.avatar-thumb, a[href*="https://brave.com/"] {
    display: none !important;
}

a {
    text-decoration: unset !important;
}


.pull-right > section.aside > audio {
    width: 100% !important;
}

.pagination li {
    margin-top: -20px !important;
}

.forum-tools {
    padding: 0 30px;
}

.processing .glyphicon {
	animation: processing .2s linear 0s infinite;
}

.aside ul {
    display: grid;
}

.forum-tools .left-tools input.form-control {
    max-width: unset;
    display: inline;
}

label {
    display: unset;
}

hr {
    margin-top: 10px;
    margin-bottom: 10px;
    border: 0;
    border-top: 1px solid #2b2e35;
}

.left-tools {
    margin-top: -1px
}

.forum-tools {
    margin-bottom: -10px;
}

@media (max-width: 470px) {
	.forum-tools, .forum-tools .left-tools, .form-inline, .forum-tools .left-tools .form-group {
		display: flex;
	}
	
	.forum-tools .left-tools input.form-control {
		width: 100%;
	}
	
	.forum-tools .left-tools .form-group, .left-tools {
		padding-right: 2px !important;
	}
	
	.forum-tools .left-tools .form-group:last-child {
		display: contents;
	}
}

.topic-message, .preview {
    border: .0625rem solid #171a21 !important;
    border-radius: 0;
    background-color: #282b32 !important;
}

.topic-icon > a > img {
    margin-top: -4px;
    margin-bottom: 0;
}

/* Topics */

.message-header {
    height: 68px !important;
    padding-left: 55px;
    border-bottom: .0625rem dotted #171a21 !important;
}

.topic-title {
    font-family: 'Roboto Condensed',Arial,Helvetica,sans-serif !important;
    text-align: inherit;
    font-weight: 600;
    font-size: 1.125rem;
    margin: 10px 0;
}

tr.active, tr.active td {
    background: #ff000030 !important;
}

@media (min-width: 991px) {
	.topics tbody tr td a {
		display: block;
	}
}

.topic-favorize {
    font-size: 0.7em;
    vertical-align: baseline;
}

section .pagination li {
    display: inline-block;
    font-size: 12px;
    font-weight: 600;
    vertical-align: sub;
    width: 25px !important;
    height: 25px !important;
}

section .pagination li a{
    vertical-align: sub;
}

section .pagination li.subactive {
    border-bottom: 0;
    background: #282b32;
}

.message-content-quote, .spoiler-content {
    margin: 0;
    padding: 5px 5px 5px 15px !important;
    font-size: 1em;
    line-height: 1.6em;
    border-left-style: solid;
    border-left-width: 5px;
    border-left-color: #171a21 !important;
    background: #21242b !important;
    color: #fff !important;
}

.table > tbody > tr > td {
    line-height: unset;
}


/* Avatars */
.message-aside {
    position: absolute;
    margin: 10px 0 0 10px;
    width: 46px !important;
    display: table;
    border: .0625rem solid #3a3d44;
    padding: unset !important;
}

.message-avatar {
    display: table-cell !important;
    vertical-align: middle;
    text-align: center;
    height: 44px;
    width: 44px;
    padding-bottom: unset !important;
}

.message-avatar img {
    max-width: 42px;
    max-height: 42px;
    height: unset !important;
    width: unset !important;
    vertical-align: middle;
}

.message-aside.hidden-xs {
    display: table !important;
}

/* Message actions */
.message-actions {
    display: flex !important;
    position: relative !important;
    top: 5px !important;
    list-style-type: none;
}

.message-actions li:first-child {
    margin-left: -45px;
}

.message-actions li {
    display: flex;
}

.grey-btn.btn[data-refresh] {
	padding: .45rem .6rem;
	margin-top: -3px;
}

img[src="/images/topic/delete.png"] {
    position: absolute;
    right: 23px;
	top: 7px
}

img[src="/images/topic/edit.png"] {
    position: absolute;
    right: 46px;
	top: 7px
}

/* Message quote */
.message-quote {
    position: absolute;
    right: 0px;
}

.message-username a {
    -webkit-font-smoothing:antialiased;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    font-weight: 600 !important;
    float: left;
    max-width: 12.5rem;
    margin-right: .75rem;
    font-size: 1.125rem !important;
    line-height: 1em;
    position: relative;
    top: .75rem;
    display: block;
    height: 1.375rem;
    font-family: 'Roboto Condensed',Arial,Helvetica,sans-serif !important;
}

.message-content {
    min-height: unset !important;
    font-family: -apple-system,BlinkMacSystemFont,Segoe UI,Helvetica,Arial,sans-serif,Apple Color Emoji,Segoe UI Emoji !important;
	font-size: 13px;
}

.message-date {
    position: absolute;
    top: 2.25rem;
    font-size: .6875rem;
}

.message-date a {
    color: rgb(232, 230, 227) !important;
}

.btn {
    display: inline-block;
    margin-bottom: 0;
    font-weight: 700;
    text-align: center;
    white-space: nowrap;
    vertical-align: middle;
    cursor: pointer;
    background-image: none;
    border: .0625rem solid transparent;
    padding: .375rem .75rem;
    font-size: .875rem;
    line-height: 1.42857;
    border-radius: .25rem;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
}

.btn-primary, .btn-danger {
    text-transform: uppercase;
    color: #fff;
    background: #171a21 !important;
    border: 0;
    font-size: .75rem;
    transition: 0.2s;
}

.dropdown-menu {
    background: #171a21 !important;
}

.dropdown-menu li a {
    color: #fff !important;
}

.dropdown-menu li:hover, .dropdown-menu a:hover, .dropdown-menu li:focus, .dropdown-menu a:focus {
    background: #21242b !important;
}

.btn-primary:hover, .btn-danger:hover {
    background: #171a2166 !important;
    transition: 0.2s;
}

.btn-primary:focus, .btn-primary:active:focus, .btn-primary:active, .btn-danger:focus, .btn-danger:active, .btn-danger:active:focus {
    background: #0d1017 !important;
    outline: 5px auto #0d101799 !important;
    transition: 0.2s;
}

/* Profile */

.profile-title h2 {
    font-family: 'Roboto Condensed',Arial,Helvetica,sans-serif !important;
    display: flex;
    font-weight: 600 !important;
    text-shadow: 0px 0px 10px #fff;
}

.profile .profile-section {
    background: none;
}

.profile main.profile-section {
    display: flex;
}

.profile {
    background: #21242bf0;
    border-radius: 5px;
}

.profile-biography {
	margin-top: -40px !important;
}

.profile .profile-wrapper-left, .profile .profile-wrapper-right {
	width: unset;
}

.profile-title {
    margin-bottom: -10px !important;
}

.profile-infos tbody, .profile-stats tbody {
    background: #ffffff08;
}

.profile .profile-stats {
    margin: unset;
    width: 100%;
}

.profile-avatar img {
	border-radius: 5px;
}

.profile-infos tbody tr, .profile-stats tbody tr {
    border-bottom: 1px dotted #171a21;
}

.profile-infos-left {
    padding-left: 10px;
}

.profile-infos-left, .profile-stats tbody tr td:first-child {
    text-align: right;
	font-weight: bold;
	margin-right: -5px;
	border-radius: 5px 0 0 5px;
}

.profile-infos-right, .profile-stats tbody tr td:last-child {
    text-align: left;
	border-radius: 0 5px 5px 0;
}

.profile-title .player-wrapper {
    position: absolute;
    top: 24px;
    right: 30px;
    display: flex;
    float: right;
}

.profile-title .glyphicon-play, .profile-title .glyphicon-pause {
    font-size: 20px;
}

.topics tbody tr td {
    font-family: -apple-system,BlinkMacSystemFont,Segoe UI,Helvetica,Arial,sans-serif,Apple Color Emoji,Segoe UI Emoji !important;
    font-size: 11px !important;
}

.topics-title {
    font-weight: 700;
}

.right-tools {
    padding-top: 2px;
}

.bottombar ul {
    background: #171a21aa;
}

