@-moz-document domain("userbenchmark.com") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

selector
{
	text-transform: capitalize!important;
}

.btn-default,
.pagination>li>a, .pagination>li>span,
.pagecontainer,
.pagination>.disabled>a, .pagination>.disabled>a:focus, .pagination>.disabled>a:hover, .pagination>.disabled>span, .pagination>.disabled>span:focus, .pagination>.disabled>span:hover,
.select2-container .select2-choice,
.select2-container-active .select2-choice,
.select2-container-active .select2-choices,
.wheattext,

:focus,
[style*="background-image:linear-gradient(to left, #ddd 0%, white 50%)"],
a.bglink:hover,
a.mfam-imglink:hover
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

.mh-td th.mh-td-col, .mh-td th.mh-td-th-arrow {
    border-right: none;
}

table {
    border-collapse: separate;
    border-spacing: 0 12px;
}

/* Fonts 
_____________________________________________________________________________________________________*/

selector
{
	font-size: 2.5rem!important;
}

.medp,

body
{
	font-size: 1.85rem!important;
}

.smallp,
.urd-modeldesc
{
	font-size: 1.2rem!important;
}

.semi-strongs.lighterblacktexts,
.medp,
x.smallp,

h5.fancyfont
{
	line-height: 2.5rem!important;
}

selector
{
	line-height: 2.1rem!important;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

[id*="div-gpt-ad-"]
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Left/Right/Top/Bottom 
_____________________________________________________________________________________________________*/

.be-mr {
    margin-right: 1rem;
}

.smallp {
    margin-top: 8rem!important;
}


.be-mr {
    margin-top: 1rem;
}

[style="float:left;width:30%;margin-top:-13px"] {
    float: left;
    width: 30%;
    margin-top: -11px;
    margin-bottom: 1rem;
}

.fa-square-o:before,
.fancyfont
 {
    top: -36px!important;
    left: 33px!important;
}

/* Margins 
_____________________________________________________________________________________________________*/

.boxthumb,
.center-block,
.col-xs-4,
.table,
.table-hover>tbody>tr,
.table-striped>tbody>tr:nth-of-type(odd),
.btn-group-vertical>.btn+.btn,
.btn-group-vertical>.btn+.btn-group,
.btn-group-vertical>.btn-group+.btn,
.btn-group-vertical>.btn-group+.btn-group,

form
{
	margin: 1.2rem!important;
}

selector
{
	margin: 3px 6px!important;
}

.btn-primary
 {
	margin: 3px 6px!important;
}

/* Paddings 
_____________________________________________________________________________________________________*/

.chapt-m-t,
.col-xs-4,
.col-xs-8,
.table,

[style*="background-image:linear-gradient(to left, #ddd 0%, white 50%)"],
form
{
	padding: 1.2rem!important;
}

.top-menu a,
.top-menu-lower a
{
	padding: 13px 26px!important;
}

/* Heights 
_____________________________________________________________________________________________________*/

.center-block
{
	max-height: none!important;
}

.be-mr,
.be-lb
{
	height: auto!important;
}

[style*="background-image:linear-gradient(to left, #ddd 0%, white 50%)"] {
	height: 21rem!important;
}

/* Widths 
_____________________________________________________________________________________________________*/

.center-block,
.pagebounds
 {
	max-width: none!important;
}

.btn-group-vertical>.btn, .btn-group-vertical>.btn-group, .btn-group-vertical>.btn-group>.btn,
.be-mr,
.btn-group-justified,
.btn-group-justified>.btn, .btn-group-justified>.btn-group,
.be-lb
 {
	width: auto!important;
}

.boxthumb,
.smallp
 {
	width: 98%!important;
}

.col-xs-8 {
    width: 64.666667%;
}
}

