/*
_____________________________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}

/* Misc 
_____________________________________________________________________________________________________*/

.token.punctuation
 {
    color: #fff!important;
}

h5.fancyfont {
    color: #faac04!important;
}

.urd-modeldesc,
.select2-large>.select2-container .select2-choice
 {
    color: #e3dfc9!important;
}

.token.property, .token.tag, .token.boolean, .token.number, .token.constant, .token.symbol, .token.deleted
 {
    color: #ff6cbe!important;
}



}

@-moz-document domain("userbenchmark.com") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

.btn-warning-3d,
.headergrad,
.pg-head-block,
.select2-container .select2-choice,
.select2-container-multi .select2-choices,
.select2-dropdown-open .select2-choice,

[style*="background-image:linear-gradient(to bottom, #eee 0%, white 40%)"],
[style*="background-image:linear-gradient(to left, #ddd 0%, white 50%)"]
{
	background-image: none!important;
}

/* Background 
_____________________________________________________________________________________________________*/

.headergrad,
.top-menu,

[style*="background:#333"]
 {
	background-color: #060505!important;
}

.top-menu-lower
 {
    background-color: #0a0909!important;
}

/* Background Transparent 
_____________________________________________________________________________________________________*/

#scrnotfooter,

.be-img:hover,
.btn-default,
.btn-default-3d:focus,
.btn-default-3d:hover,
.list-group-item,
.mh-td th.mh-td-col,
.mh-td th.mh-td-th-arrow,
.pagecontainer,
.pagination>.disabled>a, .pagination>.disabled>a:focus, .pagination>.disabled>a:hover, .pagination>.disabled>span, .pagination>.disabled>span:focus, .pagination>.disabled>span:hover,
.pagination>li>a, .pagination>li>span,
.table-hover>tbody>tr:hover,
.table-striped>tbody>tr:nth-of-type(odd),
.wheattext,

[style*="background-color:#F6F5F2"],
[style="background-color:white;"],
a.bglink-gray:hover,
a.bglink:hover,
a.mfam-imglink:hover
{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
_____________________________________________________________________________________________________*/

/* Blue 
_____________________________________________________________________________________________________*/

x.btn-default
{	
	background: radial-gradient(circle, #031017 0%, #0a587f 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem 1.85rem!important;
	position: relative!important;
	z-index: 1!important;
}

x.btn-default::before
{
	background: radial-gradient(circle, #0f79af 0%, #031017 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

x.btn-default:hover::before
 {
  opacity: 1!important;
}

/* Red 
_____________________________________________________________________________________________________*/

.btn.btn-danger,
.btn-default-3d
{	
	background: radial-gradient(circle, #380002 0%, #870101 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem 1.85rem!important;
	position: relative!important;
	z-index: 1!important;
}

.btn.btn-danger::before,
.btn-default-3d::before
{
	background: radial-gradient(circle, #870101 0%, #380002 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.btn.btn-danger:hover::before,
.btn-default-3d:hover::before {
  opacity: 1!important;
}

/* Orange 
_____________________________________________________________________________________________________*/

.btn.btn-warning,
.btn-primary
{	
	background: radial-gradient(circle, #651c03 0%, #e1410b 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem 1.85rem!important;
	position: relative!important;
	z-index: 1!important;
}

.btn.btn-warning::before,
.btn-primary::before
{
	background: radial-gradient(circle, #e1410b 0%, #651c03 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.btn.btn-warning:hover::before,
.btn-primary:hover::before {
  opacity: 1!important;
}

/* Green 
_____________________________________________________________________________________________________*/

.boxthumb,
.btn-default-3d:not(.extramutedtext),
.btn-warning-3d
{	
	background: radial-gradient(circle, #022d01 0%, #228005 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem 1.85rem!important;
	position: relative!important;
	z-index: 1!important;
}

.boxthumb::before,
.btn-default-3d:not(.extramutedtext)::before,
.btn-warning-3d::before
{
	background: radial-gradient(circle, #3de10b 0%, #066503 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.boxthumb:hover::before,
.btn-default-3d:not(.extramutedtext):hover::before,
.btn-warning-3d:hover::before {
  opacity: 1!important;
}

/* Plain Black 
_____________________________________________________________________________________________________*/

.be-int:hover,
.chapt-m-t,
.col-xs-2,
.col-xs-4,
.col-xs-8,
.pg-head-block,
.table-h-center,

[style*="background-image:linear-gradient(to left, #ddd 0%, white 50%)"]
{
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

/* Plain 
_____________________________________________________________________________________________________*/

.be-mr,
.select2-drop
{
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

.be-mr:hover,
.chapt-m-t:hover,
.col-xs-2:hover,
.col-xs-4:hover,
.col-xs-8:hover,
.pg-head-block:hover,
.select2-drop:hover,
.table-h-center:hover,

[style*="background-image:linear-gradient(to left, #ddd 0%, white 50%)"]:hover
{
	box-shadow: inset 0 0 5px #0006, #9b9b9b 0 1px 0, 10px 10px 5px #0a0a0a66, #9b9b9b 0 1px 0!important;
}

/* Red Gradient 
_____________________________________________________________________________________________________*/

.prbg
{
	background: linear-gradient(to bottom, #300202 0%, #590505 100%)!important;
}

/* Blue Gradient 
_____________________________________________________________________________________________________*/

selector
{
	background: linear-gradient(to bottom, #03193c, #000 100%)!important;
}

/* Green Gradient 
_____________________________________________________________________________________________________*/

.mh-td th.mh-td-col:hover,
.mh-td th.mh-td-sort-active,
.mh-td th.mh-td-th-arrow:hover,
.pgbg
{
	background: linear-gradient(to bottom, #15534d 0%, #000 100%)!important;
}

/* Yellow Gradient 
_____________________________________________________________________________________________________*/

.spybr,
.well
{
	background: linear-gradient(to bottom, #9d9d5d 0%, #000 100%)!important;
}

/* Orange Gradient 
_____________________________________________________________________________________________________*/

selector
{
	background: linear-gradient(to bottom, #da3a04 0%, #000 100%)!important;
}

/* Gray Gradient 
_____________________________________________________________________________________________________*/

selector
{
	background: linear-gradient(to bottom, #424040 0%, #000 100%)!important;
}

/* Gradient Border 
_____________________________________________________________________________________________________*/

selector
{
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
}

/* Code 
_____________________________________________________________________________________________________*/

pre
{
	background: url(https://image.ibb.co/e8fWZk/red_carbon_pattern_by_mystica_264_d4ceu9m.png) #111!important;
	background-size: 100%!important;
}


code
 {
    background: #FDFCE4!important;
    border: 1px solid #eec!important;
    font-size: 110%!important;
    padding: 3px 8px!important;
    white-space: pre-wrap!important;
}

/* Highlight Background 
_____________________________________________________________________________________________________*/

.list-group-item:hover,
.select2-results .select2-highlighted
{
	background: linear-gradient(to right, #3b0202, #0000)!important;
	border-left-color: #434343!important;
}

/* Radial Background 
_____________________________________________________________________________________________________*/

selector
{
	background: radial-gradient(ellipse at center, #07ed07 0%,#0000 70%)!important;
	padding: 13px 26px!important;
}

selector
{
	background: radial-gradient(ellipse at center, #9a9d00 0%,#0e0e0e 70%)!important;
	padding: 13px 26px!important;
}

.top-menu a.active,
.top-menu-lower a:hover
{
	background: radial-gradient(ellipse at center, #00809d 0%,#0e0e0e00 70%)!important;
	padding: 13px 26px!important;
}


}

