css:
@-moz-document domain("invidio.us") {
center, hr {
    display: none;
}
.pure-g:not(.h-box) h3 {
    margin: 20px 0 10px;
}
.pure-u-md-20-24 > .pure-g:not(.h-box) > .pure-u-md-1-4 .h-box {
    border-radius: 5px;
    background: #2a2a2a;
    padding: 8px;
    margin: 5px;
    opacity: 0.75;
    box-shadow: 0px 1px 20px rgba(0,0,0,0.3);
    transition: all 0.2s;
}
.pure-u-md-20-24 > .pure-g:not(.h-box) > .pure-u-md-1-4 .h-box:hover {
    transform: scale(1.1);
    transition: all 0.2s;
    box-shadow: 0px 1px 50px rgba(0,0,0,0.6);
    opacity: 1;
    z-index: 99999;
}
.pure-u-md-20-24 > .pure-g:not(.h-box) > .pure-u-md-1-4 .h-box a[href^="/channel/"] {
    background: #393939;
    padding: 3px 6px;
    border-radius: 20px;
}
.pure-u-md-20-24 > .pure-g:not(.h-box) > .pure-u-md-1-4 .h-box a[href^="/channel/"]:hover {
    background: rgb(0, 182, 240);
    color: #FFF;
}
.pure-u-md-20-24 > .pure-g:not(.h-box) > .pure-u-md-1-4 .h-box p > a[href^="/watch"] {
    position: relative;
    left: 7px;
}
.pure-u-md-20-24 > .pure-g:not(.h-box) > .pure-u-md-1-4 .h-box > p {
    margin: 8px 0px;
}
.pure-u-md-20-24 > .pure-g:not(.h-box) > .pure-u-md-1-4 .h-box h5 {
    margin: 0px 8px 5px;
}
}
