css:
@-moz-document domain("focus.pl") {
html,body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scrollbar-color: #E20714 #1c1c1c;
}
a{
    color: #E20714
}
/*HEADER*/
header .container,nav.navbar.bootsnav,.navbar-default .navbar-collapse{
    background: #1c1c1c;
}
.page-header{
    border-top: 0;
}
.magazine-last-box,.navbar-default .navbar-nav > li > a{
    color: #fff;
}
.navbar-default .navbar-nav > li:hover > a{
    background: #1c1c1c;
    color: #E20714;
}
.search a img{
    filter: invert(100%);
}
.navbar-default .navbar-nav > .active > a, .navbar-default .navbar-nav > .active > a:hover, .navbar-default .navbar-nav > .active > a:focus{
    background: #1c1c1c;
    border: 1px solid #fff;
    color: #E20714;
}
/*HEADER*/
/*STRONA-GŁÓWNA*/
.container, .container-fluid{
    background: #1c1c1c;
}
.main-feature-3,.main-feature-3:hover{
    background: #1c1c1c;
}
.shapes-preview,.one-carousel-top .shapes-preview{
    border-top-color: #E20714;
}
.item-category,.one-carousel-top .item-category{
    color: #E20714;
}
.box-all-link a p, .box-all-link p a{
    color: #fff;
}
.box-all-link a p:hover, .box-all-link p a:hover,.row-item:hover a{
    color: #959393;
}
.marquee-box marquee,.marquee-box p{
    color: #fff;
}
/*STRONA-GŁÓWNA*/
/*PODSTRONY*/
/*NEWSLETTER*/
.form-newsletter input, .form-newsletter select{
    background: #1c1c1c;
    color: #fff;
}
.form-newsletter input[type="checkbox"]{
    background: #1c1c1c;
}
.form-newsletter input[type="checkbox"]:checked::before{
    background: none #E20714;
}
.newsletter-form-btn button{
    background: #1c1c1c !important;
    border: 1px solid #fff !important;
}
.newsletter-form-btn button:hover{
    background: #E20714 !important;
    border: 1px solid #fff !important;
}
/*NEWSLETTER*/
/*MAGAZYN*/
.menu-nav-magazines .navbar-nav > li > a:hover{
    background: #E20714;
    color: #1c1c1c;
}
.magazin-list .magazin-list-item a p.lead{
    color: #E20714;
}
.magazin-list .magazin-list-item:hover a p.lead{
    color: #fff;
}
.pagination > .disabled > span, .pagination > .disabled > span:hover, .pagination > .disabled > span:focus, .pagination > .disabled > a, .pagination > .disabled > a:hover, .pagination > .disabled > a:focus{
    background: #1c1c1c;
    color: #3b3b3b;
}
.pagination > li > a, .pagination > li > span{
    background: #1c1c1c;
    color: #fff;
}
.pagination > li > a:focus, .pagination > li > a:hover, .pagination > li > span:focus, .pagination > li > span:hover{
    color: #E20714
}
.pagination > li.active span{
    color: #E20714;
}
.pagination > li.active span:hover{
    color: #E20714;
}
.pagination > li.first.active.disabled span{
    color: #3b3b3b;
}
.e-wydanie-box{
    background: #1c1c1c;
}
/*MAGAZYN*/
/*PODSTRONY*/
/*ARTYKUŁ*/
.breadcrumb, .sonata-page-breadcrumbs{
    background: #1c1c1c;
}
.breadcrumb a, .sonata-page-breadcrumbs a, .breadcrumb .last span, .sonata-page-breadcrumbs .last span{
    color: #fff;
}
.main-row h1,.articles-header .articles-header-author-date .author a,.prev-next-item a{
    color: #E20714;
}
.articles-header .articles-header-author-date p,.lead,.article-body,.prev-next-item h4,.article-gallery-main-photo p{
    color: #fff;
}
blockquote{
    background: #1c1c1c;
    border-color: #E20714;
}
/*PRENUMERATA*/
.one-carousel h3 span,.content-sidebar .one-carousel .magazine-sidebar-lead .e-wydanie h4{
    color: #000;
}
.content-sidebar .one-carousel .photo .carousel-btn a img:hover{
    filter: invert(100%);
}
/*PRENUMERATA*/
/*TAGI*/
.tags-items a{
    background: #1c1c1c;
    color: #fff;
}
.tags-items a:hover{
    border: 1px solid #fff;
    color: #E20714;
}
/*TAGI*/
/*ARTYKUŁ*/
/*FOOTER*/
.footer-seo01 ul li a,.footer-seo01 ul,.get-promo-link li a{
    color: #fff;
}
.container-fluid.container-fluid-footer .get-promo-link li:first-child:before{
    content: "| Ciemny motyw by Maciejozaman ";
    color: #E20714;
}
/*FOOTER*/
}
