css:
@-moz-document domain("ezgif.com") {
body,
html,
#wrapper,
#submenu,
.file-menu {
	background: #100e17;	
}

#menu a,
p,
h1,
h2,
h3,
ul,
.footer-menu-row strong {
	color: #e9e9e9 !important;
}
a,
a:hover {
	color: #dd2a2a;
}
#menu li.active a {
	background-color: #dd2a2a;
}

#menu li a:hover {
	background-color: #dd2a2a;
}

#submenu ul li a,
#submenu ul li.active a {
	color: #e9e9e9;
	border-color: #201c29;
	border-radius: 3px
}

#submenu ul li.active a {
	background-color: #dd2a2a;
}

#submenu ul li:first-child,
#header {
	border-color: #dd2a2a;
}

.file-menu {
	border: none;
}
.file-menu a.active {
	color: #e9e9e9;
	background-color: #dd2a2a;
	box-shadow: initial;
	text-shadow: initial;
	border: none;
}
.file-menu a {
	color: #e9e9e9;
	box-shadow: initial;
	text-shadow: initial;
	border: none;
	background-color: #201c29;
}

.file-menu a:hover, .file-menu a.save:hover {
	background: #dd2a2a;
	border:  none;
	box-shadow: initial;
	color: #e9e9e9;
}

.video-js  {
	height: 500px !important;
}


#hdr {
	display: none;
}

#logo {
	display: none;
}

.vjs-poster {
	background-color: #100e17 !important;
}
.convert-btn,
.convert-btn:hover {
	background: #121212 !important;
	text-shadow: none;
	border: none;
	color: #dd2a2a;
}

.options {
	background: #100e17;
	border-color: #201c29
}

.form .options label input,
.form select {
	background: #100e17;
	color: #e9e9e9;
	border-color: #201c29;
}

.button.primary {
	background:  #dd2a2a;
	border-color: #dd2a2a;
	color: #fff;
}

#output {
	background: #100e17 !important;
}

.txt {
	background: #100e17;
	border: 1px solid #201c29
}

#footer {
	background: #100e17;
	border-top-color: #201c29
}

#news ul li {
	background: #100e17;
	border: 1px solid #201c29
}

#sidebar {
	display: none;
}

#main {
	margin: initial;
}

.form legend {
	color: #e9e9e9;
}

#upload-form fieldset {
	background: #100e17;
	border: 1px solid #201c29;
}

#upload-form {
	background: #100e17;
}

.form input.text {
	background: #100e17;
	border-color: #201c29;
	color: #e9e9e9;
}
}
