css:
@-moz-document domain("venticity.net") {
body {
    background-color: #131313 !important;
}
.vp_vid_foot {
    background-color: #232323 !important;
}
.vp_vid_list .video-list-title h4 {
    color: #f4f4f4
}
.vp_vid_list .video-list-by a {
    color: #fff
}
a {
    color: #fff
}
header {
    background-color: #131313
}
body {
    background: #131313;
    color: #fff
}

.vp_featured_vid:before {
    background: linear-gradient(180deg, rgba(0, 0, 0, .5) 0%, #131313 100%)
}

.video-big-title a {
    color: #ebebeb
}

.articles-content {
    background-color: #131313;
}

.recent-articles {
    border-bottom: 1px solid #232323
}

.pt_footer .container-fluid {
    background: #131313;
    border-top: 1px solid #131313
}

nav.navbar-findcond ul.dropdown-menu {
    background: #1a1a1a
}

.dropdown-menu.ani-acc-menu li > a,
.dropdown-menu.ani_log_menu li a {
    color: #f4f4f4
}

.dropdown-menu.ani-acc-menu li.divider {
    background: #222
}

.vp_acc_menu_hd .hd_btn {
    border: 1px solid #1a1a1a
}

nav.navbar-findcond .search-header input {
    background-color: #1a1a1a
}

nav.navbar-findcond .search-header input:focus {
    background-color: #2f2f2f
}

nav.navbar-findcond .search-header input {
    color: #fff
}

nav.navbar-findcond .search-header .search-dropdown {
    background-color: #2f2f2f
}
nav.navbar-findcond .search-header .search-dropdown .search-result a {
    color: #fff
}

nav.navbar-findcond .search-header .search-dropdown .search-result a:hover {
    background-color: #444
}

.video-big-title {
    color: #fff
}

.video-options .btn_opts span {
    background: #2f2f2f
}

.video-options .btn_opts {
    color: #fff
}

.video-options .btn_opts:hover span {
    background: #444
}

.publisher-element .publisher-name a {
    color: #fff
}

.vp_vid_page_lmore:hover {
    background: #444
}

.pt_video_comments .yp_video_comment textarea {
    background: #2f2f2f
}

.pt_video_comments .yp_video_comment textarea:hover {
    background: #444
}

.form-control,
output {
    color: #fff
}

.pt_video_comments .yp_video_comment textarea:focus {
    background: #444
}

.dropdown-menu {
    background-color: #2f2f2f
}

.pp_mat_input > input,
.pp_mat_input > textarea {
    background: #444
}
.pp_mat_input > select {
    background: #444
}

.form-control,
output {
    color: #2f2f2f
}

.user_sttng_panel {
    background-color: #2f2f2f
}

.pp_mat_input > input,
.pp_mat_input > textarea {
    color: #fff
}

.pp_mat_input > select {
    color: #fff
}
.settings-sidebar ul {
    background-color: #2f2f2f
}

.settings-sidebar ul li.active a {
    background-color: #444
}

.settings-sidebar ul li.active a {
    color: #fff
}

.pp_mat_input > input:placeholder-shown:not(:focus) + label,
.pp_mat_input > textarea:placeholder-shown:not(:focus) + label {
    color: #fff
}

.vp_cset_cover .vp_cset_cover_hdr .btn-file {
    background-color: #444
}

.vp_cset_cover .vp_cset_cover_hdr .btn-file span,
.vp_cset_avtr .vp_cset_avtr_hdr .btn-file span {
    background-color: #575757
}

.vp_cset_cover .vp_cset_cover_hdr .btn-file span,
.vp_cset_avtr .vp_cset_avtr_hdr .btn-file span {
    color: #fff
}

.vp_cset_avtr .vp_cset_avtr_hdr .btn-file {
    background-color: #444;
    border: 5px solid #575757
}

ul.notfi-dropdown ul#notifications-list li.notifications-list {
    background: #444
}

ul.notfi-dropdown ul#notifications-list li div.notifications-info p {
    color: #fff
}

ul.notfi-dropdown ul#notifications-list li div.notifications-info p .main-color {
    color: #fe5d44
}

ul.notfi-dropdown ul#notifications-list li.notifications-list:hover {
    background: #575757
}

.main-comment .user-name a {
    color: #fff
}

.user-comments .main-comment .user-comment {
    color: #fff
}

.content {
    background: #0000
}

.pt_upload_vdo .upload {
    background-color: #282828;
    border: 1px dashed #262626
}

.pt_upload_vdo .upload p {
    color: #fff
}

.pp_mat_input > ul.tagit {
    background: #444
}

.create-new-ad-container .pp_mat_input .bootstrap-select > button.btn,
#submit-imported-video .pp_mat_input .bootstrap-select > button.btn,
.vp_edt_vdo_pg .pp_mat_input .bootstrap-select > button.btn,
.vid_upld_vdo_geo .pp_mat_input .bootstrap-select > button.btn {
    background: #444;
    color: #fff
}

.pp_mat_input > select {
    color: #444
}

.dropdown-menu.vp_creator_menu li a {
    border: 1px solid #ebebeb00
}
}
