css:
@-moz-document domain("sredniawski.pl") {
:root {
    --gcolor: #22AA44;
    --bgc: #F8F9FA;
    --bgc2: #DDDFDF;
}

html {
  box-sizing: border-box;
    background-color: var(--bgc);
}
*, *:before, *:after {
  box-sizing: inherit;
}

body {
    color: #212121;
    background-color: var(--bgc);
}

#bg {
    display: flex !important;
    background: none;
}

#bg > div {
    flex: 1;
    display: flex;
    flex-flow: column nowrap;
}

#header {
    width: auto;
    display: flex;
    flex-flow: column nowrap;
    justify-content: center;
    align-items: center;
    height: auto;
}

#header_l {
    flex: 0 1 auto;
    margin: 1rem;
    background: none;
    width: auto;
}

#header_r {
    flex: 0 1 auto;
    width: 100%;
    background: url(https://bip.malopolska.pl/api/files/1133206);
    background-repeat: no-repeat;
    background-size: contain;
    margin: 1rem 4rem;
    position: relative;
    left: -80px;
}

#header_in {
    width: 100%;
    height: 100px;
}

#header_in::after {
    content: "Zespół Szkół im. Andrzeja\AŚredniawskiego w Myślenicach";
    font-size: 3rem;  
    font-weight: bold;
    display: block !important;
    box-sizing: border-box !important;
    position: relative;
    left: 100px;
    white-space: pre-wrap;
    color: #323232;
}

#logo_box {
    display: none;
}

#top_l {
    background: none;
    background-color: #343a40;
    width: 100%;
    align-self: stretch;
}

#top_r {
    background: none;
    width: 100%;
}

#top {
    width: auto;
    height: auto;
    display: flex;
    justify-content: space-between;
    padding: 1rem;
}

#menu_box {
    float: none;
    width: auto;
}

#menu {
    height: auto;
    margin: 0;
}

#search_box {
    float: none;
    width: auto;
}

#search {
    float: none;
    padding: 0;
}

.search .inputbox {
    color: #424242;
    padding: 0.5rem 1rem;
    background: none;
    background-color: var(--bgc);
    border-radius: 0.4rem;
    width: auto;
    height: auto;
    vertical-align: auto;
    line-height: normal;
}

#container {
    display: flex;
    flex-flow: row nowrap;
    width: auto;
}

#left_col {
    flex: 0 1 auto;
    width: auto;
    padding: 2rem;
}

#left_mod {
    width: auto;
}

#left_mod div.module {
    width: auto;
    background: none;
}

#left_mod div.module div {
    background: none;
    background-color: var(--bgc2);
    border-radius: 0.4rem;

}

#left_mod div.module div div {
    background: none;
    padding: 0;
}

#left_mod div.module div div div {
    padding: 0;
    width: auto;
}

#left_mod div.module div div div table, #left_mod ul, #right_mod ul,
#right_mod div.module div div div table {
    padding: 1rem;
}

#left_mod a.mainlevel:link, #left_mod a.mainlevel:visited, #left_mod ul.menu li a:link, #left_mod ul.menu li a:visited {
    color: #424242;
}

#left_mod a.mainlevel#active_menu, #left_mod ul.menu li a#active_menu, #left_mod ul.menu li#current a {
    color: var(--gcolor);
}

#left_mod div.module h3,
#right_mod div.module h3 {
    background-color: #44BB66;
    padding: 1rem 0;
    color: #FAFAFA;
    text-indent: 0;
    height: auto;
    line-height: normal;
    margin-bottom: 0;
    text-align: center;
    border-radius: 0.4rem 0.4rem 0 0;
}

#pathway_box {
    display: none;
}

#right_col {
    flex: 1;
    display: flex;
}

#foot_l {
    width: auto;
    background: none;
    background-color: #343a40;
}

#foot_r {
    background: none;
    width: auto;
}

#foot {
    width: auto;
    display: flex;
    justify-content: space-around;
    padding: 1rem;
    height: auto;
}

#license_box, #copyright_box {
    width: auto;
    float: none;
}

#license {
    padding: 0;
    float: none;
}

#copyright {
    padding: 0;
    text-align: center;
}

#content {
    background: none;
    width: auto;
    padding: 1rem;
}

#content_in {
    background: none;
    width: auto;
}

#mainbody {
    padding: 0;
}

#right_box {
    float: none;
    width: auto;
}

#right_mod {
    width: auto;
    padding: 0;
}

#right_mod div.module {
    width: auto;
    background: none;
    margin: 0;
    padding: 2rem 2rem 0 2rem;
}

#right_mod div.module div {
    background: none;
    
    width: auto;
}

#right_mod div.module div div {
    width: auto;
    background: none;
}

#right_mod div.module div div div {
    padding: 0;
    width: auto;
    color: #424242;
    border-radius: 0.4rem;
    background-color: #DDDFDF;
}

#right_mod div.module div div div strong {
    display: inline-block;
    padding: 1rem;
}

.joomimg47_main {
    padding: 1rem !important;
}

#content-noright {
    background: none;
    width: auto;
    margin: 3rem 3rem 3rem 0;
    flex: 1;
}

#content_in-noright {
    background: none;
    width: 100%;
}


#left_mod a:link, #left_mod a:visited, #right_mod a:link, #right_mod a:visited, #mainbody a:link, #mainbody a:visited, .contentheading, .componentheader, .componentheading {
    color: var(--gcolor);
}

#mainbody .pagenav,#mainbody .pagenav_next, #mainbody .pagenav_prev {
    color: #777;
}

.menu a:hover {
    color: #66CC88;
}

.moduletable > hr {
    color: transparent;
    border: 1px solid #AAA;
}

#left_mod li, #right_mod li {
    padding: 0;
    margin: 0;
}
}
