css:
@-moz-document domain('music.yandex.ru') {
 /* Стиль темы смотрится хорошо ДЛЯ ТЁМНОЙ ТЕМЫ. Сначала выберите её в настройках Яндекс.Музыка */

/* Ad blocks removing */
.app-sms, .app-sms__content, .artist-pics__desc, .stamp, .payment-bar, .payment-show, .payment-bar__pay, .teaser, .subscription-hint, .subscription-hint_top, .overhead-wrapper, .ads-block, .ads-block__no-ads, .smalladv, .ads-block_side, .slider-hider, .sidebar-cont__adContainer, .slider-hider_ads-block, .event__notify, .audio-advert__block, .bar__new-pay, .bar__addition, .bar-below, .d-overhead, .sidebar.deco-pane, .payment-plus-popup, .overlay.overlay_shown {
    display: none !important;
}

/* Убрать херню на слайдере (машинки, лыжники) */
.winter__skater-pause, .winter__skater-play, .winter__skater-pause_left,
.bar__branding, .haval__car, .haval__car-pause {
    display: none !important;
}

/* Высокий прогресс бар, легче тыкать пальцем или мышкой */
.deco-progress .progress__bar, .deco-progress .progress__bar:hover {
    height: 32px !important;
    line-height: 32px !important;
}

/* Убрать в меню Аккаунта из меню "Попробовать +" */
li.multi-auth__line >div.multi-auth__plus {
    display: none;
}

/* Убрать верхний баннер с уведомлялкой */
div[data-b="2"], div[data-b="3"] {
    display: none !important;
}

/* Расширить на весь экран */
.page-root {
    max-width: 1920px;
}
.centerblock-wrapper {
    margin-right: 0px !important;
    width: 100%;
}


/* No max width for sidebar panel */
.sidebar-cont, .bar {
    max-width: none;
}

/* Coloring for tracklists */
.d-track:nth-child(odd) {
    background: #333;
}

.d-track {
    border-left: 1px solid #333;
    border-right: 1px solid #333;
}

.d-track:last-child {
    border-bottom: 1px solid #333;
}

/* Small fixes */
.d-track_selectable:hover:after, .d-track_selectable.d-track_hover:after, .d-track_selectable.d-track_selected:after {
    bottom: 0px;
}

.sidebar-album__tracks > div {
    margin: 0 -13px;
}

.sidebar-artist__tracks {
    margin: -5px -18px 0px -16px;
}

.longlist__cont {
    margin: 0 -8px 0 -12px;
}

.d-track__start-column {
    margin-left: 6px;
}

.d-track_best:before {
    left: 8px;
    top: 14px;
}

.page-playlist__upload {
    border-top: 1px solid #333;
}

.page-album__label .link {
    margin-left: 10px;
}

.popup_page {
    min-height: calc(100% - 60px);
    box-sizing: border-box;
    border-left: 2px dashed;
    border-right: 2px dashed;
}

.sidebar-album__tracks > .sidebar-album__vol {
    padding: 10px 0 10px 10px;
}
}
