css:
@-moz-document domain("thestar.com") {
/* 
* TheStar Dark Night Mode
* 
* A dark style for the Toronto Star (aka The Star) website.
* 
* By pkiff
* Version 1.0
* Last update: 2019-12-14
* Initial launch: 2019-12-14
*
* 2019-12-14: Version 1.0
* - Initial launch
*
*/

/* Colors
*
* blue: #0072bc
* black: #111
* off-black: #222 
* grey: #444
* light: #999
*
* */


body {
  background-color: #111;
  color: #999
}


/* layout */

.o-breadcrumb-header {
  background-color: #111;
}

.o-hero-header {
  background-color: #222;
}

.o-main-menu-container {
  background-color: #222;
}

.o-main-menu {
  background-color: #222 !important;
}

.o-sub-menu {
  background-color: #222 !important;
}

.o-main-menu.o-main-menu--no-submenu {
  border-bottom-color: #111;
}

.o-section-header {
  background-color: #111;
}

.o-site-footer {
  background-color: #111 !important;
}

.o-tophat {
  background-color: #222;
}

.c-article-layout {
  background-color: #222;
}

.c-article-wrapper {
  background-color: #111;
}
.c-articles-equal-column-list {
  background-color: #222;
}

.c-articles-equal-column-list__heading {
  border-bottom-color: #111;
}

.c-article-fullbleed {
  background-color: transparent;
  border-top-color: #111;
  border-bottom-color: #111;
  margin-bottom: 0;
}

.c-articles-rail {
  background-color: #222;
}

.c-button.c-button--main-nav {
  color: #999;
}

.c-cityname--open {
  color: #999;
}

.c-conversation-title {
  color: #999;
  border-bottom-color: #666;
}

.c-conversation-indicator-icon--aggregation {
  background: url(https://www.thestar.com/assets/img/indicator-icon.svg) no-repeat !important;
  width: 24px;
  height: 24px;
}

.c-footer-group__title {
  color: #666;
}

.c-footer__anchor {
  color: #666;
}

.c-logo-anchor--thestar img {
  background: url(https://www.thestar.com/assets/svg/logo-toronto.svg) no-repeat;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
  padding-left: 623px;
}

.c-mediacard:hover .c-mediacard__heading span,
.c-mediacard:active .c-mediacard__heading span {
  border-bottom-color: #0072bc !important;
}

.c-disclaimer {
  color: #666;
}

.c-mediacard__heading {
  color: #999;
}

.c-mediacard-img {
  background-color: #222;
}

.c-page-layout {
  background-color: #222;
}

.c-pullquote-logo {
  color: #666;
}
.c-pullquote .c-pullquotetext {
  color: #666;
}


/* menu */

.c-underline {
  transition-property: none;
}

.o-sub-menu .c-underline, 
.o-main-menu .c-underline {
  background-color: #0072bc !important;
}


/* aggregation and containers */

.aggregation {
  background-color: #222;
}

.top-aggregation,
.container-aggregation {
  background-color: #222;
}

.top-aggregation .container-aggregation__overset {
  border-right-color: #222;
}

.top-aggregation__list li .story {
  border-color: #444;
}

.top-aggregation__top .top-aggregation__secondary li .story {
  border-color: #444;
}

.equal-columns__list li .story {
  border-color: #444;
}

.feature-with-list__list li .story {
  border-color: #444;
}

.container-gutters {
  border-color: #222;
}

.homepage-topstories-container {
  background-color: #222;
}

.left-rail .container-aggregation__overset {
  background-color: #222; 
  border-left-color: #222;
}

.left-rail .layout-alpha__body {
  background-color: #222;
}

.recommended-for-you-cta {
  background-color: #444;
}

.section-header {
  background-color: #222;
  border-color: #444;
}

.o-section-header {
  border-right-color: #222;
  border-right-width: 2px;
  border-right-style: solid
}

.side-box {
  background-color: #222;
}

.author-endnote-container {
  color: #999;
  border-top-color: #111;
}

.author-endnote-container.border-bottom {
  border-bottom-color: #111;
}


/* textblock */

.text-block-container {
  color: #999;
}

.text-block-container ul > li {
  color: #999;
}

a.text-block__link {
  color: #ddd;
  border-bottom-color: #0072bc !important;
}

a.text-block__link:hover, 
a.text-block__link:focus {
  background-color: #0072bc !important;
  border-bottom-color: #ddd !important;
  transition-property: none !important;
}


/* related links */

.related-links-title {
  color: #666;
}

.related-links-item div p a {
  color: #666 !important;
}

.related-links-item div p a:hover, 
.related-links-item div p a:active, 
.related-links-item a:hover, 
.related-links-item a:active, 
.related-links-item :visited:hover, 
.related-links-item :visited:active {
  color: #0072bc !important;
  border-bottom-color: #0072bc !important;
  transition-property: none !important;
}



/* related topics */

.related_topics .tags a:hover, 
.related_topics .tags a:focus {
  color: #ddd !important;
  text-decoration: underline;
}

/* share icons */

.share-icon-anchor:hover, 
.share-icon-anchor:active {
  color: #ddd !important;
}


/* trustbar */

.trust-item-staricon {
  color: #666;
}

.trust-item-text {
  color: #666;
}

.trust-item-text:hover,
.trust-item-text:focus {
  color: #0072bc;
  text-decoration: underline;
}


/* story elements */ 

.story .story__abstract {
  color: #999;
}

.story .story__label {
  color: #999;
}


/* ad elements */

.article-bottom-ad-container {
  background-color: transparent;
}

.origami-widget-container {
  background-color: #111;
}

/* captions */
.caption-container .caption-button .show-caption-icon:hover,
.caption-container .caption-button .show-caption-icon:focus {
  color: #444 !important;
}


/* viafoura - comments */

.viafoura .vf-commenting {
  background-color: #222;
  color: #999;
}

.vf-comments-widget {
  color: #999;
}

#conversations .viafoura .vf-user-meta.vf-hlist .vf-username {
  color: #999;
}

#conversations .vf-comment-html-content {
  color: #999;
}

#conversations .vf-count-likes, 
#conversations .vf-count-dislikes,
#conversations .vf-reply-btn::after {
  color: #666;
}

.vf-date {
  color: #999 !important;
}

.vf-comment-html {
  color: #999 !important;
}


/* SECTIONS */

/* specific elements */

.article-ads {
  background-color: #222;
}

.article__creditlogo-container {
  background-color: #666;
  border-right-color: #222;
}

.article__author-by, 
.article__author-credit {
  color: #999;
}

.c-article-layout .article__byline a, 
.c-article-layout .author-endnote-container a {
  color: #0072bc;
  text-decoration: none;
}

.c-article-layout .article__byline a:hover, 
.c-article-layout .article__byline a:focus, 
.c-article-layout .author-endnote-container a:hover,
.c-article-layout .author-endnote-container a:focus {
  color: #ddd !important;
  text-decoration: underline;
}

.article__time-container .article__published-date, 
.article__time-container .article__readtime  {
  color: #999;
}

.article-header__heading, 
.article-header__heading--long {
  color: #999;
  text-shadow: none;
}

.author-profile {
  background-color: #222
}

.author-profile .author-profile-info .author-profile-title {
  color: #666
}

.cta-description {
  color: #666;
}

.search-parent .search-parent__top .search-parent__form-wrap .search-parent__input-wrap input::placeholder {
  color: #666;
}

.search-parent .search-parent__top .search-parent__form-wrap .search-parent__query-btn:hover, 
.search-parent .search-parent__top .search-parent__form-wrap .search-parent__query-btn:focus {
  color: #0072bc;
}

.search-parent .search-parent__top .search-parent__wrap {
  border-bottom-color: #444;
}

.simple__top li .story {
  border-bottom-color: #444;
 
}

.single-column-profile .layout-alpha__body h4 {
  color: #999;
}

.staricon {
  color: #666 !important;
}

.share-icon-anchor:hover .staricon, 
.share-icon-anchor:focus .staricon {
  color: #0072bc !important;
}

.o-site-footer__social-icons a:hover .staricon, 
.o-site-footer__social-icons a:focus .staricon {
  color: #0072bc !important;
}

a .story__headline {
  color: #ddd;
}

a.text-block__link {
  color: #ddd !important;
}

.text-label-basic .text-label-text {
  border-color: #666;
  color: #666;
}


}
