css:
@-moz-document domain("avenoel.org") {
#risi-overlay, .risi-panel {
    width: unset;
    position: inherit;
    z-index: unset;
    left: unset;
    top: unset;
    background-color: unset;
    overflow-x: hidden;
}

#risi-overlay {
	margin-bottom: 10px;
}

.risi-r {
    max-height: 200px;
    overflow-y: auto;
}

.overlay .closebtn {
    position: absolute;
    top: 217px;
    right: 10px;
}

.overlay-content {
	width: unset;
	top: 0;
	margin: 0;
	display: flex;
    flex-direction: column-reverse;
}

.risi-sticker {
	padding: 0 !important;
}

.risi-sticker:hover {
	background: #0005;
}

.risi-sticker, .risi-img {
	max-width: 60px;
	max-height: 45px;
	height: unset;
	box-shadow: unset !important;
	border-radius: unset;
}

.risi-btn {
	font-size: 0.9em;
	height: 25px;
	overflow: hidden;
}

#risi-recherche {
	font-size: 1.1em;
	background: #0005;
}
}
