css:
@-moz-document domain("eclat-bfc.fr") {
body{color: #4ff;font-family:comic sans ms,sans-serif;font-size:.8125rem;font-size:0.8125rem;font-weight:500;line-height:1rem;}
a{color: #f00;text-decoration:none;cursor:pointer;}a:hover{color:#e00}a.disabled{cursor:not-allowed;color:#f00}
.tabs__button,.tabs__link{display:inline-block;color:rgba(255,0,0,.8);padding-bottom:.8125rem;padding-left:.25rem;padding-right:.25rem;margin-left:.25rem;margin-right:.25rem}
.tabs__button.active,.tabs__link.active{border-color:#f00;color:#f00}
.menu{background-color:#f0f}.menu,.menu a{color:#ff0}
.bar{background-color:#00f;}
html{box-sizing:border-box;background-color:#ff0}
.panel--outlined{background-color:#0f0;border:1px solid #9400D3}
.header{background-color: #ff8c00;;left:0;position:fixed;right:0;top:0;z-index:6000}
}
