css:
@-moz-document domain("brick-hill.com") {
/* BETA VERSION 

    this version is still in beta, I need to ajust some colors on cards, texts, etc
    by the way, if you're seeing this message me on bh and talk about what do you think about this theme ;)
       ~ rowbot

*/

body {
    color:#ddd;
    background-color: #202020
}

nav div.primary {
    background-color: #131313;
    color: #fff !important;
}

nav div.secondary {
    background-color: #181818;
    color: #b4d2e3 !important;
}

nav .info {
    background-color: #202020;
}

.img-white {
    -webkit-filter: brightness(1) invert(0);
    filter: brightness(1) invert(0);
}

.arrow-down {
    filter: invert(1);
}

.card .content {
    background-color: #181818;
}

footer, footer a {
    background-color: #181818;
}

.modal .modal-content {
    background-color: #181818;
}

.modal .close:focus,.modal .close:hover {
    color: white;
}

.modal {
    background-color: rgba(0, 0, 0, .37);
}

.card .item-card-details, .card .item-content {
    background-color: #181818;
    border-top: 1px solid #131313;
}

.card .thumbnail.dark {
    background-color: #0E0E0E;
}

.box.shaded {
    background-color: #0E0E0E;
}

.box {
    border: 1px solid #131313;
}

.profile-card.crate img {
    width: 100%;
    border: 1px solid #131313;
    background-color: #0E0E0E;
    border-radius: 5px;
    padding: 5px;
}

.profile-card:hover {
    background-color: #222
}

.friend-card:hover {
    background-color: #222
}

.hover-card.viewed {
    background-color: #111
}

.hover-card:hover {
    background-color: #222
}

.file-img-box {
    border: 1px solid #131313;
}

.upload-preview-box {
    border: 1px solid #131313;
}

.mem-cols .col {
    border: 1px solid #131313;
}

.membership-info {
    border: 1px solid #131313;
}

.line, hr {
    border-top: 1px solid #131313;
    border-bottom: 1px solid #131313;
}

.divide {
    background-color: #131313;
}

.sellers .seller:not(:last-child) {
    border-bottom: 1px solid #131313;
}

.thread-row {
    border-bottom: 2px solid #131313;
}

.thread {
    border-bottom: 1px solid #131313;
}

.crate-types {
    border-right: 2px solid #131313;
}

input.rigid, textarea.rigid {
    background-color: #121212;
    color: #D4D4D4;
    border: 1px solid #0E0E0E;
}

input, textarea {
    background-color: #121212;
    border: 1px solid #0E0E0E;
}

.input-group {
    border: 1px solid #0E0E0E;
}

.select, select {
    background-color: #121212;
    border: 1px solid #0E0E0E;
    color: #7f817f;
}

.toggle-user-desc:not(.open):after {
    background-color: #181818;
    color: grey;
}

.read-more-desc {
    background-color: #181818;
}

.card .top:not(.absolute) {
    color: #999;
    background-color: #0A0A0A;
    border-color: #999;
}

.card .top:not(.absolute).blue {
    color: #00A3DA;
    background-color: #0A0A0A;
    border-color: #00A3DA;
}

.card .top:not(.absolute).red {
    color: #DE000D;
    background-color: #0A0A0A;
    border-color: #DE000D;
}

.card .top:not(.absolute).orange {
    color: #F49B00;
    background-color: #0A0A0A;
    border-color: #F49B00;
}

.card .top:not(.absolute).green {
    color: #007B28;
    background-color: #0A0A0A;
    border-color: #007B28;
}

.forum-tag {
    background: #0A0A0A;
    color: #fff;
}

.forum-tag:after {
    border: 5px solid transparent;
    border-right-color: #0A0A0A;
}

.button.blue, button.blue {
    color: #00A3DA !important;
    background-color: #1d1d1d00;
    border-color: #00A3DA;
}

.button.red, button.red {
    color: #DE000D !important;
    background-color: #1d1d1d00;
    border-color: #DE000D;
}

.button.orange, button.orange {
    color: #F49B00 !important;
    background-color: #1d1d1d00;
    border-color: #F49B00;
}

.button.green, button.green {
    color: #007B28 !important;
    background-color: #1d1d1d00;
    border-color: #007B28;
}

.button.bits, button.bits {
    color: #f49b00 !important;
    background-color: #1d1d1d00;
    border-color: #f49b00;
}

.crate-types .active, .crate-types :hover {
    background-color: #222;
    border-left: 4px solid #DE000D;
}

.button.bucks, button.bucks {
    color: #009624 !important;
    background-color: #1d1d1d00;
    border-color: #009624;
}

.input-group .input-button {
    border-left: 1px solid #0E0E0E;
    color: #cbcbcb;
    background-color: #f8f8f800;
}

.modal .cancel-button {
    background-color: #1D1D1D;
    color: #fff;
    border: 1px solid #fff;
}

.tabs .tab-body {
    border: 1px solid #dcdcdc00;
    background-color: #181818;
    box-shadow: 0 2px 5px rgba(0,0,0,.2)
}

.tabs .tab {
    color: #999;
    background-color: #131313;
    border: 1px solid #dcdcdc00;
}

.tabs .tab.active {
    background-color: #181818;
    border-bottom: 1px solid transparent;
}

.royal-gradient {
    background-image: -webkit-gradient(linear,right top,left top,from(#F49B00),to(#fefefe00));
    background-image: linear-gradient(270deg,#F49B00,#fefefe00);
}

.ace-gradient {
    background-image: -webkit-gradient(linear,right top,left top,from(#DE000D),to(#fefefe00));
    background-image: linear-gradient(270deg,#DE000D,#fefefe00);
}

.mint-gradient {
    background-image: -webkit-gradient(linear,right top,left top,from(#00A3DA),to(#fefefe00));
    background-image: linear-gradient(270deg,#00A3DA,#fefefe00);
}
}

@-moz-document domain("brick-hill.com") {
a {
    text-decoration: none;
    color: #fff !important; 
}

.ablack-text {
    color: #ddd;
}

.label.dark {
    color: #aeaeae;
}

.agray-text {
    color: #aaa;
}

.darkest-gray-text {
    color: #777;
}

input, textarea {
    color: #ccc;
}

.card .item-creator {
    color: #999;
}

.card .name {
    color:#aeaeae;
}

.board-info a, .stat .title {
    color: #777;
}
}
