css:
@-moz-document domain("shop.lirik.com") {
body{
    background: #000;
}
:root{
    scrollbar-color: #03B1DC #000;
}
/*HEADER*/
#header-nav{
    background: #000;
    border-bottom: 1px solid #03B1DC;
}
.influencer-logo svg path{
    fill: #03B1DC;
}
.nav-col{
    color: #03B1DC;
}
.nav-dropdown{
    background: #000;
    border: 1px solid #03B1DC;
    border-top: 0;
}
.nav-indicator{
    background: #03B1DC;
}
.nav-child{
    right: -40px;
}
.nav-dropdown-text{
    color: #fff;
}
/*HEADER*/
/*FRONT-PAGE*/
.site-hero-text,.grid-title{
    color: #03B1DC;
}
/*FRONT-PAGE*/
/*SUBPAGES*/
.title-bar h1,.listing-col, .listing-title, .listing-price,.tool-bar-top{
    color: #03B1DC;
}
.color-swatch{
    border: 2px solid #03B1DC;
}
.toggle-filters-icon{
    border-color: #03B1DC;
}
.color-swatch:hover{
    border: 4px solid #03B1DC;
}
.listing-trigger{
    color: #03B1DC;
}
.pagination .pages{
    background: #000;
    border: 2px solid #03B1DC;
}
.pagination .page{
    color: #03B1DC !important;
}
.pagination .page:hover, .pagination .page.selected{
    background: #03B1DC;
    color: #000 !important;
}
.pagination .page-pn{
    background: #000;
    border: 2px solid #03B1DC;
}
.m-form-type-grid .m-input, .m-form-type-grid .m-select, .m-form-type-grid .m-textarea, .m-form-type-grid .txt-input, .m-form-type-grid .holder{
    background: #000;
    border: 1px solid #03B1DC;
    box-shadow: none;
    color: #fff;
}
.m-form-type-grid .m-select:focus{
    background: #000;
    box-shadow: none;
}
/*SUBPAGES*/
/*ITEM*/
.detail-view-title,.detail-view-price,.product-options .tooltip-title{
    color: #03B1DC;
}
.details-tab-content p,.detail-view-heading,.product-options .tooltip-content{
    color: #fff;
}
.detail-view-back{
    color: #03B1DC !important;
    opacity: 1 !important;
}
.product-options .options-helper-icon{
    opacity: 1 !important;
}
.product-options .select-options select{
    background: #000 !important;
    color: #03B1DC;
    border-color: #03B1DC !important;
}
.add_to_cart-trigger{
    background: #03B1DC;
}
#product-zoom-open,#product-zoom-close{
    background: #000;
    border: 2px solid #03B1DC;
    color: #03B1DC;
}
.product-options .tooltip{
    background: #000;
    border: 2px solid #03B1DC;
}
/*SIZE-CHART*/
.dbh-tabs .active{
    background: #03B1DC;
}
#cboxClose{
    background-color: #000;
    border: 2px solid #03B1DC;
    color: #03B1DC;
}
.dbh-size-section select{
    background-color: #000;
    border: 1px solid #03B1DC;
    color: #fff;
}
.dbh-size-chart-table thead .td{
    color: #03B1DC;
}
.dbh-size-chart-table .even{
    background: #03B1DC;
    color: #000;
}
.dbh-size-chart-table .odd{
    background: #000;
    color: #03B1DC;
}
.size-chart-description{
    color: #fff;
}
/*SIZE-CHART*/
/*ITEM*/
/*CART*/
#cboxLoadedContent,.cart-overlay{
    background: #000;
}
#cart-overlay h2,#cart-overlay .add-to-cart-stub-total,.cart-overlay .title,#cart-overlay .or,#cart-overlay .keep-shopping{
    color: #03B1DC;
}
#cart-overlay .add-to-cart-stub-message,#cart-overlay .attributes{
    color: #fff;
}
#cart-overlay .or::before, #cart-overlay .or::after{
    background: #03B1DC;
}
.cart-product-bg{
    background-color: #000;
    border: 1px solid #03B1DC;
}
.cart-product .title, .cart-product-bg em,#cart .price{
    color: #03B1DC;
}
.cart-product-bg ul li,#cart .cart-qty-label{
    color: #fff;
}
#cart .remove-product{
    color: #d33;
}
#cart .cart_qty{
    background: #000;
    border-color: #03B1DC;
    color: #fff;
}
input[type="number"] {
  -webkit-appearance: textfield;
     -moz-appearance: textfield;
          appearance: none;
}
input[type=number]::-webkit-inner-spin-button, 
input[type=number]::-webkit-outer-spin-button { 
  -webkit-appearance: none;
}
#cart .continue{
    color: #03B1DC;
}
/*CART*/
/*CHECKOUT*/
#dbh-container-bg{
    background: #000;
}
#checkout .or,#checkout h2{
    color: #03B1DC;
}
.guest-checkout-message{
    color: #fff;
}
#checkout .or::before, #checkout .or::after{
    background: #03B1DC;
}
.dbh-checkout-step-content{
    background: #000;
    border: 1px solid #03B1DC;
}
.active .dbh-checkout-step-heading,.dbh-checkout-step-heading{
    background: #03B1DC;
}
.dbh-checkout-step-heading h2{
    color: #000 !important;
}
#dbh-checkout-form-shipping h3,.m-form-type-grid .m-label{
    color: #03B1DC;
}
.m-form-type-grid .m-input:focus{
    background: #000;
    box-shadow: none;
}
.m-checkmark{
    background: #000;
    border: 1px solid #03B1DC;
    box-shadow: none;
}
.dbh-checkout-note{
    color: #d33;
}
.dbh-checkout-step{
    background: #000;
    border: 1px solid #03B1DC;
}
.stub-label,.stub-value,.toggle-discounts,.discounts{
    color: #03B1DC;
}
.shipment-product-attr{
    color: #fff;
}
.acenter{
    border: 2px solid #03B1DC;
    border-radius: 50%;
}
.m-alert .info{
    background: #5592f7;
}
#checkoutform-billing h3,.dbh-checkout-step-subheading,#send-as-gift .m-label{
    color: #03B1DC;
}
#gift_message{
    color: #fff;
}
#gift_message textarea{
    background: #000;
    border: 1px solid #03B1DC;
    color: #fff;
}
/*GUARANTEE*/
.document h1,.document h2{
    color: #03B1DC;
}
.document p{
    color: #fff;
}
/*GUARANTEE*/
/*CHECKOUT*/
/*LOGIN*/
.user-management .or-txt,.user-management .user-management-alt-txt{
    color: #03B1DC;
}
.user-management .or::before, .user-management .or::after{
    background: #03B1DC;
}
.user-management .user-management-alt{
    border-color: #03B1DC;
}
.m-icons{
    color: #03B1DC;
}
/*LOGIN*/
/*PROFILE*/
.sidebar .inside{
    background: #000;
}
.m-form-type-grid .m-field-submit{
    padding-top: 20px;
}
#gift-card-balance-amount{
    color: #03B1DC;
}
/*PROFILE*/
/*GIFT-CARD*/
#digital-gift-card,.email-preview{
    background: #000;
    border: 1px solid #03B1DC;
}
.document h3{
    color: #03B1DC;
}
.email-preview-label{
    background: #000;
    border: 1px solid #03B1DC;
    border-bottom: 0;
    color: #03B1DC;
}
.email-preview-body{
    color: #fff;
}
/*GIFT-CARD*/
/*CONTACT-US*/
.m-form-type-grid .m-note{
    color: #fff;
}
/*CONTACT-US*/
/*ORDER-STATUS*/
#order-status-form .m-col.col{
    color: #03B1DC;
}
/*ORDER-STATUS*/
/*SUPPORT*/
.page-heading-text,#faq-search h1,#faq-landing .cat-name,.document ul li a,.document ul{
    color: #03B1DC;
}
#faq-search p{
    color: #fff;
}
.sidebar{
    background: #000;
    border: 1px solid #03B1DC;
}
#faq-search .txt-input{
    background: #000;
    border: 1px solid #03B1DC;
    color: #fff;
}
/*SUPPORT*/
/*FOOTER*/
#footer{
    border-color: #03B1DC;
}
#footer-links a{
    color: #03B1DC !important;
}
.footer-made:before{
    content: "Dark theme by Maciejozaman";
    padding-right: 1.6em;
    color: #03B1DC;
}
/*FOOTER*/
}
