css:
@-moz-document url-prefix("https://www.rp.pl/") {
body{
    background: #000;
}
:root{
    scrollbar-color: #fff #000;
}
/*HEADER*/
.main-menu{
    background: #000;
    border-top: 1px solid #787d73;
}
.main-menu__nav .main-menu__logo img{
    filter: invert();
}
.menu-item__news--active a,.menu-item__news a{
    color: #787d73;
}
.menu-item__eco a{
    color: #5d9680;
}
.menu-item__law a{
    color: #f9a334;
}
.menu-item__regions a{
    color: #CA8383;
}
.menu-item__plusminus a{
    color: #bea325;
}
.menu-item_default a{
    color: #787D73;
}
.nav-login__button:hover{
    border: 1px solid #fff;
}
.main-menu__link{
    color: #fff;
}
.main-menu__hover:hover .main-menu__link{
    background: #000;
}
/*SEARCH*/
.search,.search__btn--close,.search__input--transparent option{
    background: #000;
}
.search__btn--close::after{
    color: #fff;
}
.search__input--main{
    background: #000;
    border: 1px solid #fff;
    border-right: 0;
    color: #fff;
}
.search__btn{
    background: #000;
    border: 1px solid #fff;
    border-left: 0;
}
.search__input--transparent{
    color: #fff;
}
.datepicker input{
    background: #000;
    color: #fff;
}
.datepicker_from .react-datepicker__month-container{
    background: #000;
    border: 1px solid #fff;
    border-top: 0;
    width: 243px;
}
.datepicker_to .react-datepicker__month-container{
    background: #000;
    border: 1px solid #fff;
    border-top: 0;
    width: 242px;
}
.react-datepicker__month-select,.react-datepicker__year-select{
    background: #000;
    color: #fff;
}
.search-query__autocomplete{
    margin: 0;
}
.search-query__autocomplete-item{
    background: #000;
    color: #fff;
    border-left: 1px solid #fff;
    border-right: 1px solid #fff;
}
.search-query__autocomplete-item:hover{
    color: #fff;
    text-decoration: underline;
}
.search-query__autocomplete-item:first-child{
    border-top: 1px solid #fff;
}
.search-query__autocomplete-item:last-child{
    border-bottom: 1px solid #fff;
}
.search__no-data{
    color: #fff;
}
.teaser-group-name{
    color: #fff;
}
.teaser--clean .teaser__title{
    color: #fff;
}
.teaser--clean .teaser__title:hover{
    color: #fff;
    text-decoration: underline;
}
/*SEARCH*/
/*HEADER*/
/*MENU*/
.menu-expand,.menu-expand .container{
    background: #000;
}
.menu-tab__title a,.menu-tab__list a,.btn--transparent,.menu-tab__btn::after{
    color: #fff;
}
.menu-expand-btn.active{
    background: #000 !important;
}
.menuRP.active::after{
    color: #fff;
}
/*MENU*/
/*STRONA-GŁÓWNA*/
.screening-container,.container,.page-header,.carousel-parent .container,.mk-ewydanie{
    background: #000;
}
.carousel-parent{
    background: #000 !important;
}
.page-header__date span{
    background: #000;
    color: #fff;
}
.page-header .img-responsive{
    filter: invert(100%);
}
.main-topic__title,.section__subtitle,.teaser--vertical .teaser__title,.teaser--opinion-vertical .teaser__title,.teaser--opinion-vertical .teaser__footer, .teaser--opinion-vertical .teaser__footer a,.main-topic__footer a,.list-with-caret li,.list-with-caret li a,.teaser__title,.list-title,.teaser--no-photo-medium .teaser__title,.section__popular-subjects a,.publishing-offer__title,.mk-ewydanie__title{
    color: #fff;
}
.teaser--no-photo-medium .teaser__title:hover,.list-with-caret li a:hover,.teaser__title:hover,.main-topic__title:hover,.publishing-offer__title:hover{
    text-decoration: underline;
}
/*STRONA-GŁÓWNA*/
/*PODSTRONY*/
.page-header__title h2{
    background: #000;
}
/*PODSTRONY*/
/*ARTYKUŁ*/
.section__breadcrumbs a,.article__date,.article__title,.article__image-author,.article__author a,.article__content, .article__lead{
    color: #fff;
}
.article__icon{
    background-color: #fff;
}
.btn--darkgrey{
    background: #000;
    color: #fff !important;
}
/*ARTYKUŁ*/
/*LOGIN*/
.modal__content,.modal__footer{
    background: #1c1c1c;
}
.modal .close{
    color: #fff;
}
.close{
    opacity: 1;
}
.modal__body p{
    color: #fff;
}
.btn--danger,.btn--light{
    outline: 0
}
/*LOGIN*/
/*FOOTER*/
.page-footer__logo{
    filter: invert(100%);
}
.page-footer__company{
    color: #fff;
}
.page-footer__company img{
    filter: invert(100%);
}
.page-footer__list li a{
    color: #fff;
}
.page-footer__list li a:hover{
    color: #fff;
    text-decoration: underline;
}
.page-footer__bottom .container:before{
    content: "Ciemny motyw by Maciejozaman";
}
/*FOOTER*/
}

@-moz-document domain("energia.rp.pl") {
body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scrollbar-color: #5A914D #1c1c1c;
}
/*STRONA-GLOWNA*/
/*HEADER*/
.df-content-full,.df-header-1 .df-logo-wrap{
    background: #1c1c1c;
}
/*HEADER*/
/*MENU*/
.wrapped-menu{
    background: #1c1c1c !important;
    border-bottom: 1px solid #fff;
}
.wrapped-menu ul li{
    background: #3b3b3b;
}.wrapped-menu ul li:first-child{
    border-left: 1px solid #fff;
}
.wrapped-menu ul li a{
    color: #fff !important;
}
#megadropdown .nav > .df-is-megamenu .dropdown-menu .list_megamenu{
    background: #3b3b3b;
}
#megadropdown-sticky .dropdown:last-child{
    border-right: 1px solid #fff;
}
#wraper-mainmenu{
    background: #3b3b3b;
}
.ion-ios-search-strong{
    color: #fff;
}
#megadropdown-sticky .nav > .df-is-megamenu .dropdown-menu .list_megamenu{
    background: #3b3b3b;
}
/*MENU*/
/*SEARCH*/
#search{
    background: #1c1c1c;
}
#search input[type="search"]{
    background: #1c1c1c;
    color: #5A914D;
}
.modal-search-caption,#search .close{
    color: #fff;
}
.search-result-wraper-main p{
    color: #fff;
}
/*SEARCH*/
.df-category-header-title h1{
    color: #fff !important;
}
#df-content-top-post .df-category-top-post{
    background: #1c1c1c;
}
#df-content-top-post .top-post-wrap .top-post-nav-btn{
    background: #00000096;
}
.thumb-desc h5{
    color: #fff !important;
}
#df-content-wrapper a{
    color: #fff !important;
}
#df-content-wrapper.df-content-full{
    background: #1c1c1c;
}
.df-wrapper-inner,.container.df-bg-content{
    background: #1c1c1c;
}
.df-shortcode-blocks-top h5 a{
    color: #fff !important;
}
.article-title a,.article-content{
    color: #fff !important;
}
.df-shortcode-blocks-title{
    color: #fff !important;
}
.page-numbers li p{
    color: #fff !important;
}
.wpb_wrapper h5{
    color: #fff !important;
}
/*FOOTER*/
.df-container-footer, .df-container-footer:nth-of-type(1){
    background: #1c1c1c;
}
.df-footer-top-inner h5,.df-footer-top-inner p{
    color: #fff !important;
}
.df-copyright:after{
    content: "Ciemny motyw by Maciejozaman";
}
/*FOOTER*/
/*STRONA-GLOWNA*/
/*ARTICLE*/
.container-fluid{
    background: #3b3b3b;
}
#df-sticky-nav .nav li a{
    color: #fff;
}
.td-post-tittle h1{
    color: #fff !important;
}
.df-post-sharing li{
    background: transparent;
}
.df-post-sharing.style1 a{
    color: #fff !important;
}
.more-social{
    background: #1c1c1c;
}
.entry-content h2,.entry-content p{
    color: #fff !important;
}
.wp-caption-text{
    color: #fff !important;
}
.tags{
    color: #fff !important;
}
section.widget{
    background: #1c1c1c;
}
.post-meta-desc{
    color: #fff;
}
.df-related-post h4{
    color: #fff !important;
}
/*ARTICLE*/
}

@-moz-document domain("cyfrowa.rp.pl") {
body{
    background: #1c1c1c;
}
:root{
    scrollbar-color: #008178 #1c1c1c;
}
/*HEADER*/
.df-header-1 .df-logo-wrap{
    background: #1c1c1c;
}
.wrapped-menu{
    background: #1c1c1c ;
}
#megadropdown .nav li a, #megadropdown-sticky .nav li a{
    color: #008178 !important;
}
#megadropdown .nav li a:hover, #megadropdown-sticky .nav li a:hover{
    color: #fff !important;
}
/*SZUKAJ*/
#search{
    background-color: #1c1c1c;
}
.modal-search-caption,#df-search-result{
    color: #fff;
}
#search input[type="search"]{
    color: #008178;
    line-height: 140px;
}
#search .close, .df-modal-dialog .close{
    color: #008178;
}
#df-search-result h4 > a{
    color: #008178;
}
/*SZUKAJ*/
/*HEADER*/
/*STRONA-GŁÓWNA*/
#df-content-wrapper.df-content-full,.df-wrapper-inner,.container.df-bg-content{
    background: #1c1c1c;
}
.df-wraper #page h4 > a,.df-wraper #page h5 > a{
    color: #008178;
}
.post-meta a,.post-meta .post-meta-desc a,.article-content,.post-meta .post-meta-desc{
    color: #fff !important;
}
.df-shortcode-blocks .df-shortcode-blocks-top{
    border-color: #008178;
}
.df-shortcode-blocks-title{
    color: #008178 !important;
}
.rp-rss-head-hr{
    border-color: #008178;
    background: none !important;
}
.vc_custom_1537873009577{
    background: #1c1c1c !important;
    border-top: 1px solid #008178;
    border-bottom: 1px solid #008178;
}
.wpb_wrapper hr{
    border: #008178;
    background: #008178 !important;
}
