/*PODSTRONY*/

.df-pagination-list .active p{
    color: #008178 !important;
    border-color: #008178 !important;
}
.df-pagination-list li a p,.df-pagination-list .page-numbers.dots{
    color: #fff !important;
}
.df-pagination-list li:hover a p{
    color: #008178 !important;
    background: none;
    border: 1px solid #008178 !important;
}
