/*ARTYKUŁ*/
.container-fluid{
    background: #1c1c1c;
}
#df-sticky-nav .nav li a,#df-content-wrapper .post-meta li span{
    color: #fff;
}
#df-content-wrapper header h1, #df-content-wrapper .entry-title, .df-wraper #page h1,.df-wraper #page h4,.df-wraper #df-content-wrapper .sidebar h5.article-title a{
    color: #008178;
}
.entry-content h2,.entry-content p{
    color: #fff !important;
}
.archive-header-title{
    border-color: #008178;
}
/*UDOSTEPNIANIE*/
.df-post-sharing.style1 li{
    background: #1c1c1c;
}
.df-post-sharing.style1 li:hover{
    border-color: #008178;
}
.df-post-sharing.style1 a{
    color: #008178 !important;
}
/*UDOSTEPNIANIE*/
/*TAGI*/
.tags{
    color: #008178 !important;
}
/*TAGI*/
/*ARTYKUŁ*/
/*FOOTER*/
.df-container-footer{
    background: #1c1c1c !important;
    border-top: 1px solid #008178 !important;
}
.df-footer-top .row #media_image-2 img{
    filter: invert(100%);
}
#page #df-footer-wrapper h5.df-widget-title,#df-footer-wrapper .df-container-footer a, #df-footer-wrapper .df-container-footer a{
    color: #008178;
}
.widget_text .textwidget p,.widget_text .textwidget p a,#df-footer-wrapper .df-container-footer a:hover, #df-footer-wrapper .df-container-footer a:hover{
    color: #fff !important;
}
.df-copyright div:before{
    content: "Ciemny motyw by Maciejozaman ";
    color: #008178;
}
