css:
@-moz-document url-prefix("https://open.spotify.com/") {
.btn.btn-green {
    background-color: /*[[red]]*/;
    padding: 11px 44px;
}
button.btn.btn-green ::after {
    background-color: /*[[red]]*/;
    padding: 11px 44px;
}
._137ec408b14ac4ea5aec6ded3d95f328-scss.be059487cfa7b71ee0f482c8e5be2b7e-scss:before {
    content: "";
    height: 2px;
    background-color: /*[[red]]*/;
    width: 30px;
    position: absolute;
    display: block;
    left: 50%;
    bottom: -4px;
    margin-left: -15px;
}
.btn.btn-green:focus, .btn.btn-green:hover {
    background-color: #fff;
    color: red
}
.btn.btn-black {
    padding: 11px 44px;
    background: rgba(24,24,24,.7);
    color: #fff;
    -webkit-box-shadow: inset 0 0 0 2px #b3b3b3;
    box-shadow: inset 0 0 0 2px /*[[red]]*/;
} 
._137ec408b14ac4ea5aec6ded3d95f328-scss.be059487cfa7b71ee0f482c8e5be2b7e-scss:hover:before {
    background-color: #ffffff;
}
.btn.btn-black:focus {
    color: #f00;
}
.btn.btn-black:focus, .btn.btn-black:hover {
    color: /*[[red]]*/;
}
.control-button--active-dot:after {
    background-color: /*[[red]]*/;
}
.control-button--active {
    color: /*[[red]]*/;
}.control-button--active:not(.control-button--disabled):focus, .control-button--active:not(.control-button--disabled):hover {
    color: #f00;
}
.progress-bar--is-active .progress-bar__fg {
    background-color: #f00;
}
.connect-device-list-item--active .connect-device-list-item__icon {
    color: /*[[red]]*/;
}
.connect-device-list-item--active .connect-device-list-item__device-subtitle {
    color: /*[[red]]*/;
}
.connect-device-list-item--active {
    color: /*[[red]]*/;
}
.UserWidget__link--is-active {
    color: /*[[red]]*/;
}

element.style {
    --size: 40px;
}
._5658c6accf868644593ce36887a475b1-scss a, ._5658c6accf868644593ce36887a475b1-scss button {
    position: relative;
    z-index: 1;
}
._11f5fc88e3dec7bfec55f7f49d581d78-scss {
    font-size: 12px;
    line-height: 18px;
    font-weight: 700;
    letter-spacing: 1.76px;
    text-transform: uppercase;
    outline: none;
    --size: 40px;
    border: 0;
    border-radius: 500px;
    color: #fff;
    background-color: /*[[red]]*/;
    -webkit-transition: -webkit-transform 33ms cubic-bezier(.3,0,0,1);
    transition: -webkit-transform 33ms cubic-bezier(.3,0,0,1);
    transition: transform 33ms cubic-bezier(.3,0,0,1);
    transition: transform 33ms cubic-bezier(.3,0,0,1),-webkit-transform 33ms cubic-bezier(.3,0,0,1);
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-pack: center;
    -ms-flex-pack: center;
    justify-content: center;
    -webkit-box-align: center;
    -ms-flex-align: center;
    align-items: center;
    height: var(--size);
    width: var(--size);
}
.navBar-link--active:before {
    content: "";
    display: block;
    position: absolute;
    top: 5px;
    bottom: 5px;
    width: 4px;
    background-color: hsl(0, 0%, 100%);
}
.now-playing-bar {
    -webkit-box-align: center;
    -ms-flex-align: center;
    align-items: center;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-orient: horizontal;
    -webkit-box-direction: normal;
    -ms-flex-direction: row;
    flex-direction: row;
    -webkit-box-pack: justify;
    -ms-flex-pack: justify;
    justify-content: space-between;
    height: 70px;
    padding: 0 16px;
}
.ConnectBar {
    font-size: 12px;
    line-height: 16px;
    letter-spacing: .015em;
    background-color: /*[[red]]*/;
    color: #fff;
    height: 24px;
    line-height: 24px;
    padding: 0 16px;
    position: relative;
}
.Root__main-view {
    grid-area: main-view;
    width: 100%;
    background-image: url(/*[[font]]*/);
    background-attachment: fixed;
    background-position: center;
    background-size: cover;
}
.now-playing-bar-container {
    background-color: #000000;
    border-top: 1px solid /*[[red]]*/;
}
  .tracklist-row--active .position, .tracklist-row--active .tracklist-duration, .tracklist-row--active .tracklist-name, .tracklist-row--active .tracklist-play-pause {
    color:  /*[[red]]*/!important;
}
  .ConnectBar:after {
    content: "";
    width: 0;
    height: 0;
    border-left: 10px solid transparent;
    border-right: 10px solid transparent;
    border-bottom: 10px solid /*[[red]]*/;
    position: absolute;
    top: -10px;
}
::-webkit-scrollbar-thumb {
    background-color: /*[[bar]]*/;
}
:not(.no-focus-outline) .progress-bar:focus-within .progress-bar__fg {
    background-color: /*[[red]]*/;
}
}
