css:
@-moz-document domain(
pokemonshowdown.com) {
html,
body {
    background-color: #222 !important;
}

.header {
    background: none !important;
    background-color: #222 !important;
    border: none !important;
}
.ps-popup {
	background: #1a1a1a !important;
}
.roomtab {
    background-color: #3d3d3d !important;
    border: 1px solid #333 !important;
    border-bottom: 0px !important;
    box-shadow: none !important;
    padding-bottom: 10px !important;
}
.notifying {
    background-color: #3a0000 !important;
}
.cur {
 	background-color: #444 !important;   
}
.maintabbarbottom {
    background-color: #444 !important;
    border: none !important;
    box-shadow: 0px 1px 2px !important;
}
.ps-room {
    background-color: #1a1a1a !important;
    border: 1px solid #222 !important;
}
.ps-room-light {
    background: rgba(32, 32, 32, .85) !important;
}
.ilink {
    color: lightgray !important;
    border: 1px solid #4f4f4f !important;
    background-color: #444 !important;
}
.pm-window > h3 {
    background: rgba(32, 32, 32, 1) !important;
    border: 1px solid #222 !important;
}
.pm-log {
    background-color: #333 !important;
    border: 1px solid #222 !important;
    border-bottom-left-radius: 6px !important;
    border-bottom-right-radius: 6px !important;
}
.newsentry {
    border-bottom: 1px solid #222 !important;
    color: #c1c1c1 !important;
}
.unread {
 	background: none !important;   
}
.select {
    background: rgba(112, 112, 112, 1) !important;
    color: #e1e1e1 !important;
}

a {
    text-decoration: none !important;
    color: #d1d1d1 !important;
}
h3 {
    color: #e1e1e1 !important;
}
.mainmenuwrapper {
    background-color: #1d1d1d !important;
}
.icon {
    border: none !important;
}

.pad > .button,
td > .button,
.pad > p > .button,
.controls > p > .button,
.controls > p > span > .button {
    border: none !important;
}
.button.mainmenu1,
.button.mainmenu2,
.button.mainmenu3,
.button.mainmenu4,
.button.mainmenu5 {
    background: rgb(64, 64, 64, 1) !important;
    border-width: 1px !important;
}

.select > strong {
 	color: white !important;   
}

.battle, .innerbattle {
 	border: none !important;   
}

.leftbar, .rightbar {
 	background: rgb(32, 32, 32, 0.9) !important; 
    border: none !important;
}

.battle-controls, .controls {
 	background-color: #1a1a1a !important;   
}

.battle-log {
 	background-color: #222 !important; 
    border: none !important;
}

.userlist {
    background-color: #2d2d2d !important; 
	border: none !important;    
}

.battle-history {
 	border: none !important;
    padding: 2px !important;
}

.inner > h2 {
 	background-color: #2a2a2a !important; 
    padding-left: 10px !important;
}

.inner > .mine {
 	background-color: #222 !important;   
}

.battle-log-add {
    background-color: #2d2d2d !important; 
 	border: none !important;
}

.text-area {
 	border-color: #555 !important;   
}

.span {
 	text-shadow: none !important;   
}

.tabbar.maintabbar {
    margin-bottom: -1px !important;
}
}
