css:
@-moz-document regexp("http://www.chia-anime.me/"), regexp("http://www.chia-anime.me/latest-episodes.*"), regexp("http://www.chia-anime.me/latest-anime/"), regexp("http://www.chia-anime.me/most-recent/"), regexp("http://www.chia-anime.me/index/") {
h2.h3seo {
    max-width: 80vw !important;
    min-width: 40vw !important;
}

article > .info {
    padding-top: 20px;
}
.info > .alignleft {
    width: 100% !important;
}
.content {
    width: 100vw !important;
}

table {
    border-collapse: collapse !important;
    border-spacing: 0px !important;
}

#content {
    width: 98vw;
    display: flex;
    flex-wrap: wrap;
    align-items: center;
    justify-content: center;
}
#archive {
    min-width: 100vw !important;
}

#content article .h1-seo-article {
    width: 100%;
}

.h1-seo-article {
    text-align: center;
    font-size: 3em !important;
}
#content article {
    display: flex;
    flex-wrap: wrap;

    justify-content: center;
}

#content article:nth-child(4) {
    max-width: 80%;
}

#content article:nth-child(5) {
    max-width: 90vw;
    background-color: #202020;
}

main {
    min-width: 100%;
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
}
/* genre */
#main,
.content_genre {
    min-width: 30vw;
    box-sizing: border-box;
    display: flex;
    flex-direction: column;
}
#main {
    width: 100vw;
    align-items: center;
    background-color: #262626;
    margin-bottom: 5px;
}

.content_genre {
    width: 60vw;
    background-color: #262626;
}
#alpha_genre {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
}

ul#alpha_genre li a {
    font-size: 1.5em;
    line-height: 1.5em;
    text-align: center;
    box-sizing: border-box;
    width: 115px;
    padding: 3px 5px 3px 5px;
    transition: color 0.5s ease, background-color 0.3s ease;
}

ul#alpha_genre li a:hover {
    color: grey;
    background-color: #3A3A3A;
}

.Nav {
    width: 100% !important;
    display: flex !important;
    justify-content: flex-end;
}

.Nav > a {
    height: 40px;
    display: flex;
    justify-content: center;
    align-items: center;
    text-align: center;
    border-radius: 5px;
    width: 24%;
    transition: all 0.3s ease;
}
.Nav > a::after {
    content: "";
    font-size: 2.5em;
    transition: content 0.7s 0.9s linear;
    margin-left: 0px;
}
.Nav > a:hover::after {
    display: block;
    content: ">";
    margin-left: auto;
    transition: content 0.7s linear;
}
.Nav > a:hover {
    width: 26%;
    border-radius: 8px;
    justify-content: flex-start;
    padding-left: 10px;
}
.Nav > a > .h2seo,
.Nav:last-child {
    font-size: 1.4em !important;
}

@media (max-width: 750px) {
    .Nav > a > .h2seo,
    .Nav:last-child {
        font-size: 1em !important;
    }
}
.Nav:last-child {
    justify-content: center !important;
}
.Nav:last-child > a:hover::after {
    content: ""
}

.Nav:last-child > a:hover {
    justify-content: center;
    width: 24%;
}

/*  new anime series items*/
ul.imageList .picutre {
    width: 400px !important;

    border: 3px solid pink;
}

.picture img {
    min-width: 95%;
    min-height: 190px;
    transition: all 0.3s ease;
    position: relative;
}
.picture img::before {
    content: "zjebało się and couldn't load the image ";
    font-size: 1.4em;
    color: white;
    width: 100% !important;
    height: 190px !important;
    background: #666;
    position: absolute;
    top: 0px;
    left: 0px !important;
}
.picture:hover img {
    width: 100%;
    height: 200px;
    transition: all 0.3s ease;
}
.picture {
    margin-bottom: -1px;
    height: 80%;
}

.picture:hover + .title {
    width: 100%;
    margin-left: 0px;
    transition: width 0.3s ease, margin-left 0.3s ease;
}

.gallery {
    display: flex;
    flex-direction: column;
    justify-content: center;
    width: 140px;
}

.title {
    margin-left: 3px;
    padding: 5px 0px 5px 0px;
    width: 96%;
    border: none;
    background-color: #101010;
    text-align: center;
    overflow: visible;
    height: inherit;
    border-bottom-left-radius: 5px;
    border-bottom-right-radius: 5px;
    transition: width 0.3s ease, margin-left 0.3s ease;
    display: flex;
    justify-content: center;
}

.title > a {
    color: white;
    width: 100%;
}
.title > a:hover {
    background-color: inherit;
}

/*
element:
    tiele
    episode number
    image
    info
*/
/* latest anime episodes thumbnails */
.box2 {
    width: 200px !important;
    height: 185px !important;
    transition: background-color 0.3s linear;
    background-color: #1A1A1A;
    margin: 5px 5px 0px 0px !important;
    border: 2px solid #111111;
    padding-bottom: 8px !important;
}

.post {
    border-radius: 3px;
}
.post h3 a {
    display: block;
    position: relative;
    width: 100% !important;
    text-align: left;
    margin-left: 2px;
}

.post h3 a:hover {
    color: #BABABA !important;
}

.post img {
    width: 100% !important;
    height: auto !important;
    margin-top: 5px;
    transition: filter 0.3s ease;
}
.post img:hover {
    filter: brightness(80%);
    transition: filter 0.3s ease;
}
.post center + div + div {
    margin: 0px !important;
    width: inherit !important;
    height: auto !important;
    background: none !important;
}
.post {
    display: flex;
    flex-direction: column;
}
.video-episode {
    font-size: 1.2em !important;
    position: static;
    right: auto !important;
    bottom: auto !important;
    top: auto !important;
}
.video-subbed,
.time {
    background: none !important;
    top: auto !important;
    font-size: 1em !important;
}

.time {
    right: -60px !important;
}

main > article + div {
    width: 960px !important;
}

.content_bloc2 {
    height: 55px !important;
    width: 95% !important;
}
#alpha_listing {
    height: 60px;
}
#alpha_listing li:nth-child(even) > a {
    font-size: 2em;
    border-radius: 3px;
    padding: 7px;
    color: white;
    background-color: #303030;
    transition: background-color 0.3s ease, color 0.3s ease;
}
#alpha_listing li:nth-child(even) > a:hover {
    background-color: #292929;
    color: #1A1A1A;
}

.footer_box {
    width: 100vw !important;
}

.footer_box tr {
    display: flex !important;
    justify-content: space-evenly;
    flex-wrap: wrap;
    min-width: 20%;
    padding-top: 20px;
}
.footer_box + #longblack {
    width: 0px;
    height: 0px;
}

.footer-column {
    width: inherit !important;
    display: flex;
    flex-direction: column;
    align-items: baseline;
}

.footer-column h6 {
    font-size: 1em;
    margin-block-end: 0px;
    margin-block-start: 0px;
}

.footer-column ul {
    display: flex;
    flex-direction: column;
    align-items: baseline;
}

.footer-column li > a:hover {
    background-color: inherit;
}
center + center {
    background: #202020;
}

/*buttons on site*/
@media (max-width: 1100px) {
    #content article:nth-child(4) {
        max-width: 98vw;
    }

    .content_genre {
        width: 90vw;
    }
}
}

@-moz-document regexp("http://www.chia-anime.me/"), regexp("http://www.chia-anime.me/episode/.*"), regexp("http://www.chia-anime.me/latest-episodes.*"), regexp("http://www.chia-anime.me/latest-anime/"), regexp("http://www.chia-anime.me/most-popular-anime-series/"), regexp("http://www.chia-anime.me/most-recent/"), regexp("http://www.chia-anime.me/index/") {
a:hover {
    background: none;
}
/* nav bar  */
#top + table {
    width: 98vw !important;
}
/*  the element abouve logo /menu /search*/
#top + table > tbody > tr > td {
    margin-right: 0px;
    margin-left: 0px;
}
/* logo /menu /search */
#top + table > tbody > tr > td > table {
    border-collapse: collapse !important;
    border-spacing: 0px !important;
    width: 98% !important;
}

#top + table > tbody > tr > td > table > tbody > tr {
    display: flex;
    justify-content: space-around;
}

.menu {
    display: flex;
    justify-content: center;
    border-right: 1px solid #1a1a1a;
    width: 100%;
    background: #202020;
}
.menu:hover,
.menu2 {
    background: #1f1f1f;
}

.menu-search input[type=text] {
    border-radius: 4px !important;
    height: 33px !important;
    margin: 3px 0px 0px 0px !important;
}
.menu-search {
    width: 200px !important;
}

td.menu:nth-child(6) {
    border-right: none;
}

.menu a {
    width: 100%;
}

/* other menu */
.menu2 {
    display: flex;
    justify-content: center;
    border-right: 1px solid #1a1a1a;
    width: 100%;
    align-items: center;
}

/* display None part */
#VelBar,
#cute,
.more_entries,
#outer,
body > iframe,
#features2,
#archive > table,
.box1-top,
ul#hot-shows,
#top + table + table,
ul#alpha_genre > li img,
.Nav:last-child > a:hover::after,
.footer_box + #longblack,
center + center + center,
td.menu:nth-child(7),
iframe,
#fb-root,
#features1 {
    display: none !important;
}

/* bg none */
.box2-bot,
.box2-top,
.post,
#longblack,
.menu a:hover,
td.menu span,
.post a:hover,
#features1 {
    background: none !important;
}

html {
    padding-bottom: 0px !important;
    overflow-x: hidden;
}

@media (max-width: 750px) {

    #top + table > tbody > tr > td > table > tbody > tr {
        display: flex;
        flex-direction:column;
        justify-content: space-around;
        align-items:center;
    }
    .menu > span, .menu > span >a{
        display:block;
        width:100% !important;
        height:100% !important;
    }
    
    .menu >span>a, .menu a:hover{
        padding-top:0px;
        display:flex;
        align-items:center;
        justify-content:center;
    }

    .menu-search input[type=text] {
        border-radius: 4px !important;
        height: 33px !important;
        margin: 3px 0px 0px 0px !important;
        width: 100% !important;
    }
    .menu-search-result,.menu-search-result >div, .menu-search{
        width:80vw !important;
    }


}
}

@-moz-document regexp("http://www.chia-anime.me/episode/.*") {
/* page of certain anime */
iframe,
#fb-root,
.video-subbed {
    display: none !important;
}

#content {
    width: 98vw;
    display: flex;
    flex-wrap: wrap;
}

.col-right {
    /*   flex basis   */
    width: 68vw !important;
    display: flex;
    flex-direction: column;
    align-items: center;
}

