    height: 100px !important;
}

.col-left {
    width: 30vw !important;
}

#main.single2 {
    width: 100%;
}
.post {
    display: flex;
    flex-direction: column;
    align-items: center;
}
.post > h1 {
    text-align: center;
}
.post.wrap img {
    width: 100% !important;
    height: auto;
}

.post.wrap div > p {
    padding-left: 0px !important;
}

center > table,
#features1 {
    background: none !important;
    display: none!important;
}

div > center {
    height: 30px;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    max-width: 100vw;
    padding-top: 30px;
    background: #202020;
}

.thov {
    width: 100% !important;
    height: auto !important;
    transition: filter 0.3s ease;
}
.thov:hover {
    filter: brightness(80%);
    transition: filter 0.3s ease;
}
#archive .box2.last {
    margin-right: 14px !important;
}
}

@-moz-document regexp("http://www.chia-anime.me/.*-english-.*/") {
a:hover {
    background: none;
}
#content {
    width: 98vw;
    display: flex;
}

#hot-shows,
#main.single1 > div:nth-child(1),
.box3-top,
.box3-bot,
#salapi2,
#salapi3,
#salapi4,
#M147159PreloadC138618,
#longblack,
.col-right #twoblack,
.entry div:nth-child(9),
.entry div:nth-child(10),
