    width: 80vw !important;
}

#video-content {
    width: 100%;
}
.single1,
#video-content {
    display: flex;
    flex-direction: column;
}
#more-nav,
#more-nav + div,
#Anime-Mirrors {
    align-self: center;
}

.cvideo iframe {
    width: 100%;
    min-height: 580px;
    height: 1%;
    overflow: hidden;
}

.col-right h4,
.viewport,
#scrollbar1,
.entry,
#ads10 {
    background-color: #111 !important;
}

#episodes {
    margin-top: 20px;
    border: 1px solid black;
}
.col-right h4 {
    width: 100%;
    font-size: 1.5em;
    text-align: center;
}
.viewport {
    height: 136.9vh !important;
}
#scrollbar1 {
    margin: 0px;
}

.episode {
    margin-top: 0px;
}

.wrap + center {
    padding-top: 20px;
    height: 60px;
    background: #1a1a1a;
    display: flex;
    justify-content: center;
}

.anime-alternative ul li {
    border-left: 1px solid black;
    border-right: 1px solid black;
}
#main blockquote {
    background: none;
    width: 90%;
}
#schema h1 {
    font-family: 'Arial';
    font-size: 1.4em !important;
}

