    border-style: none;
    margin-top: -6px;
    margin-left: 2px;
    padding: 3px 3px 15px 0px;
}
div[class^='title'] > a {
    color: white;
}

div[class^='picture'] img {
    border: none;
    margin-left: 2px;
}

div[class^='picture'] a {
    background: none;
}
div[class^='gallery']:hover div[class^='title'] > a {
    color: grey;
    transition: color 0.3s ease;
}

div[class^='gallery']:hover div[class^='picture'] img {
    filter: brightness(80%);
    transition: filter 0.3s ease;
}
.episode-meta2,
.episode-meta {
    text-align: right;
    padding-left: 0px;
}
.episode-details2 > strong,
.episode-meta2,
.episode-meta,
.episode-details > h5 {
    font-family: 'Arial';
    font-size: 1.4em;
}

.episode-meta {
    margin-left: -20px;
}

.episode-details > h5 > a:hover {
    background: none;
    transition: color 0.3s ease;
}
.glow {
    text-shadow: none !important;
}
/* navbar */
