    width: 100% !important;
}

#top + table > tbody > tr > td > table > tbody > tr,
#top + table > tbody > tr > td > table > tbody > tr > td > table {
    display: flex;
    justify-content: space-around;
}

#top + table > tbody > tr > td > table {
    width: 100% !important;
}
#top + table > tbody > tr > td > table > tbody > tr > td > table {
    width: 98% !important;
}

.menu {
    border-right: 1px solid #1a1a1a;
    width: 100%;
    background: #202020;
    min-width: 13vw;
