.menu:hover {
    background: #1f1f1f;
}
.menu a {
    width: 100%;
}
.menu a:hover {
    background: none!important;
}

.menu-search input[type=text] {
    border-radius: 4px !important;
    height: 33px !important;
    margin: 3px 0px 0px 0px !important;
}
