form {
    display: flex;
    flex-direction: column;
}

#content {
    justify-content: space-between;
}

h2 {
    text-align: center;
    width: 100%;
}

#archive {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    min-width: 98vw !important;
}

