css:
@-moz-document domain(vostfree.com) {
body {
    background: #2e3543;
    font-size: 13px;
    color: #ffffff;
    overflow-x: visible;
}
  
#player-tabs {
    background-color: #2e3543;
    width: 1013px;
    float: left;
    margin: -10px 0 0;
    padding: 0 0 10px;
    border-left: 1px solid #c2c2c2;
    border-right: 1px solid #c2c2c2;
}
  
#player-tabs .tab-title {
    border-bottom: 1px solid #2e3543;
    border-top: 1px solid #2e3543;
    float: left;
    margin: 10px 0;
    width: 1013px;
}
  
#player-tabs .tab-title li {
    float: left;
    border-right: 1px solid #2e3543;
    color: #fff;
    font-weight: 700;
    line-height: 35px;
    padding: 0 11px;
    position: relative;
}
  
#player-tabs .tab-title li.current {
    border-bottom: 2.5px solid #2e3543;
    border-top: 2.5px solid #2e3543;
    margin: -2px 0;
}
  
#player-tabs .tab-title li {
    float: left;
    border-right: 1px solid #2e3543;
    color: #fff;
    font-weight: 700;
    line-height: 35px;
    padding: 0 11px;
    position: relative;
}
  
#player-tabs {
    background-color: #2e3543;
    width: 1013px;
    float: left;
    margin: -10px 0 0;
    padding: 0 0 10px;
    border-left: 1px solid #2e3543;
    border-right: 1px solid #2e3543;
}
  
.message .buttons li a {
    background-color: #ff8055;
    float: left;
    height: 20px;
    line-height: 20px;
    color: #ffffff;
    font-size: 11px;
    font-weight: 700;
    padding: 0 20px;
    text-transform: uppercase;
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px;
    border-radius: 3px;
}
  
.buttons .ratingtypeplusminus, .buttons .ratingtypeplus {
    color: #fff;
}
  
.comments-block .block-content {
    margin-bottom: 10px;
    background-color: #1d232f;
    width: 659px;
    float: left;
    padding: 0 10px;
    border: 1px solid #c2c2c2;
    border-top: 0;
    -moz-border-radius: 0 0 3px 3px;
    -webkit-border-radius: 0 0 3px 3px;
    border-radius: 0 0 3px 3px;
}
  
.message .avatar img {
    border: 3px solid #ff935e;
    vertical-align: middle;
    float: left;
    width: 80px;
    height: 80px;
    -moz-border-radius: 50%;
    -webkit-border-radius: 50%;
    border-radius: 50%;
}
  
.message .mleft .triangle {
    width: 0;
    height: 0;
    border-top: 8px solid transparent;
    border-bottom: 8px solid transparent;
    border-left: 8px solid #ff935e;
    margin: 35px 0 0;
    position: absolute;
    left: 85px;
}
  
.message .mright {
    width: 542px;
    float: left;
    position: relative;
    border-left: 1px solid #1d232f;
    margin: 0 0 0 -1px;
    padding: 10px 0 10px 20px;
}
  
.comments-block .block-content {
    margin-bottom: 10px;
    background-color: #1d232f;
    width: 659px;
    float: left;
    padding: 0 10px;
    border: 1px solid #1d232f;
    border-top: 0;
    -moz-border-radius: 0 0 3px 3px;
    -webkit-border-radius: 0 0 3px 3px;
    border-radius: 0 0 3px 3px;
}
  
.watch-top .image-bg-content {
    background: #1d232f;
    width: 100%;
    height: 100%;
    display: table;
}
  
#header .search-box {
    background-color: #fff;
    float: left;
    width: 150px;
    height: 28px;
    color: #6a7180;
    padding: 0 30px 0 5px;
    border: 1px solid #d1d1d1;
    border-radius: 25px;
}
  
#header .header-search {
    background-color: #3e863c;
    height: 30px;
    padding: 5px;
    float: right;
    margin: 10px 0 0;
    -moz-border-radius: 0 3px 3px 0;
    -webkit-border-radius: 0 3px 3px 0;
    border-radius: 0 3px 3px 0;
}
  
.jq-selectbox__select {
    height: 29px;
    padding: 0 45px 0 10px;
    background: #ff8055;
    color: #fff;
    font: 16px/30px Arial,sans-serif;
    border-radius: 25px;
}
  
.new_player_next {
    width: 50px;
    height: 29px;
    background: url(https://zupimages.net/up/19/51/ovtr.png);
    margin: 0 0 7px 8px;
    float: left;
    cursor: pointer;
    border-radius: 25px;
    background-repeat: no-repeat;
}
  
.new_player_mp4, .new_player_vidfast, .new_player_verystream, .new_player_rapids, .new_player_cloudvideo, .new_player_uqload, .new_player_mytv, .new_player_rapidvideo, .new_player_fembed, .new_player_uptostream, .new_player_smotri, .new_player_mail2, .new_player_netu, .new_player_openload, .new_player_tune, .new_player_kaztube, .new_player_moviki, .new_player_allmyvideos, .new_player_vk, .new_player_thevideo, .new_player_vk2, .new_player_vidbull, .new_player_dailymotion, .new_player_smotri, .new_player_vidto, .new_player_moevideo, .new_player_youtube, .new_player_google, .new_player_cloudy, .new_player_vid, .new_player_vidspot, .new_player_ok, .new_player_kiwi, .new_player_sibnet, .new_player_myvi, .new_player_myvi2, .new_player_rutube, .new_player_mail, .new_player_moo {
    height: 27px;
    cursor: pointer;
    border: 0px solid #e2e2e2;
    border-radius: 14px;
    background: #ff8055;
    float: left;
    font: 13px Arial;
    color: #ffffff;
    line-height: 28px;
    margin: 0 0 5px 6px;
    padding: 0 4px 0 24px;
    position: relative;
}
.watch-top .expand {
    background-color: #ff8055;
    width: 90px;
    right: 0;
    bottom: 0;
    color: #fff;
    font-weight: 700;
    height: 30px;
    line-height: 30px;
    position: absolute;
    text-align: center;
    cursor: pointer;
}
.header-top .header-top-left li a {
    background-color: #ff8055;
    float: left;
    color: #ffffff;
    border-right: 1px solid transparent;
    font-weight: 700;
    font-size: 13px;
    height: 24px;
    line-height: 24px;
    padding: 0 8px;
    position: relative;
    text-shadow: rgba(0,0,0,.2) 1px 1px 0;
    vertical-align: middle;
    border-radius: 3px;
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
}
.message .mtop span.date {
    float: right;
    color: #fff;
    font-size: 11px;
    font-weight: 700;
    padding: 0;
}
.message .malt {
    background-color: #ff935e;
    width: 498px;
    float: left;
    border: 1px solid #ff935e;
    border-top: 0;
    padding: 10px;
    font-size: 13px;
    -moz-border-radius: 0 0 3px 3px;
    -webkit-border-radius: 0 0 3px 3px;
    border-radius: 0 0 3px 3px;
}
.message .mtop {
    background-color: #ff8055;
    width: 498px;
    float: left;
    border: 1px solid #ff8055;
    padding: 10px;
    position: relative;
    -moz-border-radius: 3px 3px 0 0;
    -webkit-border-radius: 3px 3px 0 0;
    border-radius: 3px 3px 0 0;
}
.message .mright .triangle {
    width: 0;
    height: 0;
    border-top: 8px solid transparent;
    border-bottom: 8px solid transparent;
    border-right: 8px solid #ff935e;
    margin: 35px 0 0;
    position: absolute;
    left: 12px;
}
.header-top .header-top-right li a {
    display: block!important;
    background-color: #ff8055;
    float: left;
    color: #fff;
    font-weight: 700;
    font-size: 13px;
    height: 24px;
    line-height: 24px;
    padding: 0 8px;
    margin: 3px 0 0 5px;
    position: relative;
    text-shadow: 0px 0px 0 rgba(0,0,0,0);
    vertical-align: middle;
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px;
    border-radius: 3px;
}
#header .header-menu li.home a {
    background: #ff8055;
    color: #fff;
    font-size: 21px;
    border-right: 0;
    -moz-border-radius: 3px 0 0 3px;
    -webkit-border-radius: 3px 0 0 3px;
    border-radius: 3px 0 0 3px;
}
#header .header-search {
    background-color: #ff935e;
    height: 30px;
    padding: 5px;
    float: right;
    margin: 10px 0 0;
    -moz-border-radius: 0 3px 3px 0;
    -webkit-border-radius: 0 3px 3px 0;
    border-radius: 0 3px 3px 0;
}
#header .header-menu li.home a {
    background: #ff8055;
    color: #fff;
    font-size: 21px;
    border-right: 0;
    -moz-border-radius: 3px 0 0 3px;
    -webkit-border-radius: 3px 0 0 3px;
    border-radius: 3px 0 0 3px;
}
#header .header-menu li a {
    background: #ff935e;
    float: left;
    border-right: 0px solid #ff8055;
    color: #ffffff;
    font-size: 14px;
    height: 20px;
    line-height: 20px;
    padding: 10px;
    position: relative;
    vertical-align: middle;
}
.message .buttons li {
    float: left;
    padding: 1px;
    margin: 0 0 0 5px;
    border: 0px solid #d1d1d1;
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px;
    border-radius: 3px;
}

.baseform {
    background: #2e3543;
    padding: 0;
    border: 1px solid #ff935e;
    border-top: 0;
    display: block;
    height: 231px;
    margin-bottom: 15px;
    -moz-border-radius: 0 0 3px 3px;
    -webkit-border-radius: 0 0 3px 3px;
    border-radius: 0 0 3px 3px;
}

.feedback-form p {
    background-color: #2e3543;
    float: left;
    clear: both;
    width: 666px;
    padding: 4px;
    margin: 0 0 15px;
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px;
    border-radius: 3px;
}

#profile-info .block-title {
    background: #2e3543;
    float: left;
    width: 778px;
    height: 35px;
    line-height: 35px;
    padding: 0 10px;
    font-weight: 700;
    border: 1px solid #2e3543;
    -moz-border-radius: 3px 3px 0 0;
    -webkit-border-radius: 3px 3px 0 0;
    border-radius: 3px 3px 0 0;
}

}
