css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.dpreview.com") {
* {
	background-image: none !important;
}

a {
	color: navy !important;
}

body, div#container_lc, div#container_rc {
	background-color: white !important;
	color: black !important;
}

/* left side navigation menu */

div#content_menu {
	background-image: none !important;
}

a.white, 
.menuhead_opensub,
div#content h3.nomargin {
	color: navy !important;
}

#menu_faux_search,
input#menu_search_q {
	background-image: none !important;
}

/* main comments area */

div#content table {
	border-collapse: collapse !important;
}

div#content table tbody tr td {
	background-color: white !important;
}

td.forumsmessageheader {
	border-top: 2px solid gray !important;
	background-image: none !important;
}

td.forumsmessagecontainer,
td.forumsmessagelabel, 
td.forumsmessagemiddle, 
td.forumsmessagecell, 
td.forumslinkleft, 
td.forumsmessagebottombar, 
td.forumsmessagefootersingle,
td.forumscell,
td.forumscellleft,
td.forumscellright,
td.forumshead,
td.forumsheadleft,
td.forumsheadright,
td.forumsfooter {
	border: 1px solid gray !important;
	background-color: white !important;
	background-image: none !important;
}

td.forumslinkleft {
	border: none !important;
}

div#forumsmessagetext {
	border: 1px dashed silver !important;
	background-color: white !important;
}

td.forumslinksmall a, 
td.tdlabelsimpsm a, 
td.forumsmessagecell a,
td.forumscell a,
td.forumscellleft a,
td.forumscellright a {
	color: navy;
}

span.h4size {
	color: navy !important;
}

span.forumsindent0 {
	color: maroon !important;
}

}
