css:
@-moz-document domain("brick-hill.com") {
.card .top:not(.absolute).blue {
    color: #fff;
    background: linear-gradient(90deg,#2acfe1,#adf2ff);
    border-color: #aefdfd;
}

nav div.primary {
    overflow: auto;
    background-color: #151515;
    color: #fff;
    font-size: 1.1em;
    padding-top: 6px;
    
}

nav .info {
    border-radius: 5px;
    padding: 5px 10px;
    background-color: #151515;
    display: inline-block;
}

.card .top:not(.absolute).red {
    color: #fff;
    background: linear-gradient(90deg,#db8d1f,#ffd988);
    border-color: #f4c98d;
}

nav div.secondary {
    overflow: auto;
    background: linear-gradient(90deg,#000,#757575);
    color: #fff;
    font-size: .9em;
    padding-top: "0px";
}

.button.blue, button.blue {
    color: #000;
    background: linear-gradient(90deg,#c5fff0,#84fafb);
    border-color: #42dae1;
}

.card .top:not(.absolute).orange {
    color: #fff;
    background: linear-gradient(90deg,#f07c6d,#d04747);
    border-color: #f00;
}

.dropdown-content.logout-dropdown li * {
    color: #fff;
    background: linear-gradient(90deg,#571616d6,#310000cc);
}

.dropdown-content.logout-dropdown .dropdown-arrow {
    border-bottom: 7px solid #abc2d500;
}

.card .top:not(.absolute).green {
    color: #fff;
    background: linear-gradient(90deg,#43f2b5,#b2e8d0);
    border-color: #43dfb3;
}

.button.green, button.green {
    color: #000;
    background: linear-gradient(90deg,#adfaff,#4bdaff);
    border-color: #00fffb;
}

.crate-types .active, .crate-types :hover {
    background-color: #f8f8f8;
    border-left: 4px solid #feb146;
}

.card .content {
    height: inherit;
    overflow: auto;
    padding: 10px;
    background: linear-gradient(90deg,#fff,#ddd);
}

.alert.error {
    background: linear-gradient(90deg,#ffa923,#f7d39b);
}

.agray-text {
    color: #000;
}

.red-text {
    color: #4cc600;
}

.button.orange, button.orange {
    color: #fff;
    background: linear-gradient(90deg,#48e322,#a6e69f);
    border-color: #55ff7d;
}

.card .top:not(.absolute) {
    overflow: auto;
    padding: 10px;
    font-weight: 700;
    color: #222;
    background: linear-gradient(90deg,#ddd,#646464);
    border-bottom: 1.5px solid;
    border-color: #595959;
}
}
