css:
@-moz-document domain("defence24.pl") {
body{
    background: #1c1c1c;
}
:root{
    scrollbar-color: #B00100 #1c1c1c;
}
/*HEADER*/
#header{
    background: #3b3b3b;
}
#header nav,#header .head-submenu{
    background: #1c1c1c;
}
/*HEADER*/
/*SEARCH*/
#header .nav-search input{
    background: #1c1c1c;
    color: #fff;
}
#header .nav-search .btn,.search .search-breadcrumb{
    background: #1c1c1c;
    border: 1px solid #fff;
}
#header .nav-search .btn:hover{
    background: #B00100;
}
.search .search-area .item,.search .empty,.list.search li a{
    color: #B00100;
}
/*SEARCH*/
/*STRONA-GŁÓWNA*/
.box-panel.block-dark, .block.block-dark,.box-panel.block-light, .block.block-light,.block-card .card-wrapper{
    background: #1c1c1c;
}
.box-panel.block-title.block-subtitle h2 a,.block-card.vertical .title h4,.main-news-list li a,.block-skaner .nav .nav-item .nav-link,.box-panel.block-title h2,.block-card .card-wrapper  .title h4,.block.block-title.block-subtitle h2 a,.block-reviews-panel ul li a,.block-country-news .main-country-news-list ul li a,.block.block-title h2{
    color: #B00100;
}
.more.dark,.block-card .card-wrapper  .title{
    color: #fff;
}
.block .block-slider .slider-aside-arrow .slider-aside-arrow-block.light{
    background: #B00100;
}
.block .block-slider .slider-aside-arrow .slider-aside-arrow-block.light:hover,.block-card .card-wrapper:hover{
    background: #1c1c1c;
    border: 1px solid #fff;
}
.block-reviews-panel ul li:hover, .block-reviews-panel ul li.active{
    background: #B00100;
}
.block-reviews-panel ul li:hover a, .block-reviews-panel ul li.active a{
    color: #fff;
}
#main-country-news  .img-res img{
    filter: invert(100%);
}
.pagination .disabled .page-link{
    background: #1c1c1c;
    color: #505050;
}
.pagination .page-link{
    background: #1c1c1c;
    color: #B00100;
}
.reviewer-list li a, .reviewer-list .item a{
    background: #000000b0;
}
.reviewer-list li a span, .reviewer-list .item a span{
    color: #fff;
}
/*STRONA-GŁÓWNA*/
/*PDSTRONY*/
.list.dotted li a{
    color: #B00100;
}
.block-others-news .block-others-news-content .block-others-news-list a h5{
    color: #fff;
}
/*REGULAMIN*/
.other-article-content p strong{
    color: #B00100;
}
.other-article-content p{
    color: #fff;
}
/*REGULAMIN*/
/*PDSTRONY*/
/*ARTYKUŁ*/
.news .news-content .news-header h1,.news-author .news-author-content span,.block-subtitle span,.blockquote footer,.news .news-content .news-similar-link p a{
    color: #B00100;
}
.news .news-content .news-header span,.news-tags,.lead,.news-text,.news-author .news-pdf-download,.news-short{
    color: #fff;
}
.blockquote{
    background: #1c1c1c;
}
.news .news-content .news-similar-link p{
    background: #1c1c1c;
    border: 1px solid #fff;
}
/*KOMENTARZE*/
.block-subtitle small{
    color: #fff;
}
.block-subtitle small strong,.news .news-comments form label{
    color: #B00100;
}
.form-control{
    background: #1c1c1c;
    border: 1px solid #fff;
    color: #fff;
}
.form-control:focus{
    background: #1c1c1c;
    color: #fff;
}
#comment-section .empty{
    color: #fff;
}
.block-others-news .block-others-news-content .block-others-news-list .logo img{
    filter: invert(100%);
}
/*KOMENTARZE*/
/*ARTYKUŁ*/
/*FOOTER*/
.block-footer{
    background: #3b3b3b;
}
.block-footer .footer-main-link h5 a,.block-footer a{
    color: #B00100;
}
.block-footer .footer-main-link a{
    color: #fff;
}
.block-footer .footer-bottom-link .footer-bottom-link-right form input{
    background: #1c1c1c;
    color: #fff;
}
.btn-secondary{
    background: #1c1c1c;
}
.btn-secondary:hover{
    background: #B00100;
}
.footer-bottom-link-left:after{
    content: "Ciemny motyw by Maciejozaman";
    color: #B00100;
}
/*FOOTER*/
}

@-moz-document domain("sklep.defence24.pl") {
html,body{
    background: #1c1c1c !important;
}
:root{
    scrollbar-color: #B00100 #1c1c1c;
}
/*HEADER*/
.drv-right .search #s{
    background: #1c1c1c;
    color: #fff;
    border: 1px solid #fff;
}
input.input-text{
    box-shadow: none;
}
.search-button{
    background: #1c1c1c;
    padding: 4px 8px;
    border: 1px solid #fff;
    border-left: 0;
}
.search-button:hover{
    background: #B00100;
    border: 1px solid #fff;
    border-left: 0;
}
.cart-top a:hover{
    color: #1c1c1c;
}
/*HEADER*/
/*STRONA-GŁÓWNA*/
.DrvCarousel .fa,.DrvCarousel__desc,.DrvCarousel__title,.DrvApp{
    color: #B00100;
}
.DrvCarousel__price{
    color: #B00100;
    border-color: #fff;
}
.DrvApp:hover{
    color: #fff;
}
/*STRONA-GŁÓWNA*/
/*ITEM*/
.single-product .summary h1,#main .woocommerce-Price-amount,.widget h2,.single-product .summary .product_meta a{
    color: #B00100;
}
.single-product div.quantity,.posted_in{
    color: #fff;
}
div.quantity .qty{
    background: #1c1c1c;
    color: #fff;
    border: 1px solid #fff;
}
input[type="number"] {
  -webkit-appearance: textfield;
     -moz-appearance: textfield;
          appearance: none;
}
input[type=number]::-webkit-inner-spin-button, 
input[type=number]::-webkit-outer-spin-button { 
  -webkit-appearance: none;
}
.woocommerce_tabs .panel, .woocommerce-tabs .panel, .ui-tabs .panel{
    background: #1c1c1c;
}
.woocommerce_tabs .panel p, .woocommerce-tabs .panel p, .ui-tabs .panel p{
    color: #fff;
}
.woocommerce_message, .woocommerce-message{
    background: #1c1c1c;
    color: #fff;
}
.woocommerce_message .button, .woocommerce-message .button{
    background: #B00100;
    box-shadow: none;
}
.woocommerce_message .button:hover, .woocommerce-message .button:hover{
    background: #1c1c1c;
    color: #B00100;
}
/*ITEM*/
/*KOSZYK*/
table th,.product-name a,.woocommerce_message, .woocommerce-message a{
    color: #B00100;
}
.product-quantity label,.cart-collaterals .cart_totals h2,.entry p{
    color: #fff;
}
table.cart td.actions .button,a.button.alt,.checkout #order_review .form-row.place-order .button,.return-to-shop a{
    background: #B00100;
    box-shadow: none;
    text-shadow: none;
}
table.cart td.actions .button:hover,a.button.alt:hover,.checkout #order_review .form-row.place-order .button:hover,.return-to-shop a:hover{
    background: #1c1c1c;
    color: #B00100;
    text-shadow: none;
}
/*KOSZYK*/
/*KASA*/
.woocommerce-billing-fields h3,#ship-to-different-address span,#order_review_heading,.checkout #order_review #payment ul li label,.cart_item{
    color: #B00100;
}
.form-row label,.checkout #order_review #payment ul li .payment_box p{
    color: #fff;
}
.form-row .input-text, .form-row select{
    background: #1c1c1c;
    color: #fff;
}
.form-row .input-text:focus{
    background: #1c1c1c;
    color: #fff;
}
textarea{
    background: #1c1c1c;
    color: #fff;
    box-shadow: none;
}
textarea:focus{
    background: #1c1c1c;
    color: #fff;
}
.checkout #order_review,.checkout #order_review table,.checkout #order_review #payment ul li .payment_box{
    background: #1c1c1c;
}
.checkout #order_review #payment ul li label img{
    background: none;
}
/*KASA*/
/*KONTAKT*/
input[type="text"], input.txt, input[type="tel"], input[type="email"]{
    background: #1c1c1c;
}
input[type="text"]:focus, input.txt:focus, input[type="tel"]:focus, input[type="email"]:focus{
    background: #1c1c1c;
    color: #fff;
}
input[type="submit"]{
    background: #B00100;
    box-shadow: none;
    text-shadow: none;
}
input[type="submit"]:hover{
    background: #1c1c1c;
    color: #B00100;
    text-shadow: none;
}
/*KONTAKT*/
/*APLIKACJE*/
.drv_app{
    background: #1c1c1c;
}
.drv_app-more,.drv_apps_title{
    color: #B00100;
}
.drv_app-more:hover{
    color: #fff;
}
/*APLIKACJE*/
/*FOOTER*/
.drv-footer-wrapper{
    background: #1c1c1c;
}
.drv-footer .drv-logo,.drv-footer{
    border-color: #fff;
}
.drv-footer .drv-logo img{
    filter: invert(100%);
}
.drv-footer #drv-footer-nav li a{
    color: #B00100;
}
.drv-footer #drv-footer-nav li a:hover,.drv-footer .copyright{
    color: #fff;
}
.drv-footer .copyright:after{
    content: "| Ciemny motyw by Maciejozaman";
    color: #B00100;
    cursor: wait;
}
/*FOOTER*/
}
