@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("allegro.pl") {

/*=====WSZYSTKO=====*/
#preHeader { padding: 0px 0px 0px 0px !important; }
#my_login { position: relative; left: 0px; top: 8px !important; }
#headerBox { overflow: hidden; margin-bottom: 0px !important; }

#categories h2, #specialOffer h2 {

	font-size: 10pt !important;
	font-weight: bold !important; }
#right h2 {
	padding: 5px 0 5px 10px  !important;
	font-size: 10pt !important;
	font-weight: bold !important; }
#left ul { padding: 0px 0px 0px 0px !important; }
#left ul li a {
	font-size: 12px !important;
	padding: 0px 0px 0px 8px !important; }
#firstTime { display: none !important; }
#smallShowCase1, #smallShowCase2 {
	height: 0px !important;
	display: none !important; }
#promoItems { margin-top: 10px !important; }

/*====MOJE ALLEGRO=====*/

#navs li {
	padding-bottom: 2px !important;
	margin-bottom: 2px !important; }
#navs ul li { padding: 0px 0 0px 10px !important; }
#navs ul {
	padding: 0px 0 0 0 !important;
	padding: 1px 0 0px !important; }
#maWrapper { position: relative; top: -5px !important;  }
#navs, #maContent { position: relative; top: 0px !important;  }
#maContent .list-head { position: relative; top: -1px !important; }
#maContent .themeHead { margin: 0 0 0 0 !important; }

/*=====USUNIĘCIE REKLAM=====*/
#pagecontent1 .listStandard  {display: none !important; }
#ceneoSearchResult  {display: none !important; }
#horizontalReks  {display: none !important; }
#verticalReks  {display: none !important; }
.list-menu-topx {display: none !important; }
} 

@-moz-document  url("http://allegro.pl/"),
		url("http://www.allegro.pl/"),
		url("http://allegro.pl/?logout=1"),
		url("http://www.allegro.pl/?logout=1"),
		url("http://allegro.pl/#"),
		url("http://www.allegro.pl/#"),
		url("http://allegro.pl/#top"),
		url("http://www.allegro.pl/#top") {
/*=====SZUKAJ PRZEDMIOTU=====*/

#view_suggest { position: relative; top: 34px !important; }
#searchSuggest { position: relative; top: 54px !important; }
#left #searchBox #hide_suggest { position: relative; top: 0px !important; }
#left #searchBox { padding-bottom: 0px !important; height: 90px !important; }
#left #searchBox h2 {
	padding: 5px 0 8px 0  !important;
	font-size: 10pt !important;
	font-weight: bold !important; }
#left #searchBox #string { margin: 0px 0 6px 0 !important; padding: 2px !important; }
#left #searchBox .form-submit {
	height: 20px !important;
	padding: 0px 10px 4px 10px !important; }
#left #searchBox form p a {
	position: relative; left: 5px; top: -6px !important; }
#footerAbout ul li, #footerServices ul li, #footerExternal ul li, #specialSites ul li {
	padding: 0px 0px 0px 10px !important;}
#footerAbout ul, #footerServices ul, #footerExternal ul, #specialSites ul {
	padding: 0px 0px 0px 0px !important;}

}
