css:
@-moz-document domain(magister.nl) {


.head-bar {
    position: absolute;
    top: 56px;
    right: 10px;
    left: 0;
    z-index: 680;
    padding-left: 8px;
    height: 47px;
    line-height: 46px;
    color: #ffffff;
    border-bottom: none;
    background-color: #ff9d9b;
    font-size: 14px;
}

.content-container {
    position: relative;
    height: 100%;
    width: 100%;
    border: 1px solid #cccccc;
    background-color: #ff9d9b;
    text-align: left;
    overflow: auto;
 background-image: url("https://images.unsplash.com/photo-1554189097-ffe88e998a2b?ixlib=rb-1.2.1&auto=format&fit=crop&w=934&q=80");
background-size: 100%;
    
}

.block h3 {
    position: relative;
    z-index: 100;
    margin: 0;
    padding: 0 0 0 8px;
    font: 14px "droid_sansregular", arial, helvetica, sans-serif;
    color: #ff9d9b;
    height: 44px;
    line-height: 44px;
    white-space: nowrap;
    overflow: hidden;
    -moz-text-overflow: ellipsis;
    -ms-text-overflow: ellipsis;
    text-overflow: ellipsis;
    border-bottom: 1px solid #cccccc;
    -moz-box-shadow: 0 2px 5px -1px rgba(0, 0, 0, 0.15);
    -webkit-box-shadow: 0 2px 5px -1px rgba(0, 0, 0, 0.15);
    box-shadow: 0 2px 5px -1px rgba(0, 0, 0, 0.15);
}

.widget .agenda-list li.alert span.time:after {
    position: absolute;
    top: 7px;
    right: 8px;
    font-family: "magistersymbolsnormal";
    font-style: normal;
    font-weight: bold;
    font-size: 14px;
    speak: none;
    display: block;
    text-decoration: inherit;
    overflow: hidden;
    text-align: center;
    font-variant: normal;
    text-transform: none;
    content: 'Z';
    color: #ff9d9b !important;
}

.widget .agenda-list li.alert span.time:before {
    color: #ff9d9b;
    content: "wijziging";
}

.agenda-text-icon.outline {
    color: #ff9d9b;
    background-color: #ffffff;
    border: 1px solid #ff9d9b;
    line-height: 18px;
}

.agenda-text-icon[icon-type="information"] {
    background-color: #ff9d9b;
    color: #ffffff;
}

.endlink a {
    display: block;
    float: right;
    padding: 0 8px;
    height: 40px;
    line-height: 40px;
    color: #ff9d9b;
    text-decoration: underline;
}


.alert {
    border-left: 4px solid #ff9d9b;
    background-color: #d9fbfa !important;
}

}
