css:
@-moz-document domain(somtoday.nl) {

#header-wrapper {
    background-color: #9c0e0d;
}



body {
    line-height: 1;
    color: #000;
    font-size: 12px;
    font-family: Open Sans,Arial,Helvetica,sans-serif;
    overflow-y: scroll;
    overflow-x: hidden;
    background: #f3f9fc;
        background-image: url("http://www.hdcarwallpapers.com/walls/dub_magazine_mc_customs_wide_body_ferrari_f12_2-HD.jpg");
    background-size: 100% ;
	 
}

#main-menu a {
    display: block;
    float: left;
    height: 30px;
    margin-right: 4px;
    font-size: 13.5px;
    border-radius: 5px 5px 0 0;
    padding: 12px 16px 4px;
    color: #e60034;
    background: #e7f0f5;
}

h1 {
    font-weight: 700;
    font-size: 16pt;
    color: #e60034;
}

.afspraak {
    position: absolute;
    left: 5px;
    right: 5px;
    background-color: #e2ecec;
    margin: 2px;
    text-align: left;
}

.afspraakVakNaam {
    height: 14px;
    color: #e60034;
    font-size: 13px;
}

#main-menu a.active, #main-menu a.active:hover {
    text-decoration: none;
    cursor: pointer;
    color: #ffffff;
    background: #ff0000;
}

#main-menu-bottom {
    height: 5px;
    background-color: #ff0000;
}

.today {
    border-bottom: solid 3px #ff0000;
    color: #3f5c6e;
    font-weight: 700;
}

}
