css:
@-moz-document domain("nesa-sg.ch") {
:root {
  --bgcolor: #2e2e2e;
  --header-bgcolor: #4d4d4d;
}

.cls-page--header-row--logo-gradient {
  background-image: none;
}

.cls-page--header-row--logo-span {
  background-color: var(--header-bgcolor);
}

.cls-page--header-row--sn-logo {
  display: none;
}

.cls-page--header,
.cls-page--header-row,
.cls-page--header-row--logo-gradient {
  background-color: var(--header-bgcolor);
}

.cls-page--navlist {
  background: var(--bgcolor);
  border: none;
  color: #ffffff;
}

.mdl-navigation__link {
  color: #ffffff !important;
}

.mdl-button.mdl-button--colored {
  color: #ffffff;
}

.cls-page--header-row--logo-gradient::after {
  color: #ffffff;
}

.cls-page--header-row--logo-span {
  width: 30px;
}

.mdl-tooltip {
  display: none;
}

.mdl-layout__content {
  background-color: var(--bgcolor);
  color: #ffffff;
}

.mdl-card {
  background: var(--bgcolor);
  color: #ffffff;
}

.mdl-card--important {
  border: #d44444 solid 3px;
}

h1,
h2,
h3,
h4,
a {
  color: #ffffff;
}

a.mdl-button {
  color: #ffffff;
}

a:hover {
  color: #e6e6e6;
}

.mdl-card__title {
  color: #ffffff;
}

.mdl-card--important > .mdl-card__supporting-text,
.mdl-card--information > .mdl-card__supporting-text,
.mdl-card--neutral > .mdl-card__supporting-text {
  color: #ffffff;
}

.mdl-data-table {
  background-color: var(--bgcolor);
  color: #ffffff;
}
.mdl-data-table tbody tr:hover {
  background-color: var(--bgcolor);
}

.mdl-table--listtable th,
.mdl-table--listtable td {
  background-color: var(--bgcolor);
  color: #ffffff;
}

header {
  box-shadow: 0 3px 6px rgba(0, 0, 0, 0.16), 0 3px 6px rgba(0, 0, 0, 0.23);
}


.mdl-card__supporting-text[style="overflow: auto; display: none;"] {
  max-height: 0px;
  padding: 0 16px;
}

.mdl-card__supporting-text {
  display: block !important;
  transition: max-height 300ms ease, padding 300ms ease;
  max-height: 260px;
}
}
