css:
@-moz-document domain("bestbuy.com") {
body {
    background-color: #1c1c1c;
    color: #ccc;
}

.shop-facets legend.facet-legend,
.shop-facets label {
    color: #9E9E9E;
}

.shop-facets .search-bar-control {
    color: #aaa !important;
    background-color: #141414;
}

input.form-control,
input[type=number],
textarea.form-control {
    background-color: #141414 !important;
    background-color: #fff0;
    -webkit-box-shadow: inset 1px 2px 0 0 #fff0;
    box-shadow: inset 1px 2px 0 0 #fff0;
}

.shop-facets .price-range-submit[disabled] {
    background-color: #fff0!important;
}

.shop-facets .get-it-fast-subheading {
    color: #ccc;
}

.pricing-lib-price-7-1943-6 .priceView-hero-price,
.shop-product-title .product-data-label,
.shop-vendor-series .vs-outer .vendor-series-container.carousel-mode .vendor-series-products-wrapper .vendor-series-product-container .vendor-series-product .currentlyViewing,
.user-generated-content-ratings-and-reviews .verified-purchaser-button,
.c-facet-button,
.widget-focus h3 {
    color: #ccc;
}

.c-checkbox:hover .c-checkbox-custom-input:before {
    background-color: #1c1c1c;
}

.pricing-lib-price-7-1943-6 .pricing-price__sale-message,
.shop-warranty-selector .iSyLKq legend,
.shop-complete-your-purchase .cyp-main .cyp-item__price .pb .pb-current-price,
.c-accordion-trigger,
.shop-specifications .row-title,
.user-generated-content-ratings-and-reviews .distillation-filters legend,
.widget-flex .flex-header-with-links h2,
.widget-topper h3,
.browse-carousel .carousel-header .carousel-head,
.pricing-lib-price-7-1943-5 .priceView-hero-price,
.widget-flex .flex-header h2 {
    color: #ccc;
}

.c-dropdown,
.c-dropdown:active,
.c-dropdown:hover,
.c-rich-dropdown {
    background: #141414;
    color: #ccc;
}

input,
.shop-search-bar .appContainer .search-bar .header-search-button {
    background-color: #141414 !important;
}

.c-checkbox-custom-input:before {
    background-image: -webkit-linear-gradient(315deg, #1a1a1a, #1a1a1a 50%, #141414 0, #141414);
    background-image: linear-gradient(135deg, #1a1a1a, #1a1a1a 50%, #141414 0, #141414);
}

.shop-sku-list .current-page-number {
    color: #9e9e9e;
}

.c-checkbox input:checked ~ .c-checkbox-custom-input:before {
    background-color: #1c1c1c;
}

a,
.btn-link,
.btn-default-link,
.card-member .btn-link-unstyled,
.shop-save-for-later .appContainer button.save-for-later-save,
.shop-compare-checkbox .appContainer {
    color: #2196F3;
}

a:hover,
.btn-link:hover,
.btn-default-link:hover,
.card-member__link:hover {
    color: #08b2ff;
}

.shop-header .header-large .store-locator-lv .store-loc-btn {
    background-color: #002668;
}

.shop-media-gallery .carousel-trigger {
    background: #1c1c1c;
}

.flex-carousel-arrow-left button,
.pl-flex-carousel-container .flex-carousel-arrow-right button {
    background-color: transparent;
}


.shop-specifications .all-specs-wrapper {
    background-color: #1c1c1c;
}


.c-modal-grid {
    background-color: #1c1c1c;
}

.c-overlay-backdrop {
    background-color: rgba(35, 35, 35, 0.65);
}

.shop-product-carousels .kjyXHO .product-carousel .arrow-left.disabled,
.shop-product-carousels .kjyXHO .product-carousel .arrow-right.disabled {
    color: #2d2d2d;
}

.btn-outline {
    color: #2196f3;
    background: #1c1c1c;
}

.shop-sku-list .banner-wrapper {
    border-top: 1px solid #2d2d2d;
    border-bottom: 1px solid #2d2d2d;
}

.shop-facets .app-container.lv {
    border-top: 1px solid #2d2d2d;
    border-left: 1px solid #2d2d2d;
    border-right: 1px solid #2d2d2d;
}

.shop-sku-list .sku-item-list .sku-item {
    border-bottom: 1px solid #2d2d2d;
}

.shop-facets .lv .facet {
    padding: 20px 0 10px 10px;
    border-bottom: 1px solid #2d2d2d;
}

.form-control,
input {
    border-color: #2d2d2d;
}

.c-checkbox-custom-input:before {
    border: solid #2d2d2d;
}

.v-divide-bottom {
    border: solid #2d2d2d;
    border-width: 0 0 1px;
}

.shop-footer .appContainer.lv {
    border-top: 2px solid #272727;
    background-color: #1c1c1c;
}

.c-facet-button {
    border: 1px solid #2d2d2d;
}

.shop-header .header-large .top-navigation-section {
    background-color: #002668;
}

.shop-header .header-large .fullbleed-wrapper {
    border-top: 1px solid #1d43a0;
    background-color: #002668;
}

.shop-search-bar .appContainer .search-bar input {
    color: #aaa;
}

.c-dropdown {
    border: 1px solid #2d2d2d;
}

.shop-search-bar .appContainer .search-bar #header-clear-search-icon {
    background-color: #141414;
}

.v-border {
    border-color: #2d2d2d;
}

.shop-vendor-series .vs-outer .vs-divider {
    border-top: 2px solid #2d2d2d;
}

.shop-vendor-series .vs-outer .vendor-series-container.carousel-mode .vendor-series-products-wrapper .vendor-series-product-container.selected {
    border-bottom: 2px solid #686868;
}

.v-divide-top {
    border: solid #2d2d2d;
    border-width: 1px 0 0;
}

.shop-complete-your-purchase .cyp-main.large-view-cyp .cyp-content .cyp-driver-wrapper,
.shop-complete-your-purchase .cyp-main .cyp-items .cyp-item {
    border: 1px solid #2d2d2d;
}

.shop-product-carousels .kjyXHO .product-carousel.mini.lv {
    padding: 0 15px;
    border: 1px solid #2d2d2d;
}

.c-accordion {
    border: solid #2d2d2d;
    border-width: 0 0 1px;
}

btn {
    border: 1px solid #2d2d2d;
}

.user-generated-content-ratings-and-reviews .reviews-sort-search-filters {
    border-top: 1px solid #2d2d2d;
}

.user-generated-content-ratings-and-reviews .ugc-reviews .reviews-content-wrapper {
    border-top: 1px solid #2d2d2d;
}

.user-generated-content-ratings-and-reviews .review-item {
    border-bottom: 1px solid #2d2d2d;
}

.user-generated-content-ratings-and-reviews .feature-ratings .rating-bars .rating-group .rating-bar {
    border: 1px solid #2d2d2d;
}

.user-generated-content-ratings-and-reviews .feedback-display #helpfulness-button {
    border: 1px solid #2d2d2d;
}

.user-generated-content-ratings-and-reviews .rating-bars-v2 .progress {
    background-color: #272727;
}

.user-generated-content-ratings-and-reviews .filters .btn.btn-sm.filter-btn {
    border: 1px solid #2d2d2d;
}

hr {
    border-width: 1px;
    border-color: #2d2d2d;
}

.shop-frequently-bought-together .accessories-list .accessory-item {
    border-bottom: 1px solid #2d2d2d;
}

.widget-text-block .widget-text-block--outlined {
    border: 1px solid #2d2d2d;
}

.widget-text-block>.text-block-seo>.header {
    border-bottom: 1px solid #2d2d2d;
}

.c-checkbox.disabled .c-checkbox-custom-input:before {
    border-color: #141414;
    background-color: #141414;
}

.widget-flex .flex-header {
    border-bottom: 1px solid #2d2d2d;
}

.widget-highlight .header * {
    color: #ccc;
}

.widget-highlight .header {
    border-bottom: 1px solid #2d2d2d;
}

#site-control-content>.widget-container .container-col.container-theme-grey {
    border-color: #2d2d2d;
}

.browse-carousel {
    background: #1c1c1c;
}

#site-control-content>.widget-container .container-col.container-theme-grey::after {
    border-color: #2d2d2d;
}

.browse-carousel .horizontal-ruler {
    border-bottom: #2d2d2d solid 1px;
}

.widget-polaroid {
    border: 1px solid #2d2d2d;
}

.widget-focus {
    border-bottom: 1px solid #2d2d2d;
    border-top: 1px solid #2d2d2d;
}

.widget-navigation .widget-navigation-dynamic {
    border: 1px solid #2d2d2d;
}

.media-network-banner-pencil {
    border-bottom: 2px solid #2d2d2d;
}
}

@-moz-document domain("bestbuy.com") {
/* Remove Stuff */
.blue-assist,
.footer-email-wrap,
.disclaimer,
.social-device-feedback-block,
.utility-navigation,
.c-back-to-top.v-shown {
    display: none !important;
}
}
