css:
@-moz-document url-prefix("https://adonisjs.com") {
@media screen and (min-width: 769px)
{
	.docs-container {
    	background: linear-gradient(90deg, #000000 60%, #220052 61%);
	}
}

.wysiwyg p>a, .wysiwyg .admonitionblock a {
    color: #fb2f51;
    text-decoration: none;
    background-image: linear-gradient(to bottom, transparent 50%, #fc617b 50%);
    background-position: 0 93%;
    background-repeat: repeat-x;
    background-size: 100% .15rem;
    /* text-shadow: 0.1rem 0 #fff, 0.15rem 0 #fff, -0.1rem 0 #fff, -0.15rem 0 #fff; */
}

body {
    color: #a0c1e8;
    font-size: 1rem;
    font-weight: 500;
    line-height: 1.6;
}

strong {
    color: #fb2f51;
    font-weight: 700;
}

.about-adonis, .enjoy-adonis {
    padding-top: 106px;
    background: #1a0a31;
}

.home-section-title {
    font-weight: 900;
    color: #c094fd;
    font-size: 1.8rem;
    margin-bottom: 15px;
    line-height: 1.25;
}

.adonis-apps {
    padding-top: 106px;
    padding-bottom: 106px;
    background: #220052;
}

.app-card {
    display: flex;
    flex-direction: column;
    justify-content: center;
    padding: 60px;
    text-align: center;
    background: #44475a;
    box-shadow: 0 0 0 1px rgba(63,63,68,0.05), 0 1px 6px 0 rgba(69,79,91,0.18);
}

.content-area {
    background: #16072b;
}

h1, h2, h3, h4, h5 {
    font-family: "effra",sans-serif;
    font-feature-settings: 'dlig','liga','lnum','kern';
    font-weight: 600;
    color: #4bffd5;
}

h1 a, h2 a, h3 a, h4 a, h5 a {
    font-family: "effra",sans-serif;
    color: #9df7f7;
}

.menu-label {
    font-family: "effra",sans-serif;
    font-weight: 600;
    color: #cfe3f7;
    font-size: 0.85rem;
    text-transform: uppercase;
}

.menu-list a {
    border-radius: 2px;
    color: #98cbf7;
    display: block;
    padding: 0.5em 0.75em;
}

.footer {
    background-color: #000000;
    padding: 3rem 1.5rem 6rem;
}

.wysiwyg .admonitionblock {
    background: #1b232b;
    border-radius: 4px 4px 6px 6px;
    padding: 15px;
    position: relative;
    border-left: 1px solid #eee;
    border-right: 1px solid #eee;
    border-bottom: 1px solid #eee;
    margin-bottom: 2.5em;
    margin-top: 0.5em;
}

.wysiwyg p>a, .wysiwyg .admonitionblock a {
    color: #fb2f51;
    text-decoration: none;
    background-image: linear-gradient(to bottom, transparent 50%, #fc617b 50%);
    background-position: 0 93%;
    background-repeat: repeat-x;
    background-size: 100% .15rem;
}

.wysiwyg pre.highlight {
    padding: 20px 60px 20px;
    box-shadow: inset 0 0 0 1px rgba(0,0,0,0.1);
    border: none;
    background: #191e23;
    position: relative;
    border-radius: 4px;
    overflow-x: scroll;
    display: block;
    margin-bottom: 2.5em;
}

code[class*="language-"], pre[class*="language-"] {
    color: #aacbf3;
    background: none;
    text-shadow: none !important;
    font-family: Consolas, Monaco, 'Andale Mono', 'Ubuntu Mono', monospace;
    text-align: left;
    white-space: pre;
    word-spacing: normal;
    word-break: normal;
    word-wrap: normal;
    line-height: 1.5;
    -moz-tab-size: 4;
    -o-tab-size: 4;
    tab-size: 4;
    -webkit-hyphens: none;
    -moz-hyphens: none;
    -ms-hyphens: none;
    hyphens: none;
}

.token.operator, .token.entity, .token.url, .language-css .token.string, .style .token.string {
