css:
@-moz-document url-prefix("http://steamcommunity.com/profiles/"), url-prefix("https://steamcommunity.com/profiles/"), url-prefix("http://steamcommunity.com/id/"), url-prefix("https://steamcommunity.com/id/"), url-prefix("http://steamcommunity.com/my/"), url-prefix("https://steamcommunity.com/my/") {
/*IMAGE DATABASE*/
.profile_header_bg {
    background-image: url('https://cdn.discordapp.com/attachments/657964912085041162/660464544291880960/snowbg_2.png') !important;
}

.profile_header_bg_texture:before {
    background: url("https://cdn.discordapp.com/attachments/657964912085041162/658112797208608812/profile_header_garlands_sprite_combo.png");
}

.profile_header_bg_texture::after {
    background: url('https://cdn.discordapp.com/attachments/657964912085041162/660464541171187712/snow_floor.png') center top !important;
}

.profile_background_overlay_content {
    background: url('https://cdn.discordapp.com/attachments/657964912085041162/660464537392119830/snowbg.png') !important;
}

.profile_customization {
    background-image: url('https://cdn.discordapp.com/attachments/657964912085041162/660464534410231819/showcase_bg_tile.png') !important;
}

.showcase_content_bg,
.profile_content {
    background: url('https://cdn.discordapp.com/attachments/657964912085041162/660464537392119830/snowbg.png') !important;
}

.profile_header .playerAvatar.profile_header_size::before {
    background: url('https://cdn.discordapp.com/attachments/657964912085041162/657965667974119444/dangle_flake.png') !important;
}

.profile_header .playerAvatar.profile_header_size::after {
    background: url('https://cdn.discordapp.com/attachments/657964912085041162/657965667974119444/dangle_flake.png') !important;
}

#global_header .content {
    background: none !important;
}

.profile_header {
    padding-top: 150px !important;
}

.profile_content {
    margin-top: 0;
    clear: both;
}

.profile_summary,
.profile_summary_footer,
.profile_header_badgeinfo_badge_area a,
.btn_profile_action a span {
    position: relative;
    z-index: 2 !important;
}

.profile_header .profile_header_centered_persona {
    position: absolute;
    z-index: 3 !important;
}

.profile_header_bg_texture {
    background: none;
    padding-bottom: 0px;
    box-shadow: none;
}

.profile_header_bg {
    background-repeat: repeat;
    background-size: 50px 50px;
    background-color: #384C54;
    background-position: bottom;
    padding-bottom: 0px;
    width: 976px;
    height: 361px;
    z-index: 2;
}

.showcase_content_bg {
    background-size: 50px 50px;
    margin-top: 0;
    clear: both;
}

.profile_content {
    background-size: 50px 50px;
    overflow: hidden;
    padding-top: 14px;
}

#footer_spacer {
    padding-top: 0px !important;
}

@keyframes cycle {
    100% {
        background: url("https://cdn.discordapp.com/attachments/657964912085041162/658112797208608812/profile_header_garlands_sprite_combo.png") 0px -1324px;
    }
}

@-webkit-keyframes cycle {
    100% {
        background: url("https://cdn.discordapp.com/attachments/657964912085041162/658112797208608812/profile_header_garlands_sprite_combo.png") 0px -1324px;
    }
}

@-o-keyframes cycle {
    100% {
        background: url("https://cdn.discordapp.com/attachments/657964912085041162/658112797208608812/profile_header_garlands_sprite_combo.png") 0px -1324px;
    }
}

.profile_header_bg_texture:before {
    background-repeat: repeat-y;
    background-size: cover;
    width: 1368px;
    height: 172px;
    position: absolute;
    content: "";
    bottom: 0px;
    right: 0px;
    left: -201px;
    top: -10px;
    z-index: 2 !important;
    background-position: 0px 0px;
    /*-------------------------------------*/
    animation: cycle 3s steps(4) infinite !important;
    -webkit-animation: cycle 3s steps(4) infinite !important;
    -moz-animation: cycle 3s steps(4) infinite !important;
    -o-animation: cycle 3s steps(4) infinite !important;
}

.profile_header_bg_texture::after {
    content: "";
    position: absolute;
    width: 100%;
    height: 65px;
    background-repeat: no-repeat;
    left: 0;
    bottom: 0px;
    z-index: 1;
    pointer-events: none;
}

.profile_content {
    margin-top: 0 !important;
}

.profile_background_overlay_content {
    background-size: auto auto;
    background-size: 50px 50px;
    margin-top: 0;
    clear: both;
    opacity: 1 !important;
}

.profile_customization_header {
    color: #eee5e5;
}

.profile_customization {
    background-repeat: repeat;
    background-color: #384c54;
    margin-bottom: 40px;
}

.profile_leftcol_header h2 {
    color: #3c1802;
}

.profile_comment_area .commentthread_header,
.profile_leftcol_header {
    background: #eae4db;
    color: #3c1802;
}

.profile_comment_area .commentthread_paging {
    background: rgba( 0, 0, 0, 0.75);
    padding: 0 4px;
    border-radius: 4px;
}

.recent_game {
    background: rgba(60, 24, 2, 0.75);
}

.profile_header_badge {
    background: rgba( 0, 0, 0, 0.25);
    border: 1px solid rgba( 228, 202, 99, 0.5);
}

.showcase_notes {
    overflow-y: hidden;
}

.showcase_notes:hover {
    overflow-y: auto;
}

/*SMALL CSS TRICK FOR THAT EVIL PROFILE CONTENT*/
html {
    overflow-y: auto;
}

.profile_page {
    overflow-y: hidden;
}

/*Border Left*/
.profile_header_bg::before {
    background: url('https://cdn.discordapp.com/attachments/657964912085041162/657965322162274307/w19_gp_cane_side.png') 0 55px !important;
    content: "";
    background-size: 133px 512px;
    position: absolute;
    width: 133px;
    z-index: 2;
    left: -77px;
    right: 0;
    top: 0;
    bottom: 0;
    background-repeat: repeat-y;
    transform: scaleX(-1);
    height: 99999%;
}

/*Border Right*/
.profile_header_bg::after {
    background: url('https://cdn.discordapp.com/attachments/657964912085041162/657965322162274307/w19_gp_cane_side.png') 0 55px !important;
    content: "";
    background-size: 133px 512px;
    position: absolute;
    width: 133px;
    z-index: 1;
    left: 920px;
    right: 0;
    top: 0;
    bottom: 0;
    background-repeat: repeat-y;
    height: 99999%;
}

/***********************************************************/
/*Gold Texts*/
.profile_header .persona_name,
.persona.online,
a.persona.online,
.persona.online a,
.friend_status_online,
.friend_status_online div,
.friend_status_online a {
    color: #E4CA63;
}

@-webkit-keyframes swinging {
    0% {
        -webkit-transform: rotate(10deg);
    }
    50% {
        -webkit-transform: rotate(-5deg)
    }
    100% {
        -webkit-transform: rotate(10deg);
    }
}

@-moz-keyframes swinging {
    0% {
        transform: rotate(15deg);
    }
    50% {
        transform: rotate(-15deg)
    }
    100% {
        transform: rotate(15deg);
    }
}

@keyframes swinging {
    0% {
        transform: rotate(15deg);
    }
    50% {
        transform: rotate(-15deg)
    }
    100% {
        transform: rotate(15deg);
    }
}

@-o-keyframes swinging {
    0% {
        transform: rotate(15deg);
    }
    50% {
        transform: rotate(-15deg)
    }
    100% {
        transform: rotate(15deg);
    }
}

.profile_header .playerAvatar.profile_header_size::before {
    -webkit-transform-origin: 50% 0;
    -moz-transform-origin: 50% 0;
    -o-transform-origin: 50% 0;
    transform-origin: 50% 0;
    -webkit-animation: swinging 2.5s ease-in-out forwards infinite;
    -moz-animation: swinging 2.5s ease-in-out forwards infinite;
    -o-animation: swinging 2.5s ease-in-out forwards infinite;
    animation: swinging 2.5s ease-in-out forwards infinite;
}

.profile_header .playerAvatar.profile_header_size::after {
    -webkit-transform-origin: 50% 0;
    -moz-transform-origin: 50% 0;
    -o-transform-origin: 50% 0;
    transform-origin: 50% 0;
    -webkit-animation: swinging 3s ease-in-out forwards infinite;
    -moz-animation: swinging 3s ease-in-out forwards infinite;
    -o-animation: swinging 3s ease-in-out forwards infinite;
    animation: swinging 3s ease-in-out forwards infinite;
}

.profile_header .playerAvatar.profile_header_size::before {
    content: '';
    background-repeat: no-repeat;
    width: 148px;
    height: 261px;
    z-index: 2;
    display: block;
    position: absolute;
    top: -145px;
    left: -195px;
}

.profile_header .playerAvatar.profile_header_size::after {
    content: '';
    background-repeat: no-repeat;
    width: 148px;
    height: 261px;
    z-index: 2;
    display: block;
    position: absolute;
    top: -145px;
    left: 974px;
}

.item_showcase_item {
    border-color: transparent !important;
}

/*THEME PROFILE CANCELLER*/
.w19_side,
.w19_header {
    display: none !important;
}
}

@-moz-document domain("steamcommunity.com") {
.playerAvatar.online {
    background: -webkit-linear-gradient(top, #53A4C4 0, #458097 100%);
    background: linear-gradient(to bottom, #53A4C4 0, #458097 100%);
}
.playerAvatar.in-game {
    background: -webkit-linear-gradient(top, #8FB93B 0, #6E8C31 100%);
    background: linear-gradient(to bottom, #8FB93B 0, #6E8C31 100%);
}
.playerAvatar.offline {
    background: -webkit-linear-gradient(top, #959595 0, #333 100%);
    background: linear-gradient(to bottom, #959595 0, #333 100%);
}

/*Improved borders for avatars*/
.playerAvatar {
    padding: 3px !important;
    border-radius: 2px !important;
}

/*Fixes borders for profile avatar*/
.profile_header .playerAvatarAutoSizeInner {
    margin: 3px !important;
    border-radius: 2px !important;
}

/*Re-align the profile summary a little bit.*/
.profile_header .playerAvatar.profile_header_size {
    margin-right: 14px;
}

/*Artwork Icon Fix*/
#modalMediaRightScroll .media_game_logo [src="https://steamcdn-a.akamaihd.net/steam/apps/767/capsule_184x69.jpg"],
.screenshotAppBlock [src="https://steamcdn-a.akamaihd.net/steam/apps/767/capsule_184x69.jpg"] {
    background: url('https://cdn.discordapp.com/attachments/657964912085041162/659505201014636565/CustomArtwork.jpg') !important;
    background-repeat: no-repeat !important;
    height: 0px !important;
    width: 0px !important;
    padding-left: 120px !important;
    padding-top: 45px !important;
    margin-left: 0px !important;
    margin-top: 0px !important;
}

