css:
@-moz-document url-prefix("https://realkana.com") {
/* Made by s0m3guy */

body, input {
    color: #fff;
}

div.all {
    background-image: url("https://github.com/s0m3guy2004/StylusRepo/blob/master/realkana/background_body.jpg?raw=true")
}

.all__content, .link--button-white, .study__right, .study__shown {
    background-image: none;
    background-color: #0E0E1080;
    box-shadow: none;
}

h1, .tabs__link--front:visited {
    color:#1285EC;
}

.tabs__tab {
    background: #1C1C1E80;
    border-color: #ffffff50;
    border-width: 0 0 1px 1px
}
.tabs__link {
    box-shadow:none
}

/* Intro */

.top--text {
    background: none
}
h1+h2, .options__label, .options__label:hover {
    color: #a2c8f4
}

/* Hiragana and Katakana */

.link--plain {
    color: #fff;
    border-bottom: 1px dotted #ccc
}

.link--plain:hover {
    color: #a2c8f4;
    border-bottom: 1px solid #ccc
}

.kana__cell {
    border-color: #ffffff50;
}

.checkboxes__cell {
    border-right: 1px solid #ffffff50
}

.kana__character, .preview__icon, .study__question-kana {
    background-image: url("https://github.com/s0m3guy2004/StylusRepo/blob/master/realkana/kana.png?raw=true")
}
.kana, .preview, .typefaces {
    background-color: #E3E3E120
}

/* Study */
.study__circle {
    background-image:none;
}
.study__input, .study__input--blurred {
    background-color: #0E0E1080;
    border: 1px solid #ffffff50;
    box-shadow: none     
}

/* Extra */

.typefaces__label {
    background: url("https://github.com/s0m3guy2004/StylusRepo/blob/master/realkana/typefaces.png?raw=true") no-repeat;
    background-size: 562px 47px;
}

.typefaces__label--1 {
    background-position: 0 14px;
}

.typefaces__label--2 {
    background-position: -62px 14px;
}

.typefaces__label--3 {
    background-position: -124px 14px
}

.typefaces__label--4 {
    background-position: -186px 14px;
}

.typefaces__label--5 {
    background-position: -248px 14px;
}

.typefaces__label--6 {
    background-position: -310px 14px;
}

.typefaces__label--7 {
    background-position: -372px 14px;
}

.typefaces__label--8 {
    background-position: -434px 14px;
}

.typefaces__label--9 {
    background-position: -496px 14px;
}

.options {
    background:none
}

.message {
    background-color: #E3E3E120
}

/* App */

.device__frame {
    background: #000;
    padding: 40px 0 40px;
    border: 2px solid #ffffff50;
    border-radius: 30px;
    box-shadow: none
}

.device__image {
    background-image: url("https://github.com/s0m3guy2004/StylusRepo/blob/master/realkana/device.jpg?raw=true");
    border: 5px solid #000
}

.device__stars {
    display: none
}
}
