*{font-family: Vazir;}
#ClientTypeBody tr td:nth-child(2) {
	background: #0ff00f70 !important;
}
#ClientTypeBody tr td:nth-child(3) {
	background: #93f7c8b8;
}
#ClientTypeBody tr td:nth-child(4) {
	background: #ff7a7a59;
}
#ClientTypeBody tr td:nth-child(5) {
	background: #ff002475;
}
#ClientTypeBody tr:nth-child(6n+1) td:nth-child(2) {
	background: #fff !important;
}
#ClientTypeBody tr:nth-child(6n+1) td:nth-child(3) {
	background: #0ff00f70 !important;
}
#ClientTypeBody tr:nth-child(6n+1) td:nth-child(4) {
	background: #93f7c8b8 !important;
}
#ClientTypeBody tr:nth-child(6n+1) td:nth-child(5) {
	background: #ff7a7a59 !important;
}
#ClientTypeBody tr:nth-child(6n+1) td:nth-child(6) {
	background: #ff002475 !important;
}
#ClientTypeBody tr:nth-child(6n-1) td:nth-child(2) {
	background: #fff !important;
    color: red;
    text-align: left;
}
#ClientTypeBody tr:nth-child(6n-1) td:nth-child(3) {
	background: #fff !important;
    color: red;
    font-weight: bold;
}
}
