css:
@-moz-document url-prefix("https://www.youtube.com"), url-prefix("https://youtu.be"), url-prefix("https://youtube.com") {
:root {
    /* Backgrounds */
    --yt-spec-brand-background-primary: black !important;
    --yt-spec-brand-background-solid: black !important;
    --yt-main-app-background: black !important;
    --yt-spec-general-background-a: black !important;
    --yt-menu-hover-backgound-color: #171616 !important;
    --yt-simple-menu-header-background: black !important;
    --yt-searchbox-background: black !important;
    --ytd-searchbox-legacy-button-color: black !important;
    --ytd-searchbox-legacy-button-hover-color: #171616 !important;
    --yt-spec-icon-disabled: #171616 !important;
    --yt-spec-general-background-b: black !important;
    --yt-dialog-background: #171616 !important;
    --yt-spec-suggested-action: #171616 !important;
    --yt-live-chat-vem-background-color: #171616 !important;
    --ytd-searchbox-legacy-button-focus-color: #171616 !important;
    --yt-channel-header-background: black !important;
    --yt-std-surface-200: #171616 !important;
    
    /* Colors */
    --yt-spec-text-primary: white !important;
    --yt-endpoint-color: #d4d4d4 !important;
	--yt-endpoint-visited-color: #d4d4d4 !important;
    --yt-endpoint-hover-color: white !important;
    --yt-spec-text-secondary: #d4d4d4 !important;
    --ytd-comment-text-color: white !important;
    --yt-button-color: #d4d4d4 !important;
	--yt-spec-text-primary: white !important;
    --yt-spec-icon-active-other: #d4d4d4 !important;
    --yt-spec-brand-icon-inactive: #d4d4d4 !important;
    --yt-spec-call-to-action: #0089ff !important;
    --yt-expand-color: #d4d4d4 !important;
    --yt-primary-text-color: white !important;
    --yt-secondary-text-color: #d4d4d4 !important;
    --ytd-searchbox-legacy-border-shadow-color: black !important;
    --ytd-searchbox-legacy-button-border-color: #171616 !important;
    --ytd-searchbox-legacy-border-color: #171616 !important;
    --yt-live-chat-primary-text-color: white !important;
    --yt-live-chat-secondary-text-color: #d4d4d4 !important;
    --yt-live-chat-text-input-field-inactive-underline-color: #171616 !important;
    --yt-border-color: black !important;
    --yt-live-chat-tertiary-text-color: #d4d4d4 !important;
    --ytd-searchbox-legacy-button-hover-border-color: #171616 !important;
    --ytd-searchbox-legacy-button-icon-color: white !important;
    --yt-live-chat-picker-button-color: #d4d4d4 !important;
    --yt-live-chat-picker-button-hover-color: white !important;
    --yt-std-surface-400: #171616 !important;
    --ytd-collection-badge-color: white !important;
    --yt-featured-channel-title-text-color: white !important;
}

/* Backgrounds */
#page-manager.ytd-app, body, ytd-masthead, #yt-masthead-container, .sbsb_a, .sbdd_b {
    background: black;
}
.gsfs:hover, .arrow.yt-horizontal-list-renderer {
    background: #171616;
}
.sbfl_b {
    background: transparent;
}
/* Colors */
yt-formatted-string[has-link-only_]:not([force-default-style]) a.yt-simple-endpoint.yt-formatted-string:hover, body, .gsfs, .sbfl_b:hover, .more-button-exp.ytd-comment-renderer:hover, .more-button-exp.ytd-comment-renderer:focus, .less-button-exp.ytd-comment-renderer:hover, .less-button-exp.ytd-comment-renderer:focus, yt-icon.ytd-badge-supported-renderer {
    color: white;
}
ytd-video-meta-block[rich-meta] #channel-name.ytd-video-meta-block, ytd-video-meta-block[rich-meta] #metadata-line.ytd-video-meta-block, #text.complex-string.ytd-channel-name, .sbfl_b, .more-button-exp.ytd-comment-renderer, .less-button-exp.ytd-comment-renderer {
    color: #d4d4d4;
}

/* Border colors */
#yt-masthead-container, .sbdd_b {
    border-color: black;
}
.unfocused-line.paper-input-container, .focused-line.paper-input-container {
    border-color: #171616 !important;
}

/* Other */
#yt-masthead-content::after {
    color: white;
    content: 'The Dark Theme currently does not support the old YouTube. Please turn it off if you want to use this page.';
}
yt-icon.ytd-badge-supported-renderer {
    fill: #d4d4d4;
}
ytd-multi-page-menu-renderer.style-scope.ytd-popup-container::after {
    color: white;
    font-size: 12px;
    content: 'YouYube Dark Theme made by Lekrkoekj  https://lekrkoekj.github.io';
}
ytd-multi-page-menu-renderer.style-scope.ytd-popup-container {
    max-height: 773px !important;
}

/* Light YT Logo*/
svg.style-scope.ytd-topbar-logo-renderer * {
    color: transparent !important;
    fill: transparent !important;
}
#logo-icon-container.ytd-topbar-logo-renderer {
    background-image: url(https://raw.githubusercontent.com/Lekrkoekj/Lekrkoekj.github.io/master/images/youtube-logo.png);
    background-size: cover;
    width: 110px;
}


}
