css:
@-moz-document url-prefix("https://guacamole.") {
/*
 * Guacamole Dark Theme (2019).
 * 
 * CC BY - Creative Commons Attribution
 */
html,
body {
    color: #ececec;
    background-color: #333333;
}
pre {
    color: #ececec;
    background-color: #222;
    border: 1px solid #555;
}

a[href]:visited {
    color: #884;
}
a[href] {
    color: #080;
}

div.location,
input[type=text],
input[type=email],
input[type=number],
input[type=password],
textarea {
    background-color: #2c2c2c;
    color: #bbb;
}


/* Login */
div.login-ui {
    color: #ececec;
    background-color: #333333;
}
.login-ui .login-fields .labeled-field input:focus {
    background-color: #222222;
    color: #eee;
}

/* Home */
.recent-connections .connection:hover {
    background-color: #607830;
}

.menu-dropdown .menu-contents {
    background-color: #222222;
}
.menu-dropdown .menu-contents li a {
    color: #eee;
}
.menu-dropdown .menu-contents li a:hover {
    background-color: #607830;
}

.list-item.selected {
    background: #84af2c
}
.list-item:not(.selected) .caption:hover {
    background-color: #607830;
}
.list-item .name {
    color: #ddd;
}

/* Menu */
.menu {
    color: #ececec;
    background-color: #333333;
}

.clipboard,
.clipboard-service-target {
    background-color: #2c2c2c;
    color: #bbb;
}

/* Connection */
/*
    .notification {
        background-color: #2c2c2c;
        color: #ececec;
    }
    */
#connection-warning {
    background-color: #a8a800;
}

.transfer-manager {
    background-color: #2c2c2c;
}
.transfer.error {
    background-color: #a80000;
}

/* Settings */
.page-tabs .page-list li a[href],
.section-tabs li a {
    color: #ddd;
}
.page-tabs .page-list li a[href]:hover,
.section-tabs li a:hover {
    background-color: #607830;
}
.page-tabs .page-list li a[href]:visited {
    color: #fff;
}

.settings table.session-list tr.session:hover {
    background-color: #84af2c;
}

.location-chooser .dropdown {
    background-color: #222222;
}


.user a,
.user-group a,
.connection a,
.connection-group a {
    color: #ddd;
}
.user a:hover,
.user-group a:hover,
.connection a:hover,
.connection-group a:hover {
    color: #ddd;
}
.user a:visited,
.user-group a:visited,
.connection a:visited,
.connection-group a:visited {
    color: #fff;
}

.manage-user .notice.read-only {
    background-color: #291800;
}

#filesystem-menu .header.breadcrumbs .breadcrumb:hover {
    background-color: #607830;
}

#guac-menu #zoom-out:hover,
#guac-menu #zoom-in:hover {
    background-color: #607830;
}

/* Official Apache Guacamole org website */
.chapter div.toc,
.appendix div.toc {
    background-color: #2c2c2c;
    border-color: #555;
}

div.important {
    background-color: #2a2a00;
}

.replaceable {
    color: #a8a8ff;
}

.table-contents td,
.table-contents th,
.informaltable td,
.informaltable th {
    border-color: #555;
}

.table-contents th,
.informaltable th {
    background-color: #2c2c2c;
}

div#footer {
    background-color: #222222;
}
}
