css:
@-moz-document domain("se7ensins.com") {
/*devil logo*/
.xb-canvas .canvas-header .p-header-logo.p-header-logo--image img {
    height: 400px;
    padding-left: 230px;
    background: transparent url(https://i.imgur.com/XUTz1Jt.png) top left no-repeat;
    background-size: 220px;
    background-position-x: 4px;
    background-position-y: center;
}
.xb-canvas .canvas-header .p-header-logo.p-header-logo--image img {
    max-height: 70px;
}
/*logo background pattern*/
@media (max-width: 2100px){
.xb-canvas .canvas-header .p-header-logo.p-header-logo--image {
    position: relative;
    background-image: url(https://i.imgur.com/BMnh3Ah.png);
    background-size: 200px 200px;
}
}
/*header background pattern*/
@media (max-width: 2100px){
.xb-canvas .canvas-header {
    padding-left: 0;
    background-image: url(https://i.imgur.com/BMnh3Ah.png);
    background-size: 200px 200px;
}
}
/*profile tab top-right*/
.p-navgroup {
    float: left;
    background: rgb(19, 19, 19);
    border-top-left-radius: 4px;
    border-top-right-radius: 4px;
}
/*sidebar*/
.xb-canvas .canvas-left {
    min-height: 100vh;
    vertical-align: top;
    flex-shrink: 0;
    width: 80px;
    padding: 0;
    z-index: 100;
    transition: all .2s ease;
    color: #fff;
    background: #1a1a1a;
}
/*buttons*/
.button.button--primary, a.button.button--primary {
    color: #ffffff;
    background: #be001b;
    min-width: 80px;
    border-color: #be001b;
}
.button.button--cta, a.button.button--cta {
    color: #fff;
    background: #be001b;
    border-color: #be001b;
    border-color: #be001b #be001b #be001b #be001b;
}
.button.button--cta:hover, a.button.button--cta:hover {
    background: #6c6c6c !important;
    color: #fff !important;
}
.XenBase :not(.block-header) .button.button--link, .XenBase :not(.block-header) a.button.button--link {
    background-color: #be001b;
    color: #fff;
}
.block-filterBar .filterBar-filterToggle:hover, .block-filterBar .filterBar-menuTrigger:hover {
    text-decoration: none;
    background: #e8e8e8;
}
/*menus*/
.menu-content {
    margin: 0;
    padding: 0;
    list-style: none;
    text-align: left;
    font-size: 14px;
    color: #9b9b9b;
    background: #202020;
    border-radius: 3px;
    border-top: 3px solid #be001b;
}
.menu-arrow {
    position: absolute;
    width: 0;
    height: 0;
    top: -8px;
    left: 50%;
    margin-left: -8px;
    border: 8px solid transparent;
    border-top-width: 0;
    border-bottom-color: #be001b;
}
.XenBase label.iconic:hover>input+i {
    color: #be001b;
}
.XenBase label.iconic i {
    color: #ff0024;
}
.fr-popup .fr-action-buttons button.fr-command:not(.button--splitTrigger):hover, .fr-popup .fr-action-buttons button.fr-command.button--splitTrigger>.button-text:hover, .fr-popup .fr-action-buttons button.fr-command.button--splitTrigger>.button-menu:hover, .fr-popup .fr-action-buttons button.fr-command:not(.button--splitTrigger):focus, .fr-popup .fr-action-buttons button.fr-command.button--splitTrigger>.button-text:focus, .fr-popup .fr-action-buttons button.fr-command.button--splitTrigger>.button-menu:focus, .fr-popup .fr-action-buttons button.fr-command:not(.button--splitTrigger):active, .fr-popup .fr-action-buttons button.fr-command.button--splitTrigger>.button-text:active, .fr-popup .fr-action-buttons button.fr-command.button--splitTrigger>.button-menu:active {
    background-color: #be001b;
}
.fr-popup .fr-action-buttons button.fr-command {
    display: inline-block;
    display: inline-flex;
    align-items: center;
    justify-content: center;
    text-decoration: none;
    cursor: pointer;
    border: 1px solid transparent;
    white-space: nowrap;
    -webkit-transition: background-color .25s ease;
    transition: background-color .25s ease;
    border-radius: 4px;
    padding-top: 7px;
    padding-right: 10px;
    padding-bottom: 7px;
    padding-left: 10px;
    text-align: center;
    color: #fff;
    background: #be001b;
    min-width: 80px;
    border-color: #be001b;
    height: auto;
    line-height: 1.5;
}
/*text*/
a {
    color: #e8e8e8;
    text-decoration: none;
}
a:hover {
    color: #9b9b9b;
    text-decoration: underline;
}
.p-breadcrumbs a {
    color: #e8e8e8;
}
.tabs--standalone .tabs-tab.is-active {
    color: #e8e8e8;
    border-color: #1A1A1A;
}
.pageNav-page {
    display: table-cell;
    background: linear-gradient(0deg, #1d1d1d, #1f1f1f);
    color: #e8e8e8;
    border-width: 1px;
    border-style: solid;
    border-top-color: #1a1a1a;
    border-right-color: #1a1a1a;
    border-bottom-color: #272727;
    border-left-color: #1a1a1a;
    box-shadow: 2px 2px 2px rgba(0,0,0,0.04);
    font-size: 14px;
    white-space: nowrap;
}
.pageNav-jump {
    display: inline-block;
    background: linear-gradient(0deg, #1d1d1d, #1f1f1f);
    color: #e8e8e8;
    border-width: 1px;
    border-style: solid;
    border-top-color: #1a1a1a;
    border-right-color: #1a1a1a;
    border-bottom-color: #272727;
    border-left-color: #1a1a1a;
    box-shadow: 2px 2px 2px rgba(0,0,0,0.04);
    font-size: 14px;
    white-space: nowrap;
    border-radius: 2px;
    padding: 7px 8px;
}
.tabs--standalone .tabs-tab:hover {
    color: #e8e8e8;
}
.inputNumber-button {
    position: relative;
    font-family: 'Font Awesome 5 Pro';
    font-size: inherit;
    font-style: normal;
    font-weight: 300;
    text-rendering: auto;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    color: #be001b;
    line-height: .75em;
    vertical-align: -15%;
    width: 45px;
    justify-content: center;
    text-align: center;
    cursor: pointer;
    -webkit-touch-callout: none;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
}
.inputGroup.inputGroup--joined .inputNumber-button:hover, .inputGroup.inputGroup--joined .inputNumber-button:active, .inputGroup.inputGroup--joined .inputNumber-button:focus {
    background-color: #2f2525;
    color: #ff0024;
}
.inputDate .inputDate-icon {
    position: relative;
    font-family: 'Font Awesome 5 Pro';
    font-size: inherit;
    font-style: normal;
    font-weight: 300;
    text-rendering: auto;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    color: #be001b;
    line-height: .75em;
    vertical-align: -15%;
    cursor: pointer;
    width: 45px;
    justify-content: center;
    text-align: center;
    -webkit-touch-callout: none;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
}
.sv-rating-type-category2--text:hover {
    color: #717171;
}
.sv-rating-type-category2--text {
    color: #9b9b9b;
}
.menu-linkRow.is-selected, .menu-linkRow:hover, .menu-linkRow:focus {
    color: #be001b;
    text-decoration: none;
    background: #1d1d1d;
    border-left-color: rgba(26,26,26,0.5);
}
.XenBase .block--messages .message .message-content a {
    color: #be001b;
}
/*headers*/
.block-header {
    padding: 12px 12px;
    margin: 0;
    font-weight: 400;
    text-decoration: none;
    font-size: 18px;
    color: #fff;
    border: 1px solid #be001b;
    background: #be001b;
}
.block-filterBar {
    padding: 12px 12px;
    font-size: 14px;
    color: #fff;
    background: #be001b;
    border-bottom: 1px solid #272727;
    box-shadow: inset 0px 1px 0px 0px #14210c;
}
.p-footer-custom {
    font-size: 14px;
    color: #ababab;
    background: #171717;
    border-top: 5px solid #be001b;
    padding-top: 0px;
    padding-right: 20px;
    padding-bottom: 40px;
    padding-left: 20px;
}
.overlay .overlay-title {
    font-size: 18px;
    color: #fff;
    border: 1px solid #be001b;
    background: #be001b;
}
.p-body-pageContent .block .block-minorHeader {
    padding: 12px 12px;
    margin: 0;
    font-weight: 400;
    text-decoration: none;
    font-size: 18px;
    color: #fff;
    border: 1px solid #be001b;
    background: #be001b;
}
.block-formSectionHeader {
    padding: 12px 12px;
    margin: 0;
    font-weight: 400;
    text-decoration: none;
    font-size: 16px;
    color: #be001b;
    border-top: 1px solid #1a1a1a;
    border-bottom: 1px solid #1a1a1a;
}
.message-header .message-attribution {
    font-size: 16px;
    color: #fff;
    border: 1px solid #be001b;
    padding-top: 12px;
    padding-right: 12px;
    padding-bottom: 12px;
    padding-left: 12px;
    background: #be001b;
}
/*tooltips*/
.tooltip--basic.tooltip--bottom .tooltip-arrow {
    top: 0;
    left: 50%;
    margin-left: -5px;
    border: 5px solid transparent;
    border-top-width: 0;
    border-bottom-color: #be001b;
}
.tooltip--basic .tooltip-content {
    text-align: center;
    font-size: 13px;
    color: #fff;
    background: #be001b;
    border-radius: 4px;
    padding-top: 5px;
    padding-right: 5px;
    padding-bottom: 5px;
    padding-left: 5px;
}
.tooltip--basic.tooltip--top .tooltip-arrow {
    bottom: 0;
    left: 50%;
    margin-left: -5px;
    border: 5px solid transparent;
    border-bottom-width: 0;
    border-top-color: #be001b;
}
.tooltip--basic.tooltip--right .tooltip-arrow {
    top: 50%;
    left: 0;
    margin-top: -5px;
    border: 5px solid transparent;
    border-left-width: 0;
    border-right-color: #be001b;
}
.fr-tooltip {
    position: absolute;
    top: 0;
    left: 0;
    padding: 0 8px;
    border-radius: 2px;
    -moz-border-radius: 2px;
    -webkit-border-radius: 2px;
    -webkit-background-clip: padding-box;
    background-clip: padding-box;
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    box-shadow: none;
    background: #be001b;
    color: #fff;
    font-size: 13px;
    line-height: 26px;
    font-family: 'Roboto','Helvetica Neue',Helvetica,Roboto,Oxygen,Ubuntu,Cantarell,'Fira Sans','Droid Sans',sans-serif;
    -webkit-transition: opacity .25s ease 0s;
    -moz-transition: opacity .25s ease 0s;
    -ms-transition: opacity .25s ease 0s;
    -o-transition: opacity .25s ease 0s;
    -webkit-opacity: 0;
    -moz-opacity: 0;
    opacity: 0;
    user-select: none;
    -o-user-select: none;
    -moz-user-select: none;
    -khtml-user-select: none;
    -webkit-user-select: none;
    -ms-user-select: none;
    z-index: 2147483647;
    text-rendering: optimizelegibility;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
}
}
