css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("netvibes.com") {

	#columns {
		padding-top:0px !important; }
	#divTabs {
		border-bottom:0 !important;
		padding-top:12px !important;
		padding-left:20px !important; }
	#divTabs li {
		border-bottom: 1px solid #363636 !important; }
	#divTabsOuter, #notification-bar {
		display: none !important; }

	#topLinks {
		display: none; }

	#savingStatus {
		position: absolute;
		top: 2px;
		left: 50%;
		margin-left: -2px !important; }

	#footer {
		display: none !important; }

	#feedReader {
		border: none !important;
		width: 100% !important;
		left: 0 !important;
		height: auto !important;
		top: 0 !important;
		bottom: 2px !important;
		z-index: 1001 !important; }

	#feedReader .frame {
		border: none !important; }

	#feedReader .header {
		padding: 6px !important; }

	#feedReader .header #btShowPage {
		margin-top: -5px !important; }

	#feedReader .tdSeparator {
		width: 2px !important; }

	#feedReaderFrame2 {
		padding: 2px !important; }

	#feedReaderContentFrame, #feedReaderContentFrame iframe {
		width: 100% !important;
		height: 100% !important; }

	#feedReaderFrame, #feedReaderFrame .frame {
		height: 100% !important; }

	#feedReaderFrame2 {
		position: absolute;
		top: 30px;
		bottom: 0 !important;
		left: 0;
		right: 0; }

	#readerTable {
		height: 100%; }

	#readerTable .tdContent, #readerTable .tdHeadines {
		height: 100%;
		overflow: auto; }

	#feedReaderHeadlinesFrame {
		position: fixed; 
		width: inherit;
		height: 100% !important;
		bottom: 0;
		top: 84px; }

	#feedReaderContentFrame .contentInsideFrame {
		height: 0 !important; }

	#modulesArea {
		min-height: inherit !important; }
	#savingStatus {
		top:5px !important; }
}
