css:
@-moz-document domain("rrys2019.com") {
body .chat-enterbox,
.top-box .header .zzhu,
.top-box .header .app_link,
.top-box .header .top-login .top-login-before a:last-child,
.top-box .header .top-login .top-login-after .top_msg,
.top-box .header .top-login .top-login-after .fb,
.top-box .notice,
.usercenter-box .user-menu,
.usercenter-box .user-main .fav-tab,
.usercenter-box .user-main .fav-title,
.user-favlist .mark{display:none !important;}
.menu-box{height:48px;margin-bottom:10px;}
.menu-box li:nth-child(3),
.menu-box li:nth-child(4),
.menu-box li:nth-child(5),
.menu-box li:nth-child(7),
.menu-box li:nth-child(8),
.menu-box li:nth-child(9),
.menu-box li:nth-child(11){display:none !important;}
.menu-box a{height:48px;line-height:48px;font-size:16px;}
.menu-box a.cur:after{height:1px;}
.usercenter-box .user-main{width:100%;border-radius:8px;padding-top:0;}
.user-favlist>li{float:left;width:50%;width:540px;}
.user-favlist>li:nth-child(2n){margin-left:10px;}
.userfav-box .user-favlist li .fl-img{float:left;width:240px;height:320px;}
.userfav-box .user-favlist li .fl-info{float:right;width:295px;}
.userfav-box .user-favlist .list li{overflow: hidden;}
.userfav-box .user-favlist .list .a0,
.userfav-box .user-favlist .list .b0,
.userfav-box .user-favlist .list .b1{display:none !important;}
}
