css:
@-moz-document url-prefix(http://www.gaiaonline.com/forum/) {

.yui-b .leaderboard, #post_poll, #post_action, #post_style, #editor, #forum_subtitle, .post-signature, .say_b1 img, .say_b3 img, .whisper_b1 img, .whisper_b3 img, .icon, #gaia_footer, .threadtype, #pulse_wrapper, #pulse_show_wrapper, .header_content, .subforums{ display: none !important; }

.forum-list{ border: #000000 1px solid !important; }

.rowon, .rowon *{ background: #dddddd !important; }

a:hover{ text-decoration: underline !important; }

.postEven .message .avi_box{ left: 10px !important; right: auto !important; }
.postEven .forum_userstatus{ float: left !important; right: auto !important; left: 20px !important; }
.postEven div.user_info{ float: left !important; }
.postEven .message{ padding-left: 120px !important; padding-right: 0px !important; }
.postEven .status{ right: 45px !important; }

.post{ border-bottom: 0px !important; }

.rowoff .title, .rowon .title{ padding-left: 10px !important; }

.gotonewposts{ width: 0px !important; padding: 0px !important; }

*{
  background: white !important;
  color: black !important;
}

.post-quote{
  background: transparent url(http://i293.photobucket.com/albums/mm66/skeate/gaiashit/quote.png) no-repeat scroll left top !important;
  height: 15px !important;
  width: 15px !important;
}
.post-edit{
  background: transparent url(http://i293.photobucket.com/albums/mm66/skeate/gaiashit/edit.png) no-repeat scroll left top !important;
  height: 15px !important;
  width: 15px !important;
}
.post-delete{
  background: transparent url(http://i293.photobucket.com/albums/mm66/skeate/gaiashit/delete.png) no-repeat scroll left top !important;
  height: 15px !important;
  width: 15px !important;
}
.post-report{
  background: transparent url(http://i293.photobucket.com/albums/mm66/skeate/gaiashit/report.png) no-repeat scroll left top !important;
  height: 15px !important;
  width: 15px !important;
}

#forum_title_header{ margin-left: 0px !important; }

#forum_title_header .title{ font-size: 24pt !important; }

#btn-preview{
  background: transparent url(http://i293.photobucket.com/albums/mm66/skeate/gaiashit/preview.png) no-repeat scroll left top !important;
  width: 15px !important;
  height: 15px !important;
}
#btn-submit{
  background: transparent url(http://i293.photobucket.com/albums/mm66/skeate/gaiashit/Post.png) no-repeat scroll left top !important;
  width: 15px !important;
  height: 15px !important;
}
}
