css:
@-moz-document domain(nastol.io) {
body {
    color: #898989;
    background-color: #333336
}

.h1,
.h2,
.h3,
.h4,
.h5,
.h6,
h1,
h2,
h3,
h4,
h5,
h6 {
    color: #fff
}

.table {
    color: #9d9d9d
}

.table-hover tbody tr:hover {
    color: #fff;
}

.form-control {
    color: #fff;
    background-color: #676768;
}

.form-control:focus {
    color: #fff;
    background-color: #676768;
}

.form-control:disabled,
.form-control[readonly] {
    background-color: #676768;
    color: #a6a7a8;
}

.btn {
    color: #fff;
}

.dropdown-menu {
    background-color: #555556;
}

.dropdown-item {
    color: #fff;
}

.card {
    background-color: #4A4A4C;
}

.layout__nav {
    background-color: #222226;
}

.layout__sidebar {
    background: #333336;
}

.search__input {
    color: #fff
}

.header {
    background-color: #222226
}

@media (min-width: 960px) {
    .header__logo-wrap {
        background-color: #59595e;
    }
}

.header__logo-sub {
    color: #e8e8e8
}

#header-user-profile-menu {
    background: #555556;
}

#header-user-profile-menu ul li a {
    color: #fff;
}

.article__text {
    color: #9d9d9d;
}

.article__link:hover .article__text {
    color: #fff
}

.community__name {
    color: #fff
}

.grid-list-item__title-link {
    color: #fff
}

.last-comments-item__text {
    color: #9d9d9d
}

.last-comments-item__text:hover {
    color: #fff
}

.tile-list__name {
    color: #fff
}

.user__name {
    color: #fff
}

.market-advantages__content._primary {
    background-color: #6d6d6d
}
.market-advantages__content._primary:after {
    background-color: #6d6d6d
}
.market-advantages__content._info {
    background-color: #6d6d6d
}
.market-advantages__content._info:after {
    background-color: #6d6d6d
}
.market-advantages__content._warning {
    background-color: #6d6d6d
}
.market-advantages__content._warning:after {
    background-color: #6d6d6d
}

.content-tab-menu__item {
    border: 1px solid #3a3a3a;
}

.content-tab-menu__link {
    background-color: #6d6d6d;
    color: #fff;
}

.content-tab-menu__link._current,
.content-tab-menu__link:hover {
    color: #fff;
    background-color: #333
}

.content-tab-menu__link:hover:after {
    background-color: #6d6d6d
}

.content-tab-menu__counter {
    color: #fff;
}

.content-tab-sub-menu__link._current,
.content-tab-sub-menu__link:hover {
    color: #fff
}

.index-hot:after {
    background: linear-gradient(90deg, hsla(0, 0%, 63%, 0) 25%, #333336 85%, #333336 50%);
}

.index-statistics__total {
    color: #fff
}

.index-statistics__progress {
    background-color: #2f2f2f;
    color: #e8e8e8;
    border: 1px solid #1a1a1a;
    border-radius: 8px;
}

.index-statistics__progress:hover {
    background-color:#252525
}

.bg-warning {
    background-color: #56391c!important;
}

.bg-info {
    background-color: #0a0a71!important;
}

.bg-danger {
    background-color: #670808!important;
}

.progress {
    height: 1.2rem;
}

.progress-bar {
    background-color: #075407;
}

.progress-bar-striped {
    border-right: 1px solid #090909;
    border-radius: 7px;
}

.users-leftbar-profile__image-wrap:after {
    background: -webkit-gradient(linear, left top, left bottom, from(hsla(0, 0%, 100%, 0)), to(#333336));
    background: linear-gradient(180deg, hsla(0, 0%, 100%, 0) 0, #333336)
}

.users-leftbar-profile__name {
    margin-top: .5rem;
}

.achievement-block__info {
    background-color: #363636;
}

.static-page__header {
    background-color: #333336
}

.static-page__header:after {
    background: -webkit-gradient(linear, left top, left bottom, from(hsla(0, 0%, 97.6%, 0)), to(#333336));
    background: linear-gradient(180deg, hsla(0, 0%, 97.6%, 0) 0, #333336)
}
.static-page__title {
    color: #fff
}
.btn-light {
    background-color: #6d6d6d;
}

.games-main-image {
    margin-bottom: 1rem;
}

.games-main-image__wrap {
    position: relative;
}

.games-main-image__wrap:after {
    content:"";
    position:absolute;
    right:0;
    bottom:0;
    left:0;
    height:90px;
    background: -webkit-gradient(linear, left top, left bottom, from(hsla(0, 0%, 100%, 0)), to(#333336));
    background: linear-gradient(180deg, hsla(0, 0%, 100%, 0) 0, #333336)
}

.fa-eye-evil:before {
    content: "\f06e";
}

.hot-item__inner:hover,
.hexagon-list-item__inner:hover {
    -webkit-filter: grayscale(1);
}

.btn-outline-secondary {
    background-color: #4a4a4a;
}

.modal-content {
    background-color: #6d6d6d;
}
}
