@-moz-document domain("vk.com") {
.author[data-from-id="551830433"]:after
, .author[data-from-id="558733818"]:after
{
  display: block;
  content: '△ ◇ ⋈';
  position: absolute;
  left: 0;
  top: 32px;
  font-size: 32px;
  z-index: 11;
  text-align: center;
  width: 100%;
  height: 32px;
  line-height: 32px;
  color: white;
}

.author[data-from-id="551830433"]:before
, .author[data-from-id="558733818"]:before
{
  display: block;
  content: '';
  position: absolute;
  left: 0;
  top: 0;
  width: 100%;
  height: 100%;
  z-index: 10;
  background-color: black;
  filter: contrast(0%);
}

.reply_image[href$="/id551830433"]
, .reply_image[href$="/id558733818"]
{
  filter: contrast(0%);
}

.reply_image[href$="/id551830433"] + .reply_content
, .reply_image[href$="/id558733818"] + .reply_content
{
  position: relative;
  overflow: hidden;
  z-index: 9;
}

.post_header {
  position: static !important;
}
}
